INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1011985, '', 'en', 'Kung Fu Panda 4', 'Après trois aventures dans lesquelles le guerrier dragon Po a combattu les maîtres du mal les plus redoutables grâce à un courage et des compétences en arts martiaux inégalés, le destin va de nouveau frapper à sa porte pour l''inviter à enfin se reposer. Plus précisément, pour être nommé chef spirituel de la vallée de la Paix.', 351.001, '/y8K9wRRv7YK1ACHqaDlMBrNebVU.jpg', '2024-03-27', 0, '', '', 'Kung Fu Panda 4');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 693134, '', 'en', 'Dune: Part Two', 'Le voyage mythique de Paul Atreides qui s''allie à Chani et aux Fremen dans sa quête de vengeance envers les conspirateurs qui ont anéanti sa famille. Devant choisir entre l''amour de sa vie et le destin de l''univers, il fera tout pour éviter un terrible futur que lui seul peut prédire.', 255.271, '/dM2eC02Dq3iMtBZZDFtXSLHfFKJ.jpg', '2024-02-28', 0, '', '', 'Dune : Deuxième partie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1147400, '', 'fr', 'Miraculous World : Paris, Les Aventures de Toxinelle et Griffe Noire', 'Des porteurs de Miraculous venus d’un autre monde surgissent dans la ville de Paris. Ils viennent d’un univers parallèle où tout est inversé : Toxinelle et Griffe Noire, les alter égos maléfiques de Ladybug et Chat Noir sont les méchants et le porteur du Papillon, Hesperia, est un super-héros. Ensemble Ladybug et Chat Noir – les vrais – vont devoir aider Hespéria à contrer les attaques de leurs doubles maléfiques et les empêcher de s’emparer du Miraculous du Papillon. Mais nos héros pourront-ils aider Hesperia à faire de Toxinelle et Griffe Noire de meilleures personnes ?', 188.104, '/4wHrXw9zB4wsibWaAPDkvjNGPnQ.jpg', '2023-10-21', 0, '', '', 'Miraculous World : Paris, Les Aventures de Toxinelle et Griffe Noire');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 609681, '', 'en', 'The Marvels', 'Carol Danvers, alias Captain Marvel, a récupéré son identité auprès du tyrannique Kree et s''est vengée du renseignement suprême. Cependant, des conséquences inattendues la voient assumer le fardeau d''un univers déstabilisé. Lorsque ses fonctions l''envoient dans un trou de ver anormal lié à un révolutionnaire Kree, ses pouvoirs s''entremêlent avec deux autres superhéros pour former les Marvels.', 192.566, '/mqAQO6j5gkq6iwCXNbXpzf0RXBU.jpg', '2023-11-08', 0, '', '', 'The Marvels');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 601796, '', 'ko', '외계+인 1부', 'Afin d’anéantir la menace d’Aliens capable de posséder les corps humains, un gardien est envoyé du futur pour traquer ces envahisseurs. Mais lorsque Séoul est attaqué, ce protecteur comprend qu''il ne s’en sortira pas seul. Des siècles plus tôt, une mystérieuse élue parcourt le pays à la recherche de l''alien originel. Des quêtes parallèles de ces voyageurs du temps dépendra le salut de l''humanité.', 194.32, '/sYCUwnywPbN8GN3LUJ1kCYyKoH4.jpg', '2024-02-28', 0, '', '', 'Alienoid : Les Protecteurs du futur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 597, '', 'en', 'Titanic', 'Southampton, 10 avril 1912. Le paquebot le plus grand et le plus moderne du monde, réputé pour son insubmersibilité, le « Titanic », appareille pour son premier voyage. Quatre jours plus tard, il heurte un iceberg. À son bord, un artiste pauvre et une grande bourgeoise tombent amoureux.', 134.839, '/vpsvHLkoeKUjceIMeNSqCp3xEyY.jpg', '1998-01-07', 0, '', '', 'Titanic');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 940721, '', 'ja', 'ゴジラ-1.0', 'La guerre est finie, mais le combat est loin d''être terminé pour Shikishima, pilote rongé par la culpabilité. Peut-il se pardonner et aider à sauver le Japon de la furie de Godzilla ?', 186.105, '/tBzOqSc51CiUeOlxW2unvYVBU8v.jpg', '2023-12-07', 0, '', '', 'Godzilla Minus One');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 959098, '', 'fr', 'Le Dernier Jaguar', 'Autumn grandit dans la forêt amazonienne aux côtés de Hope, un adorable bébé jaguar femelle qu''elle a recueilli. L''année de ses six ans, un drame familial contraint Autumn et son père à retourner vivre à New York. Huit années passent, et Autumn, devenue adolescente, n''a jamais oublié son amie jaguar. Quand elle apprend que Hope est en danger de mort, Autumn décide de retourner dans la jungle pour la sauver.', 175.081, '/x5z1J5P7MugMtpPLSBa9SBJMMZV.jpg', '2024-02-07', 0, '', '', 'Le Dernier Jaguar');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 359410, '', 'en', 'Road House', 'Dalton, un ancien combattant de l''UFC tente d''échapper à son sombre passé et à son penchant pour la violence. Il est repéré par Frankie, propriétaire d''un relais routier dans les Keys de Floride. Elle l''engage comme nouveau videur dans l''espoir d''empêcher un gang violent, travaillant pour le patron du crime Brandt, de détruire son bar bien-aimé.', 141.622, '/eMoPZHBRu2yrFBm8tefqJKg1JfF.jpg', '2024-03-21', 0, '', '', 'Road House');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 763215, '', 'en', 'Damsel', 'Une jeune femme docile accepte d''épouser un beau prince et réalise que la famille royale a choisi de la sacrifier en remboursement d''une dette ancienne. Jetée dans une grotte abritant un dragon cracheur de feu, elle ne pourra compter que sur son intelligence et sur son courage pour survivre.', 118.65, '/hOZAt7wASgabmtdXRWMloJfUw0D.jpg', '2024-03-08', 0, '', '', 'La Demoiselle et le dragon');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 634492, '', 'en', 'Madame Web', '« Pendant ce temps, dans un autre univers... » Dans une variation du genre classique, Madame Web raconte les origines de l''une des plus énigmatiques héroïnes des bandes dessinées Marvel. Le suspense met en vedette Dakota Johnson dans le rôle de Cassandra Webb, une ambulancière de Manhattan ayant des dons de voyance. Contrainte de faire face à des révélations sur son passé, elle se lie d''amitié avec trois jeunes femmes destinées à un avenir de superhéroïnes… si elles parviennent à survivre aux dangers du présent.', 156.6, '/xAHGuVQFTbSbbdeUbkfrAY8kf2m.jpg', '2024-02-14', 0, '', '', 'Madame Web');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 792307, '', 'en', 'Poor Things', 'Une fable étonnante ayant pour héroïne Bella Baxter, une jeune femme ramenée à la vie par un savant brillant et peu conventionnel. Désireuse de connaître le monde, Bella s''embarque dans une aventure tourbillonnante sur plusieurs continents. Étrangère aux préjugés de son époque, Bella fonce tête baissée dans sa quête d''égalité et d''émancipation.', 118.161, '/roRjNH7cRVrZQYv1Ex8ownMu2ey.jpg', '2024-01-17', 0, '', '', 'Pauvres créatures');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1022796, '', 'en', 'Wish', 'Asha vit à Rosas, un royaume où tous les souhaits peuvent littéralement s''exaucer. Dans un moment de désespoir, elle adresse un vœu aux étoiles auquel va répondre une force cosmique :  une petite boule d''énergie infinie prénommée Star. Avec son aide, Asha doit sauver son royaume d''un roi et prouver que lorsque le courage d''un humain se lie à la magie des étoiles, de merveilleuses choses peuvent arriver.', 135.012, '/g3wF6T4xa727p4W3A44oFqM3XTm.jpg', '2023-11-29', 0, '', '', 'Wish, Asha et la bonne étoile');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1072790, '', 'en', 'Anyone But You', 'Bea et Ben ont tout du couple parfait, mais après un premier rendez-vous idéal, un incident refroidit leur attirance réciproque jusqu''à leurs retrouvailles inattendues lors d''un mariage en Australie. Ils font alors ce que n''importe quel adulte mature ferait dans cette situation : prétendre être en couple.', 129.439, '/41Dg2A3EsS3mAjeqVH1c2EdeGU3.jpg', '2024-01-24', 0, '', '', 'Tout sauf toi');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 926393, '', 'en', 'The Equalizer 3', 'Depuis qu''il a renoncé à sa vie d''assassin au service du gouvernement, Robert McCall peine à enterrer les démons de son passé et trouve un étrange réconfort en défendant les opprimés. Alors qu''il pense avoir trouvé un havre de paix dans le sud de l''Italie, il découvre que ses amis sont sous le contrôle de la mafia locale. Quand les évènements prennent une tournure mortelle, McCall sait exactement ce qu''il doit faire : protéger ses amis en s''attaquant directement à la pègre.', 97.853, '/go2gtucO5LT92jnU40pbD0LImgg.jpg', '2023-08-30', 0, '', '', 'Equalizer 3');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1041613, '', 'en', 'Immaculate', 'Cecilia, une jeune femme de foi américaine, est chaleureusement accueillie dans un illustre couvent isolé de la campagne italienne, où elle se voit offrir un nouveau rôle. L''accueil est chaleureux, mais Cecilia comprend rapidement que sa nouvelle demeure abrite un sinistre secret et que des choses terribles s''y produisent.', 152.952, '/aRhtHbgFGVKI5LJHcbSqQ0WbNib.jpg', '2024-03-20', 0, '', '', 'Immaculée');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 984324, '', 'fr', 'Le salaire de la peur', 'Afin d''empêcher une explosion qui ferait des centaines de victimes, une équipe clandestine est formée pour transporter de la nitroglycérine à travers un désert parsemé de dangers.', 143.167, '/kHxJHwSGjC0Fjl7ktWeWg7UrVF8.jpg', '2024-03-29', 0, '', '', 'Le salaire de la peur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1155089, '', 'en', 'Justice League: Crisis on Infinite Earths Part One', 'La mort arrive. Pire que la mort : l''annihilation. Pas seulement pour notre Terre, mais pour tout le monde, absolument partout, dans tous les univers ! Pour lutter contre cette destruction sans précédent, le mystérieux Monitor a réuni la plus grande équipe de super-héros jamais créée. Mais les pouvoirs combinés de Superman, Wonder Woman, Batman, Flash, Green Lantern et d''autres centaines de héros venus de différentes Terres seront-ils réellement suffisants pour défendre la réalité entière face à l''ARMAGEDDON ?!', 126.605, '/6v7K3C05dvwp4OJwxtBQXL608xU.jpg', '2024-01-09', 0, '', '', 'Justice League : Crisis on Infinite Earths Partie 1');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 695721, '', 'en', 'The Hunger Games: The Ballad of Songbirds & Snakes', 'Le jeune Coriolanus est le dernier espoir de sa lignée, la famille Snow autrefois riche et fière est aujourd’hui tombée en disgrâce dans un Capitole d''après-guerre. À l’approche des 10ème Hunger Games, il est assigné à contrecœur à être le mentor de Lucy Gray Baird, une tribut originaire du District 12, le plus pauvre et le plus méprisé de Panem. Le charme de Lucy Gray ayant captivé le public, Snow y voit l’opportunité de changer son destin, et va s’allier à elle pour faire pencher le sort en leur faveur. Luttant contre ses instincts, déchiré entre le bien et le mal, Snow se lance dans une course contre la montre pour survivre et découvrir s’il deviendra finalement un oiseau chanteur ou un serpent.', 121.962, '/A1FnJai5qjvtwYA8GiWYrqFA8Xo.jpg', '2023-11-15', 0, '', '', 'Hunger Games : La Ballade du serpent et de l''oiseau chanteur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 299054, '', 'en', 'Expend4bles', 'Armés de toutes les armes dont ils peuvent disposer, les Expendables sont la dernière ligne de défense du monde et l''équipe que l''on appelle lorsque toutes les autres options ne sont plus possibles. Le mercenaire Barney Ross et sa nouvelle équipe affrontent un marchand d''armes et son armée privée. Nouveaux membres, nouveaux styles, nouvelles tactiques.', 106.079, '/gsgAWGg94pShMku6Is1EhmJCIzc.jpg', '2023-10-11', 0, '', '', 'Expendables 4');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 955916, '', 'en', 'Lift', 'Un pro du braquage tente le coup du siècle avec une équipe chevronnée : dérober 500 millions de dollars en or... dans un avion en plein vol !', 75.948, '/2DfxiFl5XlRfBFCme9Ea9nLV9tT.jpg', '2024-01-12', 0, '', '', 'En plein vol');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 866398, '', 'en', 'The Beekeeper', 'La quête brutale de vengeance d’un homme prend des proportions démesurées alors que son passé d’agent secret d’une puissante organisation clandestine connue sous le nom des Apiculteurs est révélé.', 117.215, '/hGMcpGpnNrvv3Nn8B252jEvEvJV.jpg', '2024-01-11', 0, '', '', 'The Beekeeper');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 893723, '', 'en', 'PAW Patrol: The Mighty Movie', 'Lorsqu''une météorite magique s''écrase sur Aventureville, elle donne à la Pat'' Patrouille des superpouvoirs, les transformant en SUPER PATROUILLE ! Mais les choses dégénèrent lorsque Monsieur Hellinger, l''ennemi juré de nos amis, s''évade de prison et s''associe à une scientifique folle afin de voler et de s’accaparer ces nouveaux pouvoirs. Le destin d''Aventureville est désormais en jeu, la Super Patrouille doit arrêter les super-méchants avant qu''il ne soit trop tard.', 119.718, '/ldRLFfhHluqDRd7EWG6tSGp42M9.jpg', '2023-10-11', 0, '', '', 'La Pat'' Patrouille : La Super Patrouille, le film');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 856289, '', 'zh', '封神第一部：朝歌风云', 'Creation of the Gods est l''adaptation d''une des plus grandes légendes chinoises. Le Prince Yin Shou tente de monter sur le trône du royaume des Shang dans le sang avec l’aide de sa maîtresse Su Daji elle-même sous l''emprise du Démon Renard. Le sage taoïste Jiang Ziya descendu du Mont sacré Kunlun et Ji Fa, jeune guerrier élevé par Yin Shou, s’allient pour combattre le tyran.', 114.563, '/tc82p9xSE7QKT1RwpegCsKYt7Go.jpg', '2024-02-10', 0, '', '', 'Creation of the Gods I: Kingdom of Storms');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 897087, '', 'en', 'Freelance', 'Un agent des forces spéciales décide de prendre sa retraite de l’armée et de fonder une famille aux États-Unis. Après plusieurs années d’efforts pour se conformer à la vie en banlieue, il décide de sortir de sa retraite et d’accepter un contrat pour assurer la sécurité d’une journaliste alors qu’elle interviewe un dictateur sud-américain. Lorsqu’un coup d’État militaire éclate en plein milieu de l’interview, les voici tous trois contraints de s‘enfuir dans la jungle où ils doivent survivre aux éléments, aux militaires et les uns aux autres.', 77.998, '/wRTKRKfXxf19eDEoxUYG6fPc1ob.jpg', '2024-01-27', 0, '', '', 'Freelance');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 901362, '', 'en', 'Trolls Band Together', 'Poppy découvre que Branche et ses quatre frères faisaient autrefois partie de son boys band préféré. Lorsque l''un de ses frères et sœurs, Floyd, est kidnappé par une paire de méchants infâmes, Branche et Poppy se lancent dans un voyage déchirant et émouvant pour réunir les autres frères et sauver Floyd d''un destin encore pire que l''obscurité de la culture pop.', 88.437, '/xC6Zc2jpa3dD8IzgXQSzC3rDbc3.jpg', '2023-10-18', 0, '', '', 'Les Trolls 3');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 453395, '', 'en', 'Doctor Strange in the Multiverse of Madness', 'Le Docteur Strange lance un sort interdit qui ouvre le portail du Multivers, incluant des versions alternatives de lui-même. Cette menace à l''humanité est plus puissante que les pouvoirs combinés de Strange, Wong et Wanda Maximoff.', 100.375, '/arfzjn1tGvXWwkX7eaGVuXsc0mp.jpg', '2022-05-04', 0, '', '', 'Doctor Strange in the Multiverse of Madness');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1026227, '', 'it', 'C''è ancora domani', 'Mariée à Ivano, Delia, mère de trois enfants, vit à Rome dans la seconde moitié des années 40. La ville est alors partagée entre l’espoir né de la Libération et les difficultés matérielles engendrées par la guerre qui vient à peine de s’achever. Face à son mari autoritaire et violent, Delia ne trouve du réconfort qu’auprès de son amie Marisa avec qui elle partage des moments de légèreté et des confidences intimes. Leur routine morose prend fin au printemps, lorsque toute la famille en émoi s’apprête à célébrer les fiançailles imminentes de leur fille aînée, Marcella. Mais l’arrivée d’une lettre mystérieuse va tout bouleverser et pousser Delia à trouver le courage d’imaginer un avenir meilleur, et pas seulement pour elle-même.', 92.92, '/bgOS0ILhxR6tNzkzS2QvwkOGfGb.jpg', '2024-03-13', 0, '', '', 'Il reste encore demain');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1125311, '', 'en', 'Imaginary', 'Lorsque Jessica retourne dans sa maison d''enfance avec sa famille, sa plus jeune belle-fille Alice développe un attachement étrange pour un ours en peluche qu''elle a trouvé dans le sous-sol et nommé Chauncey. Tout commence par des jeux innocents, mais le comportement d''Alice devient de plus en plus inquiétant. Jessica comprend alors que Chauncey est bien plus qu''un simple jouet.', 78.439, '/s3OzOyXscydHtSzzskAt9qBZZgm.jpg', '2024-03-06', 0, '', '', 'Imaginary');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 968051, '', 'en', 'The Nun II', 'En France, en 1956, un prêtre est assassiné dans un internat. Après la mort du prêtre, il se passe des choses qui ne peuvent plus être expliquées rationnellement. Sœur Irène est une nouvelle fois confrontée à un pouvoir démoniaque. Elle se rend vite compte que c''est le démon Valak qui est de retour et qui se prépare à ses méfaits meurtriers.', 84.122, '/yOrY0pByezFe8Eo3AcxW5zCBj5w.jpg', '2023-09-13', 0, '', '', 'La Nonne : La Malédiction de Sainte-Lucie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 335977, '', 'en', 'Indiana Jones and the Dial of Destiny', '1969. Après avoir passé plus de dix ans à enseigner au Hunter College de New York, l''estimé docteur Jones, professeur d''archéologie, est sur le point de prendre sa retraite et de couler des jours paisibles. Tout bascule après la visite surprise de sa filleule Helena Shaw, qui est à la recherche d''un artefact rare que son père a confié à Indy des années auparavant : le fameux cadran d''Archimède, une relique qui aurait le pouvoir de localiser les fissures temporelles. En arnaqueuse accomplie, Helena vole l’objet et quitte précipitamment le pays afin de le vendre au plus offrant. Indy n''a d''autre choix que de se lancer à sa poursuite. Il ressort son fédora et son blouson de cuir pour une dernière virée...', 91.299, '/64TyGedJPmfwfO0dIQWFDLIw1U7.jpg', '2023-06-28', 0, '', '', 'Indiana Jones et le Cadran de la destinée');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1170260, '', 'de', 'Early Birds', 'Témoin malgré elle d''un deal de drogue qui a mal tourné, une femme s''enfuit avec une partenaire improbable. Les deux devront échapper aux flics, aux dealers et à leurs propres démons.', 77.641, '/ScNq4hVpvGigAv5RSPtqUYm0x9.jpg', '2024-03-06', 0, '', '', 'Early Birds');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 848538, '', 'en', 'Argylle', 'Quand les intrigues de ses romans d''espionnage rappellent un peu trop les activités d''une sombre organisation criminelle, Elly Conway, romancière introvertie, se retrouve immergée avec son chat dans le monde réel des espions, où tout n''est que faux-semblant.', 71.896, '/uVAk2YliqInQfH4B4vzZ75rwcNB.jpg', '2024-01-31', 0, '', '', 'Argylle');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 932420, '', 'en', 'Code 8 Part II', 'Connor est sorti de prison et travaille comme homme de ménage dans un centre social. Il a coupé les ponts avec son ancien complice, Garrett, et essaie de filer droit jusqu''au jour où il est bien obligé d''aider Pav, une ado de 14 ans, à échapper aux griffes d''une meute de policiers corrompus sous les ordres du sergent King. Alors que King et ses hommes se servent de nouveaux robots chiens pour retrouver Pav, Connor n''a d''autre choix que de redemander de l''aide à Garrett et à sa bande… mais comment faire confiance à l''homme qui l''a fait mettre en prison ?', 79.159, '/1aJerbWBk8sBOEXEFwgojSMYOIU.jpg', '2024-02-28', 0, '', '', 'Code 8 : Partie II');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1139829, '', 'en', 'Orion and the Dark', 'Un garçon plein d''imagination affronte ses peurs lors d''un périple inoubliable à travers la nuit avec son nouvel ami, une créature géante et souriante qui s''appelle Noir.', 76.741, '/gFzfnuAcr551q4FMGurYMUMR8fp.jpg', '2024-02-02', 0, '', '', 'La Nuit d''Orion');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1022690, '', 'en', 'Ricky Stanicky', 'Trois amis d''enfance font une farce qui tourne mal et inventent un ami imaginaire afin qu’il porte le chapeau : Ricky Stanicky ! Vingt ans plus tard, Ricky est toujours leur alibi pour justifier leur immaturité. Leurs familles ont des doutes et exigent de rencontrer le légendaire Stanicky. Le trio de menteurs engagent alors un acteur et imitateur de célébrités ringard pour lui donner vie.', 63.01, '/cEuX73Q1wpMypB1cr4l3MoMGpL4.jpg', '2024-03-07', 0, '', '', 'Ricky Stanicky');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1139566, '', 'es', 'A través de mi ventana 3: A través de tu mirada', 'Raquel et Ares font face aux défis qui se présentent dans ce dernier chapitre de leur relation et de leurs vies individuelles.', 75.781, '/j8cygJdTMUTsQLOUSHuWppQrvUX.jpg', '2024-02-23', 0, '', '', 'À travers ma fenêtre 3 : Les yeux dans les yeux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 744857, '', 'es', 'Cuando acecha la maldad', 'Après avoir découvert un cadavre mutilé près de leur propriété, deux frères apprennent que les événements étranges survenant dans leur village sont causés par un esprit démoniaque qui a élu domicile dans le corps purulent d’un homme. Le mal dont souffre ce dernier ne tarde pas à se répandre comme une épidémie, affectant d’autres habitants de la région.', 65.392, '/rwuI0qezt0hoXKSzZiA8wOLdeDk.jpg', '2024-05-15', 0, '', '', 'When Evil Lurks');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 802219, '', 'en', 'Bob Marley: One Love', 'Célébrez la vie et la musique d''une icône qui a inspiré plusieurs générations grâce à son message d''amour, de paix et d''unité. Dans ce film puissant et inspirant, découvrez comment Bob Marley a surmonté l''adversité et comment sa musique révolutionnaire a changé le monde.', 59.477, '/iN6WZnetgrf2gnS4bMdCDZLDkar.jpg', '2024-02-14', 0, '', '', 'Bob Marley : One Love');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1148027, '', 'en', 'New Life', 'Jessica Murdock est une femme en fuite qui tente désespérément d’échapper aux autorités en traversant la frontière canadienne. Elle est traquée par Elsa Gray, qui n’est ni policière ni agente fédérale, mais plutôt une mercenaire qui a un don pour retrouver les personnes qui tentent de disparaître. Alors que la poursuite se resserre, des corps sont retrouvés sur le chemin de Jessica. La confrontation entre ces deux femmes, en quête de sens et de rédemption, semble inévitable.', 63.173, '/nk15OpDdPgRFgA3I536FCAbSG74.jpg', '2024-01-28', 0, '', '', 'New Life');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 11351, '', 'en', 'Jeepers Creepers 2', 'Tous les 23 ans, une créature maléfique surgie des profondeurs de la Terre prend son envol et, durant 23 jours, sème la terreur et la mort. Son nom : le Creeper. Au 22ème jour de ce cycle infernal, le fermier Jack Taggart voit son jeune fils Billy se faire enlever et dévorer par le monstre. À quelques kilomètres de là, un car scolaire transportant une équipe de basket, ses entraîneurs et ses pom-pom girls, tombe mystérieusement en panne en rase campagne. La nuit approche. Le Creeper alléché fond sur ses jeunes proies. Commence alors un combat désespéré pour la survie…', 64.088, '/69HBuUNfXKV7PghGH80fywWdSce.jpg', '2004-02-04', 0, '', '', 'Jeepers Creepers 2');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 973912, '', 'fr', 'Cocorico', 'Sur le point de se marier, Alice et François décident de réunir leurs deux familles. Pour l’occasion, ils réservent à leurs parents un cadeau original : des tests ADN pour que chacun puisse découvrir les origines de ses ancêtres. Mais la surprise va virer au fiasco quand les Bouvier Sauvage, grande famille aristocrate, et les Martin, beaucoup plus modestes, découvrent les résultats, pour le moins… inattendus !', 100.556, '/uJCxYoJ45N7NId1JvnzrsScxesY.jpg', '2024-02-07', 0, '', '', 'Cocorico');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1216512, '', 'en', 'The Casagrandes Movie', 'En voyage au Mexique pour ses 12 ans, Ronnie Anne libère par mégarde une demi-déesse transformée en montagne et compte sur sa famille pour l''aider à arranger les choses.', 67.563, '/1lAIHXZihTwssgK5rxuL6dIdTMK.jpg', '2024-03-22', 0, '', '', 'Bienvenue chez les Casagrandes : Le film');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1014590, '', 'en', 'Upgraded', 'Contre toute attente, Ana, stagiaire dans une salle de ventes, se voit offrir par sa patronne un voyage professionnel de dernière minute pour Londres. Ayant la chance d''être surclassée, elle rencontre le beau et riche William et décide de vivre son rêve éveillé plus longtemps qu''elle ne le devrait.', 75.396, '/fVE3Th7UCcTVehxnjab5dAlUajH.jpg', '2024-02-09', 0, '', '', 'Surclassée');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1212073, '', 'de', '60 Minuten', 'Voulant à tout prix conserver la garde de sa fille, un combattant d''arts martiaux abandonne un match crucial et traverse Berlin pour assister à sa fête d''anniversaire.', 64.069, '/aajCqg315CoJPu1NmgPCkbRjnl6.jpg', '2024-01-19', 0, '', '', 'Soixante minutes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 784651, '', 'hi', 'फाइटर', 'Les meilleurs aviateurs de l''IAF se réunissent face à un danger imminent pour former les Air Dragons. Fighter dévoile sa camaraderie, sa fraternité et ses combats, internes et externes.', 69.233, '/zDZowwb9GZGEctAu2PCpjiPQAMM.jpg', '2024-01-24', 0, '', '', 'Fighter');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 635910, '', 'en', 'The Last Voyage of the Demeter', 'Le navire de commerce Demeter quitte la Transylvanie pour un long voyage vers Londres, avec à son bord une mystérieuse cargaison. D''étranges événements vont alors survenir et décimer peu à peu les membres d''équipage. Les survivants vont tenter de survivre à une présence qui sévit chaque nuit sur le Demeter.', 57.169, '/96QWb164zRwt3devmmpwAMVGIw7.jpg', '2023-08-23', 0, '', '', 'Le Dernier Voyage du Demeter');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 914206, '', 'fr', 'La Bête', 'Dans un futur proche où les émotions sont devenues une menace, Gabrielle se décide enfin à purifier son ADN dans une machine qui va la plonger dans ses vies antérieures et la débarrasser de tout sentiment fort. Elle rencontre alors Louis et ressent une puissante connexion, comme si elle le connaissait depuis toujours. Un mélodrame traversé par le genre, qui se déploie sur trois périodes distinctes, 1910, 2014 et 2044.', 61.058, '/aNLGMfyhz1ZRSHMkrUvJDTkW9Oe.jpg', '2024-02-07', 0, '', '', 'La Bête');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 408, '', 'en', 'Snow White and the Seven Dwarfs', 'Blanche Neige est une princesse d’une très grande beauté, ce qui rend jalouse sa belle‐mère. Celle‐ci demande quotidiennement à son miroir magique de lui dire qu’elle est la plus belle; mais un jour, le miroir affirme que la plus belle femme du royaume est Blanche Neige. La reine décide alors de la tuer, mais le garde chargé de cette tâche ne trouve pas le courage et abandonne Blanche Neige dans la forêt. Perdue, à bout de force, elle échoue dans une maison où habitent sept nains.', 66.854, '/dZy4vDboXhihq0AqTup8lzVBXkg.jpg', '1938-05-04', 0, '', '', 'Blanche-Neige et les Sept Nains');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1094556, '', 'en', 'The Thundermans Return', 'Les jumeaux Phoebe et Max profitent de leur vie de super-héros dans une toute nouvelle ville. Mais lorsqu''un « sauvetage » tourne mal, la famille Thunderman est renvoyée à Hiddenville. Alors que Hank et Barb profitent de leur retour, que Chloé se crée de nouveaux amis et que Billy et Nora commencent une vie normale au lycée, Max et Phoebe sont déterminés à retrouver leur statut de super-héros.', 67.18, '/p4HOnQETJNCrSAQvvjvx6rGAHeV.jpg', '2024-03-31', 0, '', '', 'Le Retour des Thunderman');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 758679, '', 'en', 'Mothers'' Instinct', 'Au début des années 60, deux voisines, Alice et Céline, par ailleurs meilleures amies, mènent une vie paisible et sans histoires entre leurs pelouses bien entretenues, leurs maris prospères et leurs deux fils du même âge. Cette harmonie parfaite se brise tout à coup suite à un tragique accident. La culpabilité, la suspicion et la paranoïa se combinent pour briser leur lien fraternel.', 52.886, '/6EtPToJlGpjyuIxkIoztnBXYFgJ.jpg', '2024-03-27', 0, '', '', 'Mothers'' Instinct');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 670292, '', 'en', 'The Creator', 'Alors qu''une future guerre entre la race humaine et l''intelligence artificielle fait rage, l''ancien agent des forces spéciales Joshua est recruté pour traquer et tuer le Créateur, l''insaisissable architecte de l''IA avancée.', 69.791, '/pP1cyoXFc5Br1Sg21uORSN49yyu.jpg', '2023-09-27', 0, '', '', 'The Creator');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 848439, '', 'en', 'Firebrand', 'Catherine Parr est la sixième femme du roi Henri VIII, dont les précédentes épouses ont été soit répudiées, soit décapitées (une seule étant décédée suite à une maladie). Avec l’aide de ses dames de compagnie, elle tente de déjouer les pièges que lui tendent l’évêque, la cour et le roi…', 60.968, '/lr9uabyIY0OcEtwt9lvuX7iwrIt.jpg', '2024-03-27', 0, '', '', 'Le jeu de la Reine');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 673593, '', 'en', 'Mean Girls', 'La nouvelle élève Cady Heron est accueillie au sommet de la hiérarchie sociale par le groupe des filles populaires appelé « Les Plastiques », dirigé par l''impitoyable diva, Regina George. Après avoir conclu une alliance secrète avec les exclus de l''école pour renverser Regina, Cady doit apprendre à rester fidèle à elle-même tout en naviguant dans la jungle la plus impitoyable de toutes : le lycée.', 71.327, '/jG3tYQYIAt327g8YedxY97UOmhQ.jpg', '2024-01-10', 0, '', '', 'Mean Girls : Lolita malgré moi');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1216299, '', 'en', 'The Night They Came Home', 'Dans l''Ouest américain en 1895, un US Marshal s''associe aux Amérindiens pour traquer le gang de Rufus Buck sur leur territoire', 65.258, '/fIqX334cyfG4LjIFeTAc9xNedXM.jpg', '2024-01-12', 0, '', '', 'The Night They Came Home');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 891699, '', 'en', 'Silent Night', 'L''histoire de vengeance d’un père tourmenté ayant été témoin de la mort de son fils, victime d’une balle perdue lors d’un affrontement entre gangs la veille de Noël. Alors qu’il se remet d’une blessure lui ayant couté la parole, il s’entraîne sans relâche afin de venger la mort de son fils.', 42.375, '/olOmXkNlSk4ysw7VWdhdyL2EEz6.jpg', '2024-02-29', 0, '', '', 'Silent Night');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 467244, '', 'en', 'The Zone of Interest', 'Le commandant d''Auschwitz Rudolf Höss et son épouse Hedwig réalisent sur un terrain directement adjacent au mur du camp leur vision d''une vie de rêve avec une famille nombreuse, une maison et un grand jardin. Cependant, lorsque Rudolf doit être muté à Oranienburg, leur petite vie idéale menace de s''effondrer et il cache l''information à son épouse. Quand Hedwig l''apprend, elle refuse de quitter sa maison de rêve.', 70.053, '/wD2eFNkSLV0ACM9C82qIYfTMFv9.jpg', '2024-01-31', 0, '', '', 'La Zone d''intérêt');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 951491, '', 'en', 'Saw X', 'Dans l''espoir d''une guérison miraculeuse, John Kramer se rend au Mexique pour une procédure médicale risquée et expérimentale, pour découvrir que toute l''opération est une arnaque visant à escroquer les plus vulnérables. Armé d''un nouvel objectif, le tristement célèbre tueur en série utilise des pièges dérangés et ingénieux pour renverser la situation contre les escrocs.', 67.215, '/kCsp11TgrvRN229lYRdKU1bFO6o.jpg', '2023-10-25', 0, '', '', 'Saw X');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 967582, '', 'fr', '3 jours max', 'Après avoir prouvé à tous qu''il est un policier hors pair, Rayan s''inscrit au concours d''agent secret. Il se donne à fond dans les épreuves mais le couperet tombe : il est recalé. Rayan n''a pas le temps de pleurer sur son sort, sa grand-mère et Lynda sont enlevées par d''anciens associés du rat.', 62.204, '/yLtsR4j4hkZnDh9JfCpMlucdRne.jpg', '2023-10-25', 0, '', '', '3 jours max');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 729854, '', 'ko', '콘크리트 유토피아', 'Le monde a été réduit en décombres par un tremblement de terre massif. Bien que personne ne sache avec certitude jusqu''où s''étendent les ruines ou quelle peut être la cause du tremblement de terre, au cœur de Séoul, il ne reste qu''un seul immeuble d''appartements. Ils s''appellent les appartements Hwang Gung.', 56.536, '/ghgQtuasVccaR7JW6N4zoSrLorO.jpg', '2024-01-28', 0, '', '', 'Concrete Utopia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 636706, '', 'en', 'Spaceman', 'Six mois après son départ, un astronaute en mission solo essaie de sauver son mariage qui bat de l''aile avec l''aide d''une mystérieuse créature qui se cache dans son vaisseau.', 38.493, '/f46WvKEsBn98WJbPJcO47ZoKn6B.jpg', '2024-03-01', 0, '', '', 'Spaceman');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 959395, '', 'fr', 'La nouvelle femme', 'En 1900, Lili d’Alengy, célèbre courtisane parisienne, a un secret honteux - sa fille Tina, née avec un handicap. Peu disposée à s’occuper d’une enfant qui menace sa carrière, elle décide de quitter Paris pour Rome. Elle y fait la connaissance de Maria Montessori, une femme médecin qui développe une méthode d’apprentissage révolutionnaire pour les enfants qu’on appelle alors « déficients ». Mais Maria cache elle aussi un secret : un enfant né hors mariage. Ensemble, les deux femmes vont s’entraider pour gagner leur place dans ce monde d’hommes et écrire l’Histoire.', 81.546, '/qBWKIcBKxypZuG88HapvAs2MB6r.jpg', '2024-03-13', 0, '', '', 'La nouvelle femme');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 906126, '', 'es', 'La sociedad de la nieve', 'Le 13 octobre 1972, le vol 571 de l''armée de l''air uruguayenne, affrété pour transporter une équipe de rugby au Chili, s''écrase sur un glacier au cœur des Andes.', 54.917, '/nDbV9AUeUc7Zf5vUAqoagF7O8M7.jpg', '2023-10-16', 0, '', '', 'Le Cercle des neiges');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 807172, '', 'en', 'The Exorcist: Believer', 'Depuis que sa femme a perdu la vie treize ans plus tôt, Victor Fielding élève, seul, leur fille Angela. Un jour, Angela et son amie Katherine disparaissent dans les bois avant de refaire surface 72 heures plus tard sans le moindre souvenir de ce qui leur est arrivé. Dès lors, d''étranges événements s''enchaînent et Victor doit affronter des forces maléfiques. Désespéré et terrorisé, il sollicite la seule personne encore en vie qui ait jamais été témoin de pareils phénomènes : Chris MacNeil.', 64.016, '/pbBwVz8FCt1L1YOOOVsmnaLeSyV.jpg', '2023-10-11', 0, '', '', 'L''Exorciste : Dévotion');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1128606, '', 'en', 'Snoopy Presents: Welcome Home, Franklin', 'Franklin vient d’emménager et il espère se faire des amis. Malheureusement, ses approches habituelles ne semblent pas fonctionner sur la bande des Peanuts. Une course de caisses à savon a lieu en ville, il y voit une chance d’impressionner ses camarades et décide de faire équipe avec le seul enfant qui n’a pas trouvé de coéquipier : Charlie Brown.', 44.804, '/zXEmVBnIuPgZLiLdOb8zKXPFtOO.jpg', '2024-02-16', 0, '', '', 'Snoopy présente : Bienvenue à la maison, Franklin');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 812037, '', 'fr', 'L''été dernier', 'Anne, brillante avocate, défend les mineurs victimes d’abus et les adolescents en difficulté. Son mari Pierre, leurs deux filles et elle vivent en parfaite harmonie dans une belle villa sur les hauteurs de Paris. Lorsque Théo, fils de Pierre né d’un précédent mariage emménage avec eux, l’équilibre de la famille se retrouve chamboulé par l’irruption de cet adolescent rebel et contestataire. Anne entame en effet une liaison avec cet adolescent de dix-sept ans rebelle et contestataire, et cela risque de tout détruire.', 58.67, '/j9IdrFAH2MwoFaMAUx34x8uiFe3.jpg', '2023-09-13', 0, '', '', 'L''été dernier');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 974558, '', 'en', 'Destroy All Neighbors', 'Le musicien, William Brown, se retrouve dans un véritable cauchemar lorsqu''il tue accidentellement Vlad, son voisin.', 44.852, '/jIpjUy6ACDk8Fc8GkynLcIAPP49.jpg', '2024-01-27', 0, '', '', 'Destroy All Neighbors');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 974036, '', 'en', 'Ordinary Angels', 'Une coiffeuse en difficulté retrouve un sens renouvelé lorsqu''elle rencontre un père veuf qui travaille dur pour prendre soin de ses deux filles. Alors que la plus jeune est gravement malade et attend une greffe du foie, cette femme féroce rallie à elle seule toute une communauté pour l''aider.', 45.758, '/1NW0Ily0RyCPad1sNd2rl3W3LUc.jpg', '2024-03-26', 0, '', '', 'Ordinary Angels');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 975773, '', 'en', 'Wicked Little Letters', 'Lorsque les habitants de Littlehampton, y compris la très conservatrice Edith, commencent à recevoir des lettres remplies d''obscénités et de blasphèmes hilarants, Rose, une immigrante irlandaise et turbulente, est accusée du crime. Soupçonnant que quelque chose ne tourne pas rond, les femmes de la ville mènent l''enquête.', 43.41, '/3OB8B8iSznL6rlmNNfg9Bs5H2F1.jpg', '2024-03-13', 0, '', '', 'Scandaleusement vôtre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 848187, '', 'en', 'Role Play', 'Emma et Dave pimentent leur soirée d''anniversaire avec un jeu de rôle à New York. Mais les choses deviennent dangereuses lorsque la vie secrète d''assassin international de Emma, inconnue de Dave, est exposée par Bob, ce qui met en danger sa famille. Emma doit se servir de ses compétences mortelles et sa détermination pour protéger sa famille à tout prix.', 42.348, '/nAdbrQvzIlqGTmod9kgsbKeCLAV.jpg', '2024-01-13', 0, '', '', 'Role Play');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1248795, '', 'tr', 'Romantik Hırsız', 'Alin, policière, travaille au sein de l''unité Vol d''œuvres d''art d''Interpol. Depuis quelque temps, elle est sur la piste d''un voleur qui dérobe des tableaux de valeur dans des musées réputés imprenables du monde entier. Quelle que soit l''identité de ce malfrat, il est clair qu''il n''est pas motivé par l''argent. Après avoir passé des années à traquer ce gentleman cambrioleur, Alin devine quelle est sa prochaine cible. Et la policière découvre avec stupéfaction que le coupable n''est autre qu''un ancien amoureux, Güney, homme d''affaires milliardaire qui a soudainement disparu alors que le couple était sur le point de se marier. Alin n''a plus qu''une solution : retrouver Güney et le prendre en flagrant délit.', 54.135, '/7yQrflwBkZWVJbrJs67s31IKPUJ.jpg', '2024-03-14', 0, '', '', 'Le Cœur au vol');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 944401, '', 'fr', 'Dogman', 'L’incroyable histoire d’un enfant, meurtri par la vie, qui trouvera son salut grâce à l’amour que lui portent ses chiens.', 43.441, '/3L91Cmz5lhfPR5ppsmkGT2IC2lJ.jpg', '2023-09-27', 0, '', '', 'Dogman');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 945729, '', 'en', 'A Haunting in Venice', 'Après la Seconde Guerre mondiale en une sombre veille de Toussaint. Retraité, vivant un exil volontaire dans la resplendissante Venise, en Italie, Poirot assiste à contre-cœur à une séance de spiritisme dans un palazzo hanté en ruines. Quand une invitée est assassinée, le détective est propulsé dans un univers sinistre d''obscurité et de secrets.', 30.366, '/wQdOoevcY3cYDySvtcezd1AkP1p.jpg', '2023-09-13', 0, '', '', 'Mystère à Venise');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 850165, '', 'en', 'The Iron Claw', 'L''histoire vraie des inséparables frères Von Erich, qui ont marqué l''histoire du monde extrêmement compétitif de la lutte professionnelle au début des années 1980. À travers la tragédie et le triomphe, sous l''ombre de leur père et entraîneur tyrannique, les frères recherchent une immortalité plus grande que nature sur la plus grande scène sportive.', 39.755, '/64gAIwqLz0TQbZMtefYyZmHvQQk.jpg', '2024-01-24', 0, '', '', 'Iron Claw');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 306, '', 'en', 'Beverly Hills Cop III', 'Dans cette nouvelle aventure Axel Foley va mener l''enquête dans le célèbre parc d''attractions de Wonderworld à Beverly Hills. Il recherche le dangereux commanditaire d''une bande de voleurs de voitures responsable de la mort de son ami Todd.', 37.366, '/hnBhVTb8xwxNFA02GwI43yQ4sVw.jpg', '1994-08-17', 0, '', '', 'Le Flic de Beverly Hills 3');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1072342, '', 'en', 'Night Swim', 'Ray Waller, un ancien joueur de football américain qui a dû abandonner sa carrière en raison d’une maladie dégénérative, s’installe dans une nouvelle maison, avec sa famille : sa femme Eve, leur fille Izzy et leur plus jeune fils. Ray, contre toute attente, ne désespère pas de reprendre sa carrière et persuade toute la famille que la piscine qui se trouve dans le jardin sera parfaite pour sa rééducation et pour ses enfants. Mais cette maison cache un lourd passé et ils vont sans le vouloir réveiller une force malveillante qui va tous les précipiter aux tréfonds de l’horreur absolue.', 48.351, '/srQXP7Pr8yHdkpwHzrCb8HVsLOd.jpg', '2024-01-03', 0, '', '', 'Night Swim');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1020006, '', 'en', 'Priscilla', 'Lorsque l''adolescente Priscilla Beaulieu rencontre Elvis Presley, superstar fulgurante du rock and roll, celle-ci découvre lors de moments d’intimité un homme totalement inattendu: un amoureux passionné, un allié dans la solitude, un meilleur ami vulnérable.', 44.644, '/hshJfvKsm45ZgNuYFhMKvCqtSi9.jpg', '2024-01-03', 0, '', '', 'Priscilla');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1027073, '', 'en', 'In the Land of Saints and Sinners', 'Irlande, années 1970. Désireux de laisser son sombre passé derrière lui, Finbar Murphy mène une vie tranquille dans la ville côtière isolée de Glencolmcille, loin de la violence politique qui s''empare du reste du pays. Mais lorsqu''arrive une équipe menaçante de terroristes, dirigée par une femme impitoyable nommée Doirean, Finbar est entraîné dans un jeu de plus en plus vicieux du chat et de la souris, l''obligeant à choisir entre révéler son identité secrète ou défendre ses amis et voisins.', 37.355, '/tmGMOhp9xNm1D0HhSvWvwkUaPOi.jpg', '2024-02-23', 0, '', '', 'Saints & Sinners');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1043899, '', 'fr', 'Dans la peau de Blanche Houellebecq', 'En Guadeloupe, Blanche Gardin préside un concours de sosie consacré à Michel Houellebecq. Michel s’y rend, mais des événements imprévus vont plonger notre duo au cœur d’une intrigue rocambolesque…', 42.371, '/6uKVZmlrFIRa1xPbJu2V6VTYRdy.jpg', '2024-03-13', 0, '', '', 'Dans la peau de Blanche Houellebecq');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 760774, '', 'en', 'One Life', 'D''après l''histoire vraie du britannique Nicholas Winton (1909-2015). Avant le début de la Seconde Guerre mondiale, âgé de 29 ans, il sauve 669 enfants tchécoslovaques dont beaucoup sont juifs d''une mort certaine dans les camps d''extermination nazis.', 38.972, '/i66uPRqNC1BuyxPUmbOchxWmMut.jpg', '2024-02-21', 0, '', '', 'Une vie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 994108, '', 'en', 'All of Us Strangers', 'Le scénariste Adam rencontre son mystérieux voisin Harry. Alors qu''Adam et Harry se rapprochent, Adam est ramené dans sa maison d''enfance où il découvre que ses parents - décédés depuis longtemps - sont tous deux vivants et semblent avoir le même âge que le jour de leur mort, il y a plus de 30 ans.', 43.679, '/tNM9eZorWI2meW3rxpenu6YjdtC.jpg', '2024-02-14', 0, '', '', 'Sans jamais nous connaître');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 937746, '', 'it', 'Io capitano', 'Seydou et Moussa, deux jeunes sénégalais de 16 ans, décident de quitter leur terre natale pour rejoindre l’Europe. Mais sur leur chemin les rêves et les espoirs d’une vie meilleure sont très vite anéantis par les dangers de ce périple. Leur seule arme dans cette odyssée restera leur humanité.', 37.861, '/ydHUap6TqDR0GZakpwIJEiMKmZi.jpg', '2024-01-03', 0, '', '', 'Moi capitaine');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 980026, '', 'da', 'Bastarden', 'Au Danemark, en 1755, le capitaine Ludvig Kahlen part à la conquête d''une lande danoise réputée incultivable avec un objectif impossible : établir une colonie au nom du roi, en échange d''un titre royal qui le sortirait de la misère. Une ambition bornée que l''impitoyable seigneur de la région cherchera sans relâche à étouffer. Le destin de Kahlen est alors en jeu : son entreprise lui apportera la richesse et l''honneur, ou lui coûtera la vie.', 32.533, '/f62GP1AohDJfKkXTXCqUeMxcSqb.jpg', '2024-01-26', 0, '', '', 'King’s Land');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 852445, '', 'fr', 'Acide', 'Selma, 15 ans, grandit entre ses deux parents séparés, Michal et Élise. Des nuages de pluies acides et dévastatrices s’abattent sur la France. Dans un monde qui va bientôt sombrer, cette famille fracturée va devoir s’unir pour affronter cette catastrophe climatique et tenter d’y échapper.', 35.757, '/91vF7SNB2tqsSultsmEn8tJYh4l.jpg', '2023-09-20', 0, '', '', 'Acide');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 958006, '', 'en', 'The Kill Room', 'Patrice a un gros problème. Sa galerie d''art new-yorkaise n''est pas profitable. Gordon et Reggie ont un problème encore plus important. Même si s''occuper de meurtres pour la mafia est une affaire lucrative, ils doivent trouver un moyen de blanchir leur argent. Lorsqu''elle les rencontre grâce à une connaissance mutuelle, le monde de l''art se retrouve en présence de la pègre, et un assassin devient accidentellement une sensation avant-gardiste. Mais l''argent généré entraîne des décisions désastreuses, et la survie du trio se retrouve en jeu.', 27.392, '/kGFcgAYfovTeuLl1VKb4LZGAyEN.jpg', '2024-03-21', 0, '', '', 'The Kill Room');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 864101, '', 'ru', 'Мира', 'Astronaute mécanicien de bord d''une station spatiale, Arabov, en orbite depuis six ans, a piraté la caméra de vidéosurveillance de Vladivostok où vit sa fille Lera, adolescente, histoire de pouvoir garder un œil sur elle. Lorsqu''une pluie de météores menace de s''abattre sur la ville, Arabov cherche par tous les moyens à prévenir sa fille de l''imminence d''une catastrophe, d''une ampleur sans précédent.', 31.941, '/8r0B6GsaQCYUXNLJl59Mjw72t9Y.jpg', '2024-02-25', 0, '', '', 'Firefall');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 50124, '', 'it', 'Monella', 'Lola est une jeune et jolie jeune fille fiancée avec Masetto, le fils du boulanger. Malheureusement, celui-ci est un peu vieux jeu. Il est convaincu qu''ils doivent demeurer vierges jusqu''à leur mariage. Bien sûr, la charmante Lola n''est pas de cet avis. Elle a beau multiplier les entreprises de séduction, Masetto résiste ! Elle se tourne donc vers André, un homme plus âgé et séduisant, possédant une solide expérience des aventures érotiques. Ses récits enflamment l''imagination de Lola qui commence à penser qu''il serait peut-être un bien meilleur amant que le jeune Masetto...', 34.343, '/7eWIbH9eIAvfFJh63LMAm4qmGyZ.jpg', '2006-08-17', 0, '', '', 'Monella - Lola la frivole');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1053544, '', 'sv', 'Avgrunden', 'Alors que la ville suédoise de Kiruna s''enfonce, Frigga est déchirée entre sa famille et son poste de cheffe de la sécurité dans la plus grande mine souterraine au monde.', 37.01, '/32AIdz6KySK6DaiYYc140DKN0aM.jpg', '2024-02-16', 0, '', '', 'The Abyss');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 964960, '', 'fr', 'La Passion de Dodin Bouffant', 'Eugénie, cuisinière hors pair, est depuis 20 ans au service du célèbre gastronome Dodin. À force de passer du temps ensemble en cuisine, une passion amoureuse s’est construite entre eux où l’amour est étroitement lié à la pratique de la gastronomie.  De cette union naissent des plats tous plus savoureux et délicats les uns que les autres qui vont jusqu’à émerveiller les plus grands de ce monde. Pourtant, Eugénie, avide de liberté, n’a jamais voulu se marier avec Dodin. Ce dernier décide alors de faire quelque chose qu’il n’a encore jamais fait : cuisiner pour elle.', 30.213, '/xfyrDubkZ6v7w54GgdtIBU8ucem.jpg', '2023-11-08', 0, '', '', 'La Passion de Dodin Bouffant');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 39538, '', 'en', 'Contagion', 'Une pandémie dévastatrice explose à l’échelle du globe… Au Centre de Prévention et de Contrôle des Maladies, des équipes se mobilisent pour tenter de décrypter le génome du mystérieux virus, qui ne cesse de muter. Le Sous-Directeur Cheever, confronté à un vent de panique collective, est obligé d’exposer la vie d’une jeune et courageuse doctoresse. Tandis que les grands groupes pharmaceutiques se livrent une bataille acharnée pour la mise au point d’un vaccin, le Dr. Leonora Orantes, de l’OMS, s’efforce de remonter aux sources du fléau. Les cas mortels se multiplient, jusqu’à mettre en péril les fondements de la société, et un blogueur militant suscite une panique aussi dangereuse que le virus en déclarant qu’on "cache la vérité" à la population…', 26.13, '/iHG9Ke6kuhOMnFCkdqwcXGFJpg5.jpg', '2011-11-09', 0, '', '', 'Contagion');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1081400, '', 'en', 'The Mill', 'Un homme d''affaires se réveille à côté d''un ancien moulin situé au centre d''une cellule de prison à ciel ouvert, sans savoir comment il est arrivé là. Contraint de travailler comme une bête de somme pour rester en vie, il doit trouver le moyen de s''échapper avant la naissance de son enfant.', 24.25, '/c0vBtCxcyR20vVeYicxJZ9edu3F.jpg', '2024-03-15', 0, '', '', 'Le Moulin');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 598387, '', 'en', 'The Tiger''s Apprentice', 'Tom Lee découvre qu''il appartient à une lignée de protecteurs magiques appelés les Gardiens. Grâce à l''aide d''un tigre mythique nommé Hu et de guerriers animaux du zodiaque, Tom s''entraîne pour affronter une force maléfique qui menace l''humanité.', 38.496, '/wW8gnJkuZNG6IOCb76tmUibPA5D.jpg', '2024-02-03', 0, '', '', 'La Légende du Tigre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 4977, '', 'ja', 'パプリカ', 'Dans le futur, un nouveau traitement psychothérapeutique nommé PT a été inventé. Grâce à une machine, le DC Mini, il est possible de rentrer dans les rêves des patients, et de les enregistrer afin de sonder les tréfonds de la pensée et de l''inconscient. Alors que le processus est toujours dans sa phase de test, l''un des prototypes du DC Mini est volé, créant un vent de panique au sein des scientifiques ayant développé cette petite révolution. Dans de mauvaises mains, une telle invention pourrait effectivement avoir des résultats dévastateurs. Le Dr. Atsuko Chiba, collègue de l''inventeur du DC Mini, le Dr. Tokita, décide, sous l''apparence de sa délurée alter-ego Paprika, de s''aventurer dans le monde des rêves pour découvrir qui s''est emparé du DC Mini et pour quelle raison. Elle découvre que l''assistant du Dr. Tokita, Himuro, a disparu…', 29.324, '/kjOuNoHNy8IF6v6BHzfEbli1G3W.jpg', '2006-12-06', 0, '', '', 'Paprika');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 616747, '', 'en', 'Haunted Mansion', 'Une mère de famille et son fils de neuf ans cherchent à commencer une nouvelle vie. Ils sautent sur une occasion à ne pas laisser passer, un manoir abordable à la Nouvelle-Orléans. Ils sont loin de se douter que la maison abrite déjà des résidents inattendus. Quand ils comprennent le manoir est hanté, ils contactent un prêtre qui demande à son tour l''aide d''un expert en paranormal, d''une médium et d''un historien grincheux.', 34.106, '/bihtBp21X327UBaNaTLFQQbRWJL.jpg', '2023-07-26', 0, '', '', 'Le manoir hanté');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1217343, '', 'en', 'This Is Me…Now', 'This Is Me… Now vous invite à découvrir Jennifer Lopez comme jamais auparavant. Aux côtés du réalisateur Dave Meyers, Jennifer a créé une odyssée cinématographique narrative originale retraçant son cheminement vers l''amour vu à travers ses propres yeux.', 29.387, '/uzHDq1vBlJRpSeYI8guHtoYNNFM.jpg', '2024-02-16', 0, '', '', 'This Is Me… Now');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 792293, '', 'en', 'Dumb Money', 'L’incroyable histoire vraie d’un homme ordinaire et de ses followers qui ont ébranlé Wall Street en misant sur GameStop, une entreprise à laquelle personne ne croyait. En engageant toutes ses économies sur un pari fou, Keith Gill et ceux qui décident de le suivre, vont gagner beaucoup, beaucoup d’argent : Wall Street a ses nouveaux loups. Mais ce qui enrichit les uns appauvrit les autres, et les milliardaires des fonds d’investissement ne vont pas tarder à riposter…', 30.35, '/vfqXX7TIwUqk5pepTDhYpMsesIL.jpg', '2023-11-29', 0, '', '', 'Dumb Money');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 558915, '', 'en', 'The Color Purple', 'Un récit d''amour et de résilience qui s''étend sur plusieurs décennies, et le voyage d''une femme vers l''indépendance. Celie est confrontée à de nombreuses épreuves dans sa vie, mais trouve finalement une force et un espoir extraordinaires dans les liens indéfectibles de la sororité.', 29.042, '/fKkWAjAwNXHHtxnpufRUbgFpmxr.jpg', '2024-01-24', 0, '', '', 'La Couleur pourpre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1053592, '', 'es', 'Todos los nombres de Dios', 'Santiago, chauffeur de taxi à Madrid, conduit une cliente à l''aéroport lorsqu''une violente explosion retentit. Les victimes sont nombreuses et la panique générale sème la confusion. Protégé par sa voiture, Santiago s''en sort indemne et se porte au secours d''un jeune homme visiblement blessé par l''attaque. Il le mène jusqu''à sa voiture, mais l''individu se révèle être l''un des trois terroristes. Santiago se retrouve pris en otage dans son taxi, avec pour ordre d''escorter le terroriste.', 39.751, '/y1OIPQA0UIFzm68uCdwZF2L3W0M.jpg', '2024-03-27', 0, '', '', 'All the Names of God');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1042216, '', 'en', 'Mutt', 'Au cours d''une seule journée, trois personnes du passé de Feña, avant sa transition de femme à homme, refont surface dans sa vie : son père étranger, son ex-petit ami et sa demi-sœur de 13 ans.', 38.277, '/gWKwgwC942sUDHj0ZmOxaE58mBJ.jpg', '2023-08-09', 0, '', '', '24 heures à New York');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 365620, '', 'en', 'Ferrari', 'Au cours de l''été 1957, Enzo Ferrari, ancien pilote automobile, est en crise. La faillite guette l''entreprise que lui et sa femme, Laura, ont créée à partir de rien dix ans plus tôt. Leur mariage tumultueux doit faire face au deuil d''un fils et à la reconnaissance d''un autre. Pour tenter d''assurer leur survie, il jette les dés sur une course — 1 000 miles à travers l''Italie, l''emblématique Mille Miglia.', 34.486, '/bnfgWrQIObgN3bv4xRsHcW3C66x.jpg', '2024-03-08', 0, '', '', 'Ferrari');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 839369, '', 'en', 'May December', 'Pour préparer son nouveau rôle, une actrice célèbre vient rencontrer celle qu’elle va incarner à l’écran, dont la vie sentimentale a enflammé la presse à scandale et passionné le pays 20 ans plus tôt.', 33.702, '/rvPSbOzxlZU8gjys4jh2q5TGvCe.jpg', '2024-01-24', 0, '', '', 'May December');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 938250, '', 'fr', 'Club Zero', 'Miss Novak rejoint un lycée privé où elle initie un cours de nutrition avec un concept innovant, bousculant les habitudes alimentaires. Sans qu’elle éveille les soupçons des professeurs et des parents, certains élèves tombent sous son emprise et intègrent le cercle très fermé du mystérieux Club Zero', 31.209, '/9gXhlu2hnf1IjS73ETF0zdazmZr.jpg', '2023-09-27', 0, '', '', 'Club Zéro');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 845387, '', 'ja', 'アリスとテレスのまぼろし工場', 'Après l''explosion d''une usine, une petite ville est littéralement suspendue dans le temps. Masamune a 14 ans, et avec ses amies, il va devoir s''adapter à une réalité à l''agonie.', 31.861, '/piDXkSvBA56NTIC6D3uA1eECwtd.jpg', '2024-01-15', 0, '', '', 'maboroshi');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 943134, '', 'fr', 'Le Règne animal', 'Un mal étrange frappe l''humanité : des mutations transforment les humains en animaux. Considérés comme dangereux car incontrôlables, ces humains mi-homme mi-animal sont placés dans des centres spécialisés. C''est le cas pour la mère d''Émile. Alors que Lana est transférée dans un centre, son bateau fait naufrage et elle disparaît. François la recherche et, à son tour, Émile se transforme.', 32.448, '/1gO4SWCdMW0ptIRLQ455Ia5emC.jpg', '2023-10-04', 0, '', '', 'Le Règne animal');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 781732, '', 'hi', 'Animal', 'L''amour inconditionnel d''un fils pour son père, souvent absent, et son admiration pour sa famille devient une source de conflits...', 35.895, '/hr9rjR3J0xBBKmlJ4n3gHId9ccx.jpg', '2023-12-01', 0, '', '', 'Animal');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 315011, '', 'ja', 'シン・ゴジラ', 'Un raz de marée inonde une partie de la côte de Tokyo. Après avoir pensé qu’il s’agissait d’une catastrophe naturelle, les scientifiques se rendent compte que le responsable de ce désastre n’est autre que Godzilla, une créature géante prête à tout détruire sur son passage.', 27.107, '/8ulwqKiHxz5uH0g0I1BVeNDTWlo.jpg', '2017-12-10', 0, '', '', 'Shin Godzilla');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1019420, '', 'en', 'Irish Wish', 'Lorsque l''amour de sa vie se fiance à sa meilleure amie, Maddie met ses sentiments de côté afin d''assumer son rôle de demoiselle d''honneur à leur mariage en Irlande. Quelques jours avant la cérémonie, notre héroïne exprime le souhait spontané de trouver le grand amour… et se retrouve transportée dans une réalité alternative. Une rencontre fortuite lui montre alors qu''avant de faire un vœu, il faut réfléchir à deux fois. Alors que son rêve semble se réaliser, Maddie comprend rapidement que sa véritable âme sœur n''est pas celle qu''elle croit.', 31.882, '/b2jlRzErQu2j4tJHC8RHWNYU0Lj.jpg', '2024-03-15', 0, '', '', 'Irish Wish');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 964592, '', 'ko', '잠', 'Une femme enceinte s''inquiète des habitudes de sommeil de son mari. Ce qui commence comme une discussion légère sur le sommeil se transforme rapidement en un comportement grotesque inattendu et ils cherchent désespérément ce qui se passe.', 29.534, '/mtJodZ0dJVEVdAocvl21tbLh55k.jpg', '2024-02-21', 0, '', '', 'Sleep');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1070032, '', 'en', 'Beyond Utopia', 'Filmées en caméra embarquée, les tentatives de fuite de Coréens du Nord au péril de leur vie.  Un documentaire saisissant qui raconte les rêves de liberté d’une population soumise au joug mortifère de la dynastie Kim.', 27.989, '/4taEielR066B0uPvq1W9ZHNSPCL.jpg', '2024-03-05', 0, '', '', 'Corée du Nord : Le Prix de la liberté');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 875104, '', 'zh', '叶问宗师觉醒', 'Alors qu''Ip Man étudiait à Hong Kong pendant sa jeunesse, il est accidentellement impliqué dans une affaire d''enlèvement de femmes chinoises par la société britannique Stark Industries pour en faire un trafic à l''étranger. Ip Man s''avance et combat les occidentaux. Il combat les guerriers britanniques Patton à trois reprises et utilise le Wing Chun pour défendre la dignité des Chinois bafoués.', 30.192, '/uD114D4Di7M4sXnBiwaBHepUAZE.jpg', '2024-02-07', 0, '', '', 'Ip Man : L''Éveil du Maître');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 958196, '', 'en', 'Inside', 'Nemo, un voleur d’œuvres d’art de renom, se retrouve piégé dans un luxueux penthouse new yorkais après un cambriolage qui a mal tourné. Enfermé à l’intérieur avec des œuvres hors de prix pour seule compagnie, il va devoir faire preuve d’habilité et d’inventivité pour survivre.', 35.153, '/tgiseO8vRKFwzIw8qXX5sKIvDjZ.jpg', '2023-11-01', 0, '', '', 'À l''intérieur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 956262, '', 'en', 'The Kitchen', 'Alors qu’Izi s’apprête à enfin quitter la Kitchen, l’un des derniers grands ensembles de Londres, le jeune Benji débarque dans sa vie, l’obligeant à prendre une décision difficile.', 17.414, '/tV0hK1BiqYrYlehChv4WJXIWgmZ.jpg', '2024-01-19', 0, '', '', 'The Kitchen');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1168709, '', 'en', 'Einstein and the Bomb', 'Que s''est-il passé quand Einstein a fui l''Allemagne nazie ? Le physicien allemand Albert Einstein a 66 ans quand le 6 août 1945 une bombe atomique est larguée sur Hiroshima. Jusqu’à sa mort, dix ans plus tard, il sera rongé par la culpabilité et les remords : en 1939, il avait adressé à Franklin D. Roosevelt un courrier qui participa à convaincre les États-Unis de se lancer dans la course à l’armement nucléaire. Ce documentaire dissèque la pensée d''un génie torturé en se penchant sur ses propres mots et déclarations ainsi que sur des images d''archives.', 22.307, '/2hVW6H19YqvzXxmvr50Bqlg95hr.jpg', '2024-02-16', 0, '', '', 'Einstein et la bombe');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1115377, '', 'da', 'Englemageren', 'Enquêtrice spécialisée en cybercriminalité, Laura fait une dépression depuis la mort de son bébé. Quand une série de meurtres de jeunes femmes a lieu à Copenhague, elle retrouve la motivation de reprendre du service. Elle doit faire équipe avec l’expérimenté Jesper. Cette affaire va la mener bien plus loin qu’elle n’aurait pu l’imaginer.', 20.752, '/qool8dwcvg1utKGSCHC9mAdc9qi.jpg', '2024-02-07', 0, '', '', 'Neurosis');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 830721, '', 'en', 'The Miracle Club', 'Les femmes de la petite ville de Ballyfermot, en Irlande, espèrent toutes être les heureuses élues qui pourront effectuer un pèlerinage dans la ville sacrée de Lourdes, en France. Au cours d''une soirée de tombola pour le moins mouvementée, Lily, Eileen, Dolly et Chrissie remportent le prix tant convoité. Le groupe d''ouvrières émeutières de Dublin partent alors à Lourdes, en France. Au cours de leur périple, elles vont découvrir le sens de l''amitié et connaître chacune un miracle personnel.', 17.03, '/1683H347S0hWDctzvmzYKrl0GbJ.jpg', '2024-01-24', 0, '', '', 'Le Club des miracles');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 605542, '', 'es', 'Perdidos en la noche', 'Dans une petite ville du Mexique, Emiliano recherche les responsables de la disparition de sa mère. Activiste écologiste, elle s’opposait à l’industrie minière locale. Ne recevant aucune aide de la police ou du système judiciaire, ses recherches le mènent à la riche famille Aldama.', 19.745, '/dM4wlE4tSx9jiyUEHC2eWkJxNot.jpg', '2023-10-04', 0, '', '', 'Lost In The Night');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 801112, '', 'it', 'Rapito', 'En 1858, dans le quartier juif de Bologne, les soldats du Pape font irruption chez la famille Mortara. Sur ordre du cardinal, ils sont venus prendre Edgardo, leur fils de sept ans. L’enfant aurait été baptisé en secret par sa nourrice étant bébé et la loi pontificale est indiscutable : il doit recevoir une éducation catholique.', 17.879, '/9gTXkah44bO0qO6FDmNAGrdwoLB.jpg', '2023-11-01', 0, '', '', 'L''Enlèvement');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 986280, '', 'fi', 'Kuolleet lehdet', 'Deux personnes solitaires se rencontrent par hasard une nuit à Helsinki et chacun tente de trouver en l’autre son premier, unique et dernier amour. Mais la vie a tendance à mettre des obstacles sur la route de ceux qui cherchent le bonheur.', 16.367, '/9qSp78PV8gcGKfHqvMp9FAV4xcl.jpg', '2023-09-20', 0, '', '', 'Les Feuilles mortes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1024773, '', 'en', 'It Lives Inside', 'Sam, une adolescente indo-américaine, vit dans une banlieue idyllique et lutte avec son identité culturelle et les opinions conservatrices de ses parents. Lors d''une dispute avec son amie Tamira, elle brise un mystérieux pot de conserves que son amie porte toujours avec elle. Ce faisant, elle libère sans le savoir une ancienne force démoniaque indienne qui kidnappe Tamira. Sam doit faire tout son possible pour mettre fin à cette horreur.', 27.35, '/qmsrKo3UXcm76cEN39SNxhsdYUM.jpg', '2023-09-06', 0, '', '', 'Inside');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1059345, '', 'en', 'Cult Killer', 'Un détective privé et sa partenaire sont entrainés dans un dédale d’intrigues lorsqu’ils traquent un tueur en série qui cible une famille fortunée avec un lourd secret.', 23.227, '/9ingNkWkiTA8YNQgYZvkkvaJ5Ot.jpg', '2024-03-20', 0, '', '', 'Cult Killer');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 734002, '', 'pl', 'Chłopi', 'Jagna incarne une jeune femme résolue à prendre son destin en main dans un village polonais de la fin du 19e siècle, un terreau fertile de rumeurs, de vieilles querelles, de solidarité, de richesse et de pauvreté, de fierté nationaliste et de respect des traditions et de l''autorité patriarcale enracinée. Piégée dans une situation amoureuse conflictuelle avec le fermier le plus fortuné du village, son fils ainé et d''autres notables, Jagna opte pour sa liberté, ce qui lui attire de graves ennuis avec les membres de sa communauté.', 25.229, '/4l1m4vHlgWimjyd8GeepPewXN2q.jpg', '2024-03-20', 0, '', '', 'La Jeune fille et les paysans');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 982202, '', 'de', 'Stella. Ein Leben.', 'Berlin, durant la Seconde Guerre mondiale. Inquiète quant au sort réservé à ses parents, arrêtés après avoir été dénoncés, mais surtout déterminée à sauver sa propre vie, Stella, Juive allemande, accepte de collaborer avec la Gestapo. Elle se met alors à dénoncer systématiquement d''autres Juifs...', 30.092, '/gDk9X9tkkzmdpVnKrQV58FkXlo1.jpg', '2024-01-17', 0, '', '', 'Stella, une vie allemande');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1056360, '', 'en', 'American Fiction', 'Monk est un romancier frustré qui en a assez de l''establishment qui profite du divertissement noir qui s''appuie sur des tropes fatigués et offensants. Pour prouver son point de vue, il utilise un pseudonyme pour écrire son propre livre noir extravagant, un livre qui le propulse au cœur de l''hypocrisie et de la folie qu''il prétend dédaigner.', 29.514, '/eTyxDvtOpmTWy7rlXXCAhLsXhEP.jpg', '2024-02-26', 0, '', '', 'Fiction à l''américaine');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1213997, '', 'en', 'Bitconned', 'Dans ce documentaire inspiré de crimes réels, trois hommes exploitent le marché débridé de la cryptomonnaie pour escroquer des investisseurs et se payer une vie de luxe.', 32.061, '/hAUzDAgJudwflc9xukm2iXdVenX.jpg', '2024-01-01', 0, '', '', 'Les Escrocs de la crypto');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1039882, '', 'ko', '로기완', 'Après avoir quitté la Corée du Nord, Loh Kiwan peine à obtenir le statut de réfugié en Belgique, où il rencontre une femme qui a perdu tout espoir.', 23.489, '/cpXAOAKrxIcf3sIMfL1WI6rduUJ.jpg', '2024-03-01', 0, '', '', 'Je m''appelle Loh Kiwan');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1036619, '', 'en', 'Good Grief', 'Dévasté par la disparition de son mari écrivain, un artiste emmène ses deux meilleurs amis à Paris, où vont se dévoiler de vilains secrets et de dures réalités.', 15.364, '/wJFHUglmh7xlY7g9Ql85QAJBZ6n.jpg', '2024-01-05', 0, '', '', 'L''Effet veuf');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 808396, '', 'en', 'God Is a Bullet', 'Lorsqu’un détective retrouve son ex femme assassinée et sa fille kidnappée par une secte satanique, il quitte la police et infiltre l’organisation afin d’y traquer son charismatique gourou.', 29.618, '/lWu7s7PXKbbbyNg5jTZklbXwavf.jpg', '2024-01-27', 0, '', '', 'Le prix de la vengeance');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 977223, '', 'en', 'Polite Society', 'Une artiste martiale en formation pense qu''elle doit sauver sa soeur aînée de son mariage imminent. Après avoir demandé l''aide de ses amis, elle tente de réaliser le plus ambitieux de tous les braquages de mariage au nom de l''indépendance et de la fraternité.', 35.13, '/qH2SiNS7MJzVhxqJ2mDfd6Xy6zF.jpg', '2024-01-01', 0, '', '', 'Polite Society');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1082063, '', 'pt', 'A Semente do Mal', 'Orphelin depuis sa naissance, Edward découvre à l’âge adulte qu’il a un jumeau et une mère qu’il ne connait pas. Avec sa petite amie Ryley, il part les rencontrer dans leur magnifique demeure isolée au cœur d’une région recluse. Les retrouvailles passées, le jeune couple se rend compte que les apparences sont trompeuses : la famille d’Edward cache un monstrueux secret. Leur visite va tourner au cauchemar...', 20.321, '/kFFvKDu6H5FjyYEMlgrGVnTKkCg.jpg', '2024-01-31', 0, '', '', 'Amelia''s Children');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 889675, '', 'en', 'The Pod Generation', 'Vivant dans un futur pas si lointain, un couple de New York se lance dans une aventure folle vers la parentalité après avoir obtenu une place convoitée au Centre de la Matrice, qui offre aux couples une grossesse pratique et partageable grâce à des utérus artificiels détachables, ou des capsules.', 25.311, '/iCIa3RXsJOMsfb07jR90tEKFCww.jpg', '2023-10-25', 0, '', '', 'The Pod Generation');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 763144, '', 'en', 'The Last Rifleman', 'Artie Crawford un vétéran de la Seconde Guerre mondiale qui, à 92 ans ¾, entend parler du 75e anniversaire du Débarquement en Normandie. Bien décidé à participer à ces commémorations et plein d''audace, Artie s''échappe de sa maison de retraite et se lance dans un road-trip mémorable pour aller en France rendre un dernier hommage à son ancien régiment.', 21.869, '/47Qm5qPSq774Fva3TgmjqKrvtQj.jpg', '2024-03-23', 0, '', '', 'The Last Rifleman');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 11167, '', 'en', 'Peeping Tom', 'Mark Lewis est un solitaire, passionné d''image jusqu''à l''obsession. Opérateur sur un plateau de tournage, il fait aussi des extras comme photographe de charme au-dessus d''un marchand de journaux qui vend ses compositions sous le manteau. Son appartement comprend un immense laboratoire rempli de matériels, appareils, chimie. Là, il développe et visionne seul, ses propres films. La caméra toujours à portée de main, Mark Lewis dit tourner un documentaire mais il s''emploie à une démarche plus insolite et surtout beaucoup plus morbide....', 22.554, '/pGZygGMFN7dwXzTxFRM6MCPr8E7.jpg', '1960-09-21', 0, '', '', 'Le Voyeur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 972433, '', 'en', 'Race for Glory: Audi vs Lancia', '1983, la rivalité est à son paroxysme, entre l’écurie italienne Lancia, dirigée par le charismatique Cesare Fiorio et la puissante équipe allemande Audi, dirigée par le redoutable Roland Gumpert. Mais, c’est sur le terrain, pilotées respectivement par Walter Röhrl et Hannu Mikkola, que leurs voitures : la Lancia Rally 037 et l''Audi Quattro, les départageront durant un championnat du monde des rallyes devenu légendaire.', 20.832, '/utADmCQJNyLYQ5pLDa94TMIfzKX.jpg', '2024-02-07', 0, '', '', 'Race for Glory : Audi vs Lancia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1090874, '', 'en', 'Mea Culpa', 'Quand l''avocate pénaliste Mea Harper  se charge de l''affaire de meurtre dans laquelle est impliqué le séduisant peintre Zyair Malloy, la vérité n''est pas aussi évidente qu''il y paraît. Alors qu''elle tente de découvrir si son client, qui reste sur ses gardes, est innocent ou pas, il apparaît indéniable que tout le monde est coupable de quelque chose. Le "Mea Culpa" de Tyler Perry explore ce qu''il arrive quand un désir ardent teinte de danger une relation professionnelle.', 21.097, '/roxD9VCTTXYfPjVj9HiSC5IMtc7.jpg', '2024-02-23', 0, '', '', 'Mea Culpa');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1110390, '', 'hi', 'तेरी बातों में ऐसा उलझा जिया', 'Aryan n''arrive pas à trouver la partenaire idéale. Il rencontre une fille parfaite, Sifra, lors d''une mission aux États-Unis et tombe amoureux d''elle, avant de découvrir plus tard qu''il s''agit d''une histoire d''amour impossible.', 13.626, '/2ISTMBg7HBuqAqNyUpqoWQk28sP.jpg', '2024-02-09', 0, '', '', 'Je suis tellement empêtré dans vos mots');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 760245, '', 'en', 'Foe', 'Hen et Junior voient leur vie bouleversée lorsqu’un inconnu leur fait une offre inattendue. Seront-ils prêts à mettre leur couple et leur identité en jeu pour survivre dans un monde métamorphosé ? À travers des images fascinantes et de profondes questions sur la nature humaine (et artificielle), Le Remplaçant donne vie à un avenir moins lointain qu’on ne l''imagine.', 23.205, '/yckvpQrfqYE502QArj0rWv6daGY.jpg', '2024-01-05', 0, '', '', 'Le Remplaçant');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 2069, '', 'en', 'The Whole Nine Yards', 'Nicholas "Oz" Oseransky est un paisible dentiste de Montréal, dont la vie a tourné au cauchemar. Endetté, coincé entre une épouse acariâtre, Sophie, et une belle-mère guère plus avenante, il est prêt à tout pour recouvrer la liberté. Oz manque de s’évanouir en découvrant que son nouveau voisin n''est autre que Jimmy Tudeski, dit Jimmy la Tulipe, l''ancien exécuteur du gang Gogolack, libéré pour avoir balancé son chef à la police. Sophie oblige son mari à aller dénoncer Tudeski à Janni Gogolack. Celui-ci tient en otage la femme du tueur, dont Oz tombe amoureux.', 19.627, '/cggSlxLUnFPGJN5Vh0dZ3rAhcqs.jpg', '2000-05-10', 0, '', '', 'Mon voisin le tueur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1069945, '', 'ml', 'മഞ്ഞുമ്മല്‍ BOYS', 'À Kodaikanal, dans le Tamil Nadu, un groupe d''amis se lance dans une mission de sauvetage audacieuse pour sauver leur ami des grottes de Guna, une fosse dangereusement profonde d''où personne n''a jamais été ramené.', 20.949, '/32PPRYtQQg6LFSXovlKWdpYNOP1.jpg', '2024-02-28', 0, '', '', 'Manjummel Boys');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 676727, '', 'en', 'The Inventor', 'Bienvenue dans la Renaissance ! Une époque où artistes, savants, rois et reines inventent un monde nouveau. Parmi eux, un curieux personnage passe ses journées à dessiner d’étranges machines et à explorer les idées les plus folles. Observer la lune, voler comme un oiseau, découvrir les secrets de la médecine… il rêve de changer le monde. Embarquez pour un voyage avec le plus grand des génies, Léonard de Vinci !', 21.852, '/8EmJTUuZM7B3nPrcg1Ndwq45MLB.jpg', '2024-01-31', 0, '', '', 'Léo, la fabuleuse histoire de Léonard de Vinci');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1014209, '', 'en', 'Suncoast', 'Une adolescente accompagne sa mère afin de placer son frère dans un établissement spécialisé. Là, elle se lie d''amitié avec un activiste excentrique.', 24.288, '/nmPLUp5lVMiGomkN3db1WPeNrz3.jpg', '2024-02-09', 0, '', '', 'Suncoast');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1077600, '', 'ja', 'ベイビーわるきゅーれ2ベイビー', 'Chisato et Mahiro ont été interdites de travail parce qu''elles avaient enfreint les règles de l''organisation. Ayant besoin d''argent pour joindre les deux bouts, elles ont repris leur travail à temps partiel. Deux autres assassins se mettent à leurs trousses.', 17.755, '/hloIo1hC6QanRintrGUuNtRP9FU.jpg', '2024-02-08', 0, '', '', 'Baby Assassins 2');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 40394, '', 'it', 'Salon Kitty', 'Quelques mois après le début de la Seconde Guerre mondiale, le Troisième Reich reprend en mains le plus luxueux des bordels berlinois, le Salon Kitty, dont la vocation n''est plus uniquement de pourvoir aux fantasmes de sa clientèle, mais de l''espionner, de lui soutirer des confidences. Surtout lorsqu''il s''agit de dignitaires étrangers, d''hommes d''affaires et d''officiers allemands. Chargé, dans l''ombre, de diriger l''établissement, Wallenberg, un nazi arriviste, sélectionne les filles les plus belles et les plus fanatiques. À la fois prostituées et espionnes, elles remplissent parfaitement leur mission. Du moins jusqu''au jour où l''une d''entre-elles, Margharita, tombe amoureuse d''un Capitaine de la Wehrmacht hostile à Hitler.', 18.168, '/nij3AphWbtD8HJ4mGUrNkyccgIB.jpg', '1976-06-23', 0, '', '', 'Madame Kitty');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 859235, '', 'fr', 'Coup de chance', 'Fanny et Jean ont tout du couple idéal : épanouis dans leur vie professionnelle, ils habitent un magnifique appartement dans les beaux quartiers de Paris et semblent amoureux comme au premier jour. Mais lorsque Fanny croise, par hasard, Alain, ancien camarade de lycée, elle est aussitôt chavirée. Ils se revoient très vite et se rapprochent de plus en plus...', 15.7, '/lFNg2bbr3DrRpkokuiQDMcINLXk.jpg', '2023-09-27', 0, '', '', 'Coup de chance');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 798612, '', 'en', 'Shirley', 'En 1972, Shirley Chisholm, première femme noire élue au Congrès, entrait dans l''Histoire en se présentant à la candidature démocrate pour la présidence des États-Unis.', 18.957, '/ooo4D4kyZyFee5aiNfwZ43oKOkc.jpg', '2024-03-22', 0, '', '', 'Shirley');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 843617, '', 'en', 'Players', 'Une journaliste sportive new-yorkaise maîtresse dans l''art de la séduction craque inopinément sur sa dernière conquête. De coup d''un soir à coup de foudre, il n''y a qu''un pas...', 19.682, '/yJJlxsbQohglWP1YZsKYD0J2w3p.jpg', '2024-02-14', 0, '', '', 'Prise au jeu');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 998022, '', 'de', 'Das Lehrerzimmer', 'Alors qu''une série de vols a lieu en salle des profs, Carla Nowak mène l''enquête dans le collège où elle enseigne. Très vite, tout l''établissement est ébranlé par ses découvertes.', 19.095, '/bUQVqD5CXY3w7rZIKXf5yrsHu8f.jpg', '2024-03-06', 0, '', '', 'La Salle des profs');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 960033, '', 'fr', 'Le Théorème de Marguerite', 'L''avenir de Marguerite, brillante élève en Mathématiques à l''ENS, semble tout tracé. Seule fille de sa promo, elle termine une thèse qu’elle doit exposer devant un parterre de chercheurs. Le jour J, une erreur bouscule toutes ses certitudes et l’édifice s’effondre. Marguerite décide de tout quitter pour tout recommencer.', 21.346, '/rLomqmtgZq0XgyY4rUkxxJbM5M0.jpg', '2023-11-01', 0, '', '', 'Le Théorème de Marguerite');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 977331, '', 'fr', 'Chien et Chat', 'Monica, maîtresse de la chatte star d''internet Diva, croise à l''aéroport Jack, un voleur, accompagné du chien qui vient d''avaler le butin de son dernier casse : un rubis. Les humains sont forcés par la compagnie aérienne de mettre leurs précieux animaux en soute. Alors qu''ils sont dans l''avion ils voient par le hublot leurs animaux s''échapper de leur cage et du chariot à bagages vers une destination inconnue. Les deux duos que tout oppose s''embarquent dans un road trip contre la montre entre Montréal et New York. D''un côté, les humains qui ont perdu la trace de leurs animaux et de l''autre, les animaux livrés à eux-mêmes pour retrouver leur maître… sans savoir qu''à leur trousse, le policier Brandt est prêt à tout pour récupérer le rubis.', 17.314, '/wGzqk3XXpAQGlpskKP1HqRpFaPK.jpg', '2024-02-14', 0, '', '', 'Chien et Chat');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 798362, '', 'en', 'The Marsh King''s Daughter', 'La vie idyllique d’Helena Petterier est mise à mal lorsqu’elle apprend que l’homme qui l’a élevée et gardée en otage pendant 12 ans dans un marécage s’est évadé de prison. Pour protéger son mari et sa fille, Helena va devoir affronter son sombre passé qu’elle a toujours gardé secret et traquer son père, le tristement célèbre Roi des Marais.', 20.831, '/xz9hdLy75lgyH7r8VlnIoERUOCV.jpg', '2024-01-12', 0, '', '', 'La fille du roi des marais');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 823395, '', 'en', 'The Baker', 'Le fils d''un homme solitaire revient soudainement dans sa vie, emmenant avec lui une petite-fille dont il n''avait jamais entendu parler. Lorsque son fils disparaît subitement, le boulanger tente de le retrouver et de protéger sa petite-fille des tueurs à gages mafieux.', 27.237, '/ijzMEf8ZhGiz0f1mb2zeuTVjXj2.jpg', '2024-01-08', 0, '', '', 'The Baker');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1058616, '', 'en', '20 Days in Mariupol', 'Février 2022. Une équipe de journalistes ukrainiens de l’Associated Press (AP), piégée dans la ville assiégée de Marioupol, filme les atrocités de l''invasion russe. Seuls reporters internationaux sur place, ils capturent ce qui deviendra plus tard des images marquantes de la guerre.  Au plus près des civils, le réalisateur Mstyslav Chernov et son équipe, récompensés du prix Pulitzer, livrent un témoignage capital sur la réalité de la guerre qui déchire leur pays. Ils offrent un récit puissant et déchirant des civils pris dans le piège, ainsi qu''une réflexion sur la responsabilité d''un reporter en zone de conflit et sur l''impact d''un tel journalisme à l''échelle mondiale.', 18.798, '/qPoFLADbtvI9fjHnu275FadALWp.jpg', '2024-02-21', 0, '', '', '20 jours à Marioupol');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1015936, '', 'sv', 'Feed', 'Un groupe d’experts en médias sociaux est embauché pour aider une ancienne entreprise familiale à s’épanouir. Mais ils se retrouvent bientôt coincés sur une petite île dans un lac dans lequel vit une ancienne sorcière suédoise.', 20.424, '/4U7lZe2BcuWgUKSJih97XFOWjr5.jpg', '2024-03-05', 0, '', '', 'Feed');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 975056, '', 'en', 'Cabrini', 'Quand soeur Francesca Cabrini débarque à New York en 1889, elle ne possède rien, tout comme des milliers d''immigrants italiens. Avec l''aide de quelques soeurs, malgré sa santé fragile et son isolement dans une société patriarcale, elle va se lancer dans une aventure sans précédent, construisant un orphelinat, puis un hôpital et progressivement un véritable empire de l''espoir.', 13.221, '/t9bdz0PD7z5DfOLYEmvKPDc5K6x.jpg', '2024-03-20', 0, '', '', 'Cabrini');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1189198, '', 'en', 'Genie', 'Flora, un génie enfermé depuis plus de 2 000 ans dans une boîte à bijoux antique, est accidentellement appelée au service de Bernard, dont la vie s''effiloche autour de lui.', 15.74, '/ykkVuQgvjrVWiEe7TvUQZOrLUgC.jpg', '2024-02-21', 0, '', '', 'Genie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1232943, '', 'pl', 'Zabij mnie, kochanie', 'Pour le meilleur et pour le pire... Après avoir gagné à la loterie, un couple marié se tourne vers le pire lorsqu''il décide de s''entretuer pour récupérer l''intégralité du pactole.', 18.314, '/18opJfmMqbGBZi7BGp4Y6pYXdo8.jpg', '2024-02-13', 0, '', '', 'Tue-moi si tu l''oses');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1613, '', 'en', 'The 51st State', 'Elmo McElroy est vraiment un individu à part. Chimiste génial, il a mis au point le POS 51, LA drogue parfaite, celle qui vous donne l''impression que Dieu lui-même est venu vous faire signe. Bien décidé à commercialiser son petit chef-d''oeuvre sans intermédiaire, Elmo se débarasse de son encombrant patron, Le Lézard, pour rallier l''Angleterre - le 51e Etat - et s''associe avec Félix Desouza, un petit nerveux qui règne sur l''underground de Liverpool, parle tout le temps et ne supporte pas ce qui est Américain.Ce qu''ignore Elmo c''est que son ex-patron est toujours vivant, déterminé à se venger et à mettre la main sur la formule chimique. Et en plus il n''est pas le seul : la superbe et dangereuse Dakota Phillips est également sur ses traces.', 14.61, '/4z4J8WMCsfxSPsHkJXb0BU6EMYG.jpg', '2002-07-10', 0, '', '', 'Le 51e état');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 976584, '', 'en', 'The Book of Clarence', 'Clarence, un habitant de Jérusalem, décide de tirer profit de l''influence grandissante du Messie pour son profit personnel.', 22.931, '/gZdT3j8JvCBiAxk3QySHQxOpSnm.jpg', '2024-03-20', 0, '', '', 'Le livre de Clarence');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 985617, '', 'en', 'Reality', 'Augusta, Géorgie, États-Unis, 3 juin 2017. Après avoir fait quelques courses, Reality Winner rentre chez elle, où elle est abordée par deux hommes.', 17.35, '/dkUzu4iH7x9JTEmfUInmpwSIYTk.jpg', '2023-08-16', 0, '', '', 'Reality');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 3763, '', 'ja', '鬼婆', 'Une vieille femme et sa bru survivent en assassinant et depouillant les soldats qui s''aventurent dans les marais environnants.', 16.803, '/812SdJrROZpY7QD1TxcFgnZAQbC.jpg', '1966-01-26', 0, '', '', 'Onibaba');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1226841, '', 'en', 'The Greatest Night in Pop', 'Un soir de janvier 1985, les plus grands noms de la musique se sont réunis pour enregistrer "We Are the World". Ce documentaire dévoile les coulisses de cette collaboration historique.', 19.612, '/jDz4wFujk94tQP9Sx6EMKDULvkn.jpg', '2024-01-29', 0, '', '', 'The Greatest Night in Pop');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1112527, '', 'ar', 'كذب أبيض', 'Casablanca. La jeune cinéaste Asmae El Moudir cherche à démêler les mensonges qui se transmettent dans sa famille. Grâce à une maquette du quartier de son enfance et à des figurines de chacun de ses proches, elle rejoue sa propre histoire. C''est alors que les blessures de tout un peuple émergent et que l’Histoire oubliée du Maroc se révèle.', 15.08, '/b520SVAH15ygolpvxiQKA1w5PxH.jpg', '2024-02-28', 0, '', '', 'La mère de tous les mensonges');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1113278, '', 'en', 'It''s a Wonderful Knife', 'Un an après avoir sauvé sa ville d''un tueur psychotique la veille de Noël, la vie de Winnie Carruthers est loin d''être merveilleuse. Mais alors qu’elle souhaite ne jamais être née, elle se retrouve dans un univers parallèle cauchemardesque et découvre que les choses pourraient être bien pires sans elle.', 16.271, '/qToCVGHgu1670jZKjbdUnMVKmPc.jpg', '2024-01-27', 0, '', '', 'It''s a Wonderful Knife');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 667717, '', 'zh', '深海', 'Shenxia, une fillette de 10 ans, est aspirée dans les profondeurs marines durant une croisière familiale. Elle découvre l’univers fantastique des abysses, un monde inconnu peuplé d’incroyables créatures. Dans ce lieu mystérieux émerge le Restaurant des abysses, dirigé par l’emblématique Capitaine Nanhe. Poursuivis par le Fantôme Rouge, leur route sera semée d’épreuves et de nombreux secrets. Leur odyssée sous-marine ne fait que commencer.', 21.426, '/ujCbELjZkdlS2h3KqSmtobnjyyM.jpg', '2024-02-21', 0, '', '', 'Le royaume des abysses');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 891933, '', 'en', 'Sometimes I Think About Dying', 'Fran est employée de bureau dans une petite entreprise portuaire de l’Oregon. D’une timidité maladive, cette célibataire mène une existence millimétrée, dénuée de toute fantaisie – exception faite des étranges rêveries auxquelles elle s’abandonne. Mais les choses changent le jour où Robert, nouvelle recrue fantasque et sympathique, fait mine de s’intéresser à elle…', 24.79, '/s98uJVIBPJxS27lM4bA24aImNBZ.jpg', '2024-01-10', 0, '', '', 'La Vie rêvée de Miss Fran');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1220441, '', 'ja', 'GREAT PRETENDER razbliuto', 'Depuis son exécution ratée, Dorothy est amnésique et vit désormais dans un village à Taïwan sous une autre identité. Son quotidien sera bouleversé lorsqu’elle fera la rencontre de Jei, un mafieux issu d’une organisation qu’elle connaissait bien…', 22.314, '/wkalOqdReuGR71Og0kSn96vg78k.jpg', '2024-02-23', 0, '', '', 'Great Pretender: Razbliuto');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1072269, '', 'fr', 'Le Successeur', 'Heureux et accompli, Ellias devient le nouveau directeur artistique d’une célèbre maison de Haute Couture française. Quand il apprend que son père, qu’il ne voit plus depuis de nombreuses années, vient de mourir d’une crise cardiaque, Ellias se rend au Québec pour régler la succession. Le jeune créateur va découvrir qu’il a hérité de bien pire que du cœur fragile de son père.', 14.407, '/7Tu8EBsjLGEdzXeCD9HbhhVKXHK.jpg', '2024-02-21', 0, '', '', 'Le Successeur');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1010639, '', 'en', 'The Sweet East', 'Lilian, jeune lycéenne, fugue durant un voyage sco­laire. Au fil de ses ren­contres, elle découvre un monde insoup­çon­né. Les frac­tures men­tales, sociales et poli­tiques des États-Unis, fil­mées comme un conte de fée ou une varia­tion d’Alice au pays des merveilles.', 14.758, '/d4mfTKz1nd88z9TeXkHoK6NT4ZF.jpg', '2024-03-13', 0, '', '', 'The Sweet East');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 964426, '', 'no', 'Gulltransporten', 'Le 9 avril 1940, l''invasion nazie de la Norvège débute. Les troupes allemandes envahissent Oslo avec une mission critique : capturer le roi, le gouvernement, et s''emparer des réserves d''or nationales. Confronté à l’urgence de la situation, le ministre des Finances se tourne vers un homme de confiance, Frederik Haslund, un simple secrétaire parlementaire, et lui confie une mission périlleuse : former une équipe et déplacer par tous les moyens 50 tonnes d''or à travers le pays afin de rejoindre un convoi maritime allié.', 11.091, '/8oujrO7sp8c2ZX6O9j459RIn2OQ.jpg', '2024-01-27', 0, '', '', 'Gold Run : Le convoi de l''impossible');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1004348, '', 'fr', 'Boléro', 'En 1928, alors que Paris vit au rythme des années folles, la danseuse Ida Rubinstein commande à Maurice Ravel la musique de son prochain ballet. Tétanisé et en panne d’inspiration, le compositeur feuillette les pages de sa vie - les échecs de ses débuts, la fracture de la Grande Guerre, l’amour impossible qu’il éprouve pour sa muse Misia Sert... Ravel va alors plonger au plus profond de lui-même pour créer son oeuvre universelle, le Bolero.', 14.529, '/8H8kS9Q7unESbSrwCdTDgXVWRq7.jpg', '2024-03-06', 0, '', '', 'Boléro');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 995808, '', 'fr', 'L''Étoile filante', 'Boris est un barman qui vit dans la clandestinité après son implication dans un attentat. Son passé refait surface quand une victime le retrouve pour se venger. La rencontre avec le dépressif et solitaire Dom, son sosie, est le moyen parfait pour échapper à la vengeance. Mais Boris ignore l’existence de Fiona, détective privée, qui enquête sur la soudaine disparition de son ex-mari Dom.', 14.135, '/1QkL1RP6uCudLbaX6KUdcxRpdfx.jpg', '2024-01-31', 0, '', '', 'L''Étoile filante');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1210973, '', 'vi', 'Mai', 'Mai se lie d''amitié avec un homme mais son passé la rattrape...', 14.946, '/n764Alj5Uf1uMtnEpN3OkVyLob5.jpg', '2024-03-22', 0, '', '', 'Mai');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 912480, '', 'fr', 'Les Chambres rouges', 'Kelly-Anne se réveille chaque matin aux portes du palais de justice pour s’assurer une place au procès hyper-médiatisé de Ludovic Chevalier, un tueur en série duquel elle est obsédée. Au fil des jours, la jeune femme tissera des liens avec une autre groupie, ce qui l’extirpera momentanément de sa solitude étouffante. Mais à force de côtoyer les parents des victimes dans un procès qui s’enlise, Kelly-Anne a de plus en plus de difficulté à maintenir son équilibre psychologique et à assumer sa fixation maladive pour le bourreau. Elle tentera alors par tous les moyens de mettre la main sur l’ultime pièce du puzzle : la vidéo manquante du meurtre d’une des victimes avec qui elle a une inquiétante ressemblance.', 13.084, '/iXk5ZR5yFUtEfcuIIVxxesr9Wjc.jpg', '2024-01-17', 0, '', '', 'Les Chambres rouges');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 756681, '', 'es', 'Veneciafrenia', 'Lorsqu''un groupe de jeunes célibataires espagnols décident d''aller faire la fête à Venise, ils tombent sur des Vénitiens qui détestent les touristes et vont menacer leurs vies.', 17.779, '/iTYRLhkvoJewvyPSM3gsqs0eHJ9.jpg', '2024-03-04', 0, '', '', 'Veneciafrenia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1001500, '', 'en', 'The Starling Girl', 'Jem Starling, 17 ans, cherche sa place dans la communauté de fondamentalistes chrétiens. Tout est bousculé par le retour au sein d''église du séduisant pasteur Owen, responsable de la jeunesse.', 21.395, '/ysgnrkXwfFCxaBwviG2yNvzpfw5.jpg', '2024-01-12', 0, '', '', 'The Starling Girl');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 982271, '', 'en', 'Book Club: The Next Chapter', 'Quatre femmes emmènent leur club de lecture en Italie pour le voyage amusant entre filles qu''elles n''ont jamais eu. Lorsque les choses déraillent et que des secrets sont révélés, leurs vacances relaxantes se transforment en une aventure unique dans une vie à travers le pays.', 22.275, '/jXyKcZVGvEdARF9XP0FLRxBhZjE.jpg', '2023-08-30', 0, '', '', 'Le Book Club 2 : Un nouveau chapitre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 956964, '', 'fr', 'Le Consentement', 'Paris, 1985. Vanessa a treize ans lorsqu''elle rencontre Gabriel Matzneff, écrivain quinquagénaire de renom. La jeune adolescente devient la maîtresse et la muse de cet homme célébré par le monde culturel et politique. Se perdant dans la relation, elle subit de plus en plus violemment l’emprise destructrice que ce prédateur exerce sur elle.', 22.817, '/8QTo3OgSqS09jFYPwQWXyx0fi2W.jpg', '2023-10-11', 0, '', '', 'Le Consentement');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1020696, '', 'en', 'Play Dead', 'Chloe, étudiante en criminologie, simule sa propre mort pour s''introduire dans une morgue, afin de récupérer une preuve qui lie son jeune frère à un crime qui a mal tourné. Une fois à l''intérieur, elle découvre qu''un coroner sadique utilise les cadavres pour son entreprise perverse et tordue. Lorsqu''il se rend compte que Chloe a encore un pouls, un terrifiant jeu du chat et de la souris s''engage.', 22.187, '/d4KP3PAx58YX66XcOTy4VSnIUdz.jpg', '2023-05-24', 0, '', '', 'Play Dead');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 844075, '', 'zh', '新神榜：杨戬', 'Autrefois considéré comme un dieu puissant, Yang Jian a été réduit à la condition d’un tueur à gages déchu. Sa vie bascule le jour où une femme énigmatique lui propose une mission qui le force à reprendre du service. Il doit à nouveau faire face à son passé pour combattre ses anciens démons. La lutte entre Yang Jian et les créatures mystiques sera sans merci, au risque de perturber l’équilibre de leurs mondes. Une course contre la montre pour sauver le destin de leurs univers.', 18.132, '/tkZHCuPIwjDBApkQ0Ex6hm5qzJi.jpg', '2023-08-23', 0, '', '', 'La guerre des dieux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 988402, '', 'fr', 'Vampire humaniste cherche suicidaire consentant', 'Sasha est une jeune vampire avec un grave problème : elle est trop humaniste pour mordre ! Lorsque ses parents, exaspérés, décident de lui couper les vivres, sa survie est menacée. Heureusement pour elle, Sasha fait la rencontre de Paul, un adolescent solitaire aux comportements suicidaires qui consent à lui offrir sa vie. Ce qui devait être un échange de bons procédés se transforme alors en épopée nocturne durant laquelle les deux nouveaux amis chercheront à réaliser les dernières volontés de Paul avant le lever du soleil.', 15.94, '/dbasJMS6qn8OdEQR0E2ZLz4pkLg.jpg', '2024-03-20', 0, '', '', 'Vampire humaniste cherche suicidaire consentant');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1230085, '', 'tr', 'Kül', 'Gökçe retrouve un peu de l''excitation que sa vie a perdue au fil du temps en lisant Les Cendres, un livre soumis à la maison d''édition de son mari Kenan. Cette histoire mystérieuse qu''elle commence à suivre ravive des émotions profondément enfouies, mais fait aussi voler en éclats l''image de la famille heureuse qu''elle avait construite avec Kenan.', 14.506, '/p3BUHNNMmklNRD3g3SfNOD1IFCS.jpg', '2024-02-09', 0, '', '', 'Les Cendres');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 970284, '', 'en', 'Shooting Stars', 'Basé sur le livre du quadruple champion de la NBA LeBron James et de Buzz Bissinger, auteur lauréat du prix Pulitzer de "Friday Night Lights", "Shooting Stars" raconte l''histoire inspirante de l''icône mondiale du basket-ball LeBron James.', 21.956, '/7bilLkdepcqHlEgASwpKcidDHDb.jpg', '2024-01-02', 0, '', '', 'Shooting Stars');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 788734, '', 'no', 'Syk pike', 'Signe vit dans l’ombre de son petit ami Thomas, à qui tout réussit. En manque d’attention, elle décide de faire croire à son entourage qu’elle est atteinte d’une maladie rare. Mais le mensonge fonctionne un peu trop bien, et elle est vite prise à son propre piège.', 21.875, '/iKAFkBmM4yWDSIWxhjClGZkyNhN.jpg', '2023-05-31', 0, '', '', 'Sick of Myself');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 507250, '', 'en', 'Dead Shot', 'Michael, un paramilitaire irlandais à la retraite, est témoin de l''assassinat de sa femme enceinte par le sergent britannique Tempest. Blessé et présumé mort, il s''échappe et se venge dans le Londres des années 1970. Brut et plein de suspense, le film est un thriller alimenté par l''adrénaline qui laissera le public peser le véritable coût de la vengeance.', 21.791, '/oLMzL9cpBBnkMGF8j4Z5Dbi2Z5u.jpg', '2024-02-10', 0, '', '', 'Dead Shot');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 792614, '', 'fr', 'Complètement Cramé !', 'Depuis qu’il a perdu sa femme, Andrew Blake n’a plus le cœur à rien. Un ultime élan le pousse à quitter Londres pour retourner en France, dans la propriété où il l’avait rencontrée. Ce voyage vers le souvenir des jours heureux ne va pas du tout se passer comme prévu…Pour rester au domaine de Beauvillier, Blake se retrouve condamné à jouer les majordomes à l’essai. Entre Mme Beauvillier, la maîtresse des lieux au comportement aussi étrange que ses relations, Odile, la cuisinière au caractère bien trempé, Philippe, l’intendant un peu frappé qui vit en ermite au fond du parc, et Manon, la jeune femme de ménage dont le destin bascule, Blake découvre des gens aussi perdus que lui. Face à eux, dans cet endroit à part, cet homme qui n’attendait plus rien de la vie va être obligé de tout recommencer…', 11.749, '/40jJVTGEgHM5gXuCFsUuZYz5JFj.jpg', '2023-11-01', 0, '', '', 'Complètement Cramé !');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 2990, '', 'en', 'Pacific Heights', 'Inspiré d''un fait réel, le film raconte l''histoire de Patty et Drake qui viennent d''acheter la maison de leurs rêves à San Francisco. Pour les aider financièrement, ils décident de louer la chambre du rez-de-chaussée et choisisent un dénommé Carter Hayes qui semble être le locataire idéal. Mais dès son emménagement, les choses tournent mal...', 13.32, '/sHLRLi865iDTjp2oFdhkqwhdRnK.jpg', '1991-01-03', 0, '', '', 'Fenêtre sur Pacifique');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1053600, '', 'es', '20.000 especies de abejas', 'Cocó, huit ans, a bien du mal à savoir qui elle est. Au cours d''un été passé parmi les ruches du Pays Basque, elle éveille sa singularité au sein des femmes de sa famille, elles-mêmes en proie au doute. Dans un monde où il existe 20 000 espèces d''abeilles différentes, il existe forcément une identité qui corresponde à Cocó.', 15.549, '/jfbqALrjskx2koaFpkKGxL8yPGB.jpg', '2024-02-14', 0, '', '', '20 000 espèces d''abeilles');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1018332, '', 'en', 'The Caine Mutiny Court-Martial', 'Un avocat défend à contrecœur un officier de marine qui a pris le contrôle de l''USS Caine pendant une violente tempête. Cependant, au fur et à mesure que la cour martiale avance, il se demande de plus en plus s''il s''agit vraiment d''une mutinerie.', 13.519, '/6hQg8uZW0TyHlm1uKeMZRXrgKVy.jpg', '2024-01-17', 0, '', '', 'L''affaire de la mutinerie du Caine');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 820696, '', 'fr', 'Le temps d’aimer', '1947. Sur une plage, Madeleine, serveuse dans un hôtel restaurant, mère d''un petit garçon, fait la connaissance de François, étudiant riche et cultivé. La force d’attraction qui les pousse l’un vers l’autre est à la mesure du secret dont chacun est porteur. Si l''on sait ce que Madeleine veut laisser derrière elle en suivant ce jeune homme, on découvre avec le temps, ce que François tente désespérément de fuir en mêlant le destin de Madeleine au sien...', 12.575, '/3oeVLIDIxVxBRFexZXiXPD1UVu4.jpg', '2023-11-29', 0, '', '', 'Le temps d’aimer');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1229958, '', 'en', 'Lover, Stalker, Killer', 'Comme toutes les personnes qui ont hâte de retrouver l''amour après une longue relation, Dave a essayé une appli de rencontres en ligne. Le contact avec Liz et Cari a été prometteur. Toutes deux mères célibataires et charmantes, elles l''ont aidé, chacune à sa manière, à sortir de sa coquille. Mais ce qui devait être l''occasion pour un papa travailleur et dévoué de renouer avec des amours sans lendemains est vite devenu un triangle amoureux tordu qui a mis Dave et ses proches en danger.', 19.332, '/8HH4eUBt4nmHyyiV3mEJvXbJXyl.jpg', '2024-02-09', 0, '', '', 'Lover, Stalker, Killer : L''ex de l''extrême');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1149791, '', 'ml', 'പ്രേമലു', 'Sachin, un étudiant qui poursuit ses études à Hyderabad, tombe amoureux de Reenu... comme imaginé à l''extérieur...', 12.992, '/uPpmBjY3znUqGY8kYwI5xvOrSc0.jpg', '2024-02-21', 0, '', '', 'Premalu');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 887870, '', 'ja', 'BLUE GIANT', 'Après avoir assisté pour la première fois à un spectacle de jazz, Dai décide de changer de vie du jour au lendemain. Comme hypnotisé par cette musique à laquelle il ne connaît rien, le jeune étudiant décide de se consacrer corps et âme au saxophone, et fait du jazz sa nouvelle passion. Parviendra-t-il à devenir un grand musicien ?', 15.792, '/5s5AKOfMq31lNVTDZn4cM6cyKZA.jpg', '2024-03-06', 0, '', '', 'BLUE GIANT');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1058620, '', 'en', 'The Persian Version', 'Leila ,une jeune femme de Brooklyn qui peine à trouver ses repères et à épouser les autres cultures, tout en repoussant les étiquettes que la société lui accole. Lorsque sa famille tumultueuse se réunit à New York pour la transplantation cardiaque de son père, Leila dissocie sa vraie vie de sa vie familiale. Mais la révélation de son secret met en lumière les différents parcours d''elle et de sa mère Shireen. Ponctué d''un spectre de couleurs vives, de pointes d''humour bien envoyées et d''électrisants numéros de danse, « The Persian Version » brosse un portrait fidèle d''une femme qui défend ses valeurs, au cours d''une émouvante histoire sur la famille, les racines et l''indéniable influence de la musique pop.', 18.988, '/1nzBlZtebWJXY8ap0ibkgb3eMUr.jpg', '2024-01-15', 0, '', '', 'The Persian Version');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 25318, '', 'en', 'Escape from Sobibor', 'Shlomo et Moses sont déportés dans le camp de concentration de Sobibor. Alors que les nouveaux venus partent directement dans les chambres à gaz, eux sont mis de côté car ils sont orfèvres. En effet, ils fabriqueront des bijoux pour les nazis. Parallèlement, un déporté de longue date décide de s''allier avec un chef de soldats russes afin d''organiser une rebellion...', 12.444, '/jXpu5EPUcli5YI9isjypUEnM3z1.jpg', '2006-02-16', 0, '', '', 'Les rescapés de Sobibor');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1000862, '', 'fr', 'Pas de vagues', 'Julien est professeur au collège. Jeune et volontaire, il essaie de créer du lien avec sa classe en prenant sous son aile quelques élèves, dont la timide Leslie. Ce traitement de faveur est mal perçu par certains camarades qui prêtent au professeur d''autres intentions. Julien est accusé de harcèlement. La rumeur se propage. Le professeur et son élève se retrouvent pris chacun dans un engrenage. Mais devant un collège qui risque de s''embraser, un seul mot d''ordre : pas de vagues...', 14.317, '/cNcubOGNGpqMvkieHHzoKN6Zvmi.jpg', '2024-03-27', 0, '', '', 'Pas de vagues');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1251636, '', 'en', 'The Bloody Hundredth', 'Rencontrez les vrais aviateurs ayant inspiré Masters of the Air à travers leur récit poignant et marquant sur le 100e groupe de bombardement.', 17.961, '/yDipw9gMb62fLpweeNWTexHsTi2.jpg', '2024-03-15', 0, '', '', 'The Bloody Hundredth : la 100e escadre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 67612, '', 'pt', 'Deus e o Diabo na Terra do Sol', 'L''œuvre met en scène un couple, Manuel et Rosa, bercés dans la misère des terres arides du Sertão. Désirant s''émanciper de leur situation accablante, Manuel tente de revendre deux vaches à son propriétaire, qui, profitant de la toute-puissance de sa condition, use de contraintes soumises au paysan dominé, le conduisant à clôturer la transaction par le meurtre.  Astreint à la fuite, Manuel espère trouver refuge et consolation dans les discours fanatiques de l''illuminé Sebastião, promettant une île, terre de paradis où règnerait justice lorsque « la terre et la mer se réuniront ». Mais l''infanticide perpétré sur un nourrisson, acte terrible et follement religieux, sous le regard de Rosa conduira celle-ci à poignarder le prédicateur, précédant l''illustre mercenaire Antônio das Mortes engagé par l''Église...', 13.673, '/aw0Zm6UzFKw76QPjEjf0X75WIsV.jpg', '1967-01-25', 0, '', '', 'Le Dieu noir et le Diable blond');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 970348, '', 'en', 'The Old Oak', 'TJ Ballantyne est le propriétaire du « Old Oak », un pub situé dans une petite bourgade du nord de l’Angleterre. Il y sert quotidiennement les mêmes habitués désœuvrés pour qui l’endroit est devenu le dernier lieu où se retrouver.  L’arrivée de réfugiés syriens va créer des tensions dans le village. TJ va cependant se lier d’amitié avec Yara, une jeune migrante passionnée par la photographie. Ensemble, ils vont tenter de redonner vie à la communauté locale en développant une cantine pour les plus démunis, quelles que soient leurs origines.', 15.686, '/3Anmzt8olvgQdRwW3hqCMWcDeoX.jpg', '2023-10-25', 0, '', '', 'The Old Oak');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1085779, '', 'en', 'Nǎi Nai & Wài Pó', 'La lettre d’amour d’un réalisateur à ses grand-mères, qui passent le crépuscule de leurs vies à chasser leurs soucis en dansant, en s’étirant et en pétant.', 15.756, '/uHYoNVM5vhgTO7zYgDsQsewEYLO.jpg', '2024-03-22', 0, '', '', 'Nǎi Nai & Wài Pó');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1216452, '', 'fr', 'Heureux Gagnants', '1 chance sur 19 millions. Plus de probabilité d’être frappé par une météorite que de gagner au loto. Pour nos heureux gagnants, le rêve va rapidement se transformer en cauchemar, et leur vie va voler en éclat dans un spectaculaire feu d’artifices de comédie noire et de sensations fortes.', 10.459, '/bkhbJUiOrkdNE58uWltqjXcrZKu.jpg', '2024-03-13', 0, '', '', 'Heureux Gagnants');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 40149, '', 'ja', '藪の中の黒猫', 'Une femme et sa belle fille sont violées et tuées par un groupe de samouraïs. Ivres de vengeance, elles renaissent sous la forme d''esprits chats et jurent de tuer tous les samouraïs. Jusqu''au jour où leur victime désignée est le fils de la femme, et donc mari de la jeune femme, revenu de la guerre.', 10.8, '/zP5t84YSQe9pOggFxtWIQJc3GNw.jpg', '1968-05-15', 0, '', '', 'Kuroneko');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 900379, '', 'de', 'Roter Himmel', 'Une petite maison de vacances au bord de la mer Baltique. Les journées sont chaudes et il n''a pas plu depuis des semaines. Quatre jeunes gens se réunissent, des amis anciens et nouveaux. Les forêts desséchées qui les entourent commencent à s''enflammer, tout comme leurs émotions. Le bonheur, la luxure et l''amour, mais aussi les jalousies, les rancœurs et les tensions. Pendant ce temps, les forêts brûlent. Et très vite, les flammes sont là.', 8.323, '/b5WNL9pPxoI1ZW80ehdLe2FSqdg.jpg', '2023-09-06', 0, '', '', 'Le ciel rouge');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 630720, '', 'it', 'Occhiali neri', 'Un soir, en sortant d''un hôtel, une escort-girl se fait trancher la gorge, en pleine rue. Sur les caméras de surveillance, la police remarque un homme fuyant la scène à bord d''une camionnette. Diana, escort elle aussi, s''est brûlée la cornée après avoir fixé une éclipse de soleil. Une nuit, la jeune femme se fait prendre en chasse par le tueur.', 16.04, '/aQDv9hON1GWZPFl4LD8mAlNjnDd.jpg', '2022-08-01', 0, '', '', 'Lunettes noires');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 928999, '', 'en', 'Desperate Riders', 'Après que Kansas Red ait sauvé le jeune Billy d''une fusillade au jeu de cartes, le garçon demande à Red de l''aider à protéger sa famille du hors-la-loi Thorn, qui vient d''enlever la mère de Billy, Carol. Alors que Red et Billy partent à la rescousse de Carol, ils se heurtent à la belle et coriace Leslie, qui a réussi à échapper aux hommes de Thorn. Les trois font la course pour arrêter le mariage de Thorn avec Carol avec des armes à feu - mais veut-elle être sauvée ?', 18.777, '/7pYYGm1dWZGkbJuhcuaHD6nE6k7.jpg', '2024-01-09', 0, '', '', 'Desperate Riders');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1106049, '', 'es', 'Descansar en paz', 'Un père de famille acculé par les dettes décide de profiter d''une coïncidence et d''une circonstance imprévisible pour disparaître. Après de nombreuses années passées loin de son pays sous une fausse identité, une découverte fortuite fait naître en lui l''irrésistible tentation de vouloir savoir ce que sont devenus les habitants de sa ville en son absence. Une interrogation qui se transforme en obsession : une vie entière peut-elle disparaître et être oubliée ? Peut-on repartir de zéro sans jamais regarder en arrière ?', 12.701, '/v4IzAaCXLh8EhKVfmcwumXfqrJL.jpg', '2024-03-27', 0, '', '', 'Reposer en paix');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1224953, '', 'en', 'A Hero''s Journey: The Making of Percy Jackson and the Olympians', 'Pas facile d’être un demi-dieu... ni d’incarner des personnages très appréciés. Walker, Leah et Aryan se lancent dans une quête à l’écran, mais vivent aussi leur propre aventure. Suivez ces trois jeunes héros, accompagnés d’un casting et d’équipes de passionnés, dans des univers conçus pour les dieux, des combats contre d’inoubliables créatures et des cascades légendaires. Et j’oubliais : ils doivent aussi aller à l’école.', 15.365, '/wvxLQTGgzgiCV802ekIIEQtoWLu.jpg', '2024-01-31', 0, '', '', 'Le Périple d''un Héro : Le Making of de Percy Jackson et les Olympiens');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 869760, '', 'te', 'హను-మాన్', 'Un lieu imaginaire appelé Anjanadri, où le protagoniste acquiert les pouvoirs de Hanuman et se bat pour Anjanadri.', 8.097, '/m1zq48rWSXxplzoJR8YtbXWnnHM.jpg', '2024-01-12', 0, '', '', 'Hanu Man');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1045770, '', 'fr', 'Daaaaaalí!', 'Une journaliste française rencontre Salvador Dali à plusieurs reprises pour un projet de documentaire.', 11.321, '/9jlGTo6GiHeri1lx2czChvLzTO3.jpg', '2024-02-07', 0, '', '', 'Daaaaaalí!');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 144204, '', 'en', 'Thanatomorphose', 'Après une folle nuit de sexe avec son partenaire du moment, la jeune et jolie Laura se réveille au petit matin avec d’étranges marques sur le corps. La jeune femme n’y prête pas vraiment attention mais son état va toutefois très rapidement empirer. En effet, sa peau commence à se nécroser, ses ongles à tomber et tout son corps dégage une forte odeur de décomposition. Rien ne semble pouvoir enrayer ce phénomène répugnant et, au fur et à mesure que ses chairs se putréfient, la raison de Laura va se mettre à vaciller, la plongeant bientôt dans une véritable folie meurtrière…', 9.718, '/6EBH4OrpJrPWROoPqcURbtaUdRA.jpg', '2015-04-07', 0, '', '', 'Thanatomorphose');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 997294, '', 'es', 'Cerrar los ojos', 'Des années après sa mystérieuse disparition, Julio Arenas, un célèbre acteur espagnol, refait parler de lui grâce à une émission de télévision.', 9.429, '/qgFBRhR6FOwywlaGkIme8nC6aFc.jpg', '2023-08-16', 0, '', '', 'Fermer les yeux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 718838, '', 'en', 'Mothering Sunday', '1924, Beechwood en Angleterre. Jane Fairchild est la bonne d''une famille d''aristocrates, les Niven. À l''occasion de la fête des mères, ses patrons lui accordent une journée de repos. Jane profite de l''occasion pour retrouver son amant, Paul. Ce dernier est le fils des voisins des Niven et il est fiancé à Emma Hobnay. Les Niven qui ont perdu leur fils lors de la Première Guerre mondiale se réjouissent du futur mariage de Paul comme si celui-ci était leur propre enfant.', 17.989, '/s4lSrmTRRHj5nxJggpC2hgKjOfo.jpg', '2023-10-04', 0, '', '', 'Entre les lignes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1047016, '', 'it', 'Comandante', 'Au début de la Seconde Guerre mondiale, Salvatore Todaro commande le sous-marin Cappellini de la Royal Navy italienne. Par une nuit noire d''octobre 1940, alors qu''il navigue dans l''Atlantique, il tombe sur un navire marchand armé naviguant toutes lumières éteintes. Il tire ses canons sur le navire et le coule. Ensuite, le Commandant prend une décision destinée à entrer dans l''histoire : il sauve les 26 Belges naufragés qui autrement se seraient noyés au milieu de l''océan et les débarque au port sûr le plus proche, comme le prescrit le droit de la mer. Pour leur faire place à bord de son sous-marin, il est contraint de naviguer à la surface de l''eau pendant trois jours, visible des forces ennemies et mettant en danger sa vie et celle de ses hommes.', 12.984, '/gx7I64c46380zpRX66EsdL7Ga9x.jpg', '2024-02-08', 0, '', '', 'Comandante');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 935906, '', 'en', 'Scrapper', 'Banlieue de Londres. Georgie 12 ans vit seule depuis la mort de sa mère. Elle se débrouille au quotidien pour éloigner les travailleurs sociaux, raconte qu’elle vit avec un oncle, gagne de l’argent en faisant un trafic de vélo avec son ami Ali. Cet équilibre fonctionne jusqu’à l’arrivée de Jason, un jeune homme qu’elle ne connait pas et se présente comme étant son père.', 9.952, '/7qWaACGWt4GCpmKSWBR1SRsu6Ug.jpg', '2024-01-10', 0, '', '', 'Scrapper');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1166133, '', 'ml', 'ഭ്രമയുഗം', 'Thevan, un chanteur folk de la caste Paanan, fait une rencontre fatidique en échappant à l''esclavage, ce qui l''amène à découvrir une ancienne tradition qui modifie son destin.', 13.023, '/x4geWE7mQwGyzNGLw1MKoDf8Fs7.jpg', '2024-02-15', 0, '', '', 'ഭ്രമയുഗം');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1119355, '', 'es', 'Mi soledad tiene alas', 'Dan, un artiste de rue en herbe, survit en braquant des bijouteries. Mais lorsque son père sort de prison, ses démons refont surface et il doit alors s''enfuir.', 12.638, '/zj7UYU6PZQtWh4sznRH1LKJp945.jpg', '2024-01-19', 0, '', '', 'Ma solitude a des ailes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 39434, '', 'it', 'La sindrome di Stendhal', 'Une jeune femme inspecteur de police victime d''étranges malaises est la proie d''un dangereux maniaque sexuel.', 11.343, '/1V87vBQz6S4C91roGGe0tYxioao.jpg', '1999-12-13', 0, '', '', 'Le Syndrome de Stendhal');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1058688, '', 'et', 'Savvusanna sõsarad', 'Dans l’intimité des saunas sacrés d’Estonie, tous les rituels de la vie se croisent. Les femmes y racontent ce qu’elles taisent partout ailleurs, et dans la fumée des pierres brûlantes, la condition féminine apparaît, dans toute sa vérité et sa force éternelle.', 12.684, '/dBVE7O1ox7E3xAqk7z772pSergG.jpg', '2024-03-20', 0, '', '', 'Smoke Sauna Sisterhood');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 23439, '', 'en', 'House of Usher', 'Philip Withrop se rend a la maison Usher pour rencontrer sa fiancée Madeline. Roderick, le frère de Madeline, s''oppose a leur mariage. Madeline est victime d''une crise de catalepsie, et Roderick s''empresse de l''enterrer. Philip découvre la vérité, mais Madeline sort de son tombeau et veut tuer son frère. La maison s''écroule alors sur eux.', 13.561, '/65VBuQ30P18UoCf6jOIPDXK0naN.jpg', '1964-03-04', 0, '', '', 'La Chute de la maison Usher');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1237835, '', 'en', 'A Soweto Love Story', 'Lorsque ses trois fils adultes ne montrent aucun signe de s''installer et de fonder leur propre famille, une mère utilise la maison familiale tant convoitée pour les inciter à faire la bonne chose : le premier à se marier avant que le Nouvel An ne remporte la maison.', 10.897, '/lNxLPPXAR13sjNIBGIncnR13v6E.jpg', '2024-02-14', 0, '', '', 'A Soweto Love Story');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 947507, '', 'no', 'Helt super', 'Evie, 11 ans, n’est pas une fille ordinaire ! Elle jongle entre l’école, ses amis, et sa passion pour les jeux vidéo. Mais chut ! Elle a un incroyable secret : son papa est Super Lion, le super-héros le plus cool de l’univers… Ce sera bientôt à son tour de montrer ses pouvoirs et de plonger dans l’aventure la plus folle de sa vie. Préparez-vous, Super Evie arrive !', 10.565, '/b2uID0GWtUnwc56OmLineedvMzu.jpg', '2024-05-08', 0, '', '', 'Super Lion');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 399178, '', 'en', 'Benediction', 'Siegfried Sassoon était un soldat héroïque de la Première Guerre mondiale mais également un poète. Son oeuvre lui a valu les éloges de la critique et du public. Ses faits d''armes lui ont accordé ses gallons. Homosexuel, Sassoon a dû cacher son orientation sexuelle dans une époque peu tolérante envers la communauté gay. Il a alors tenté de trouver son salut dans un mariage conforme et dans la religion.', 9.268, '/AskdoMipKIwkJriAT0FvGGAYXFK.jpg', '2024-03-06', 0, '', '', 'Les Carnets de Siegfried');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 888003, '', 'fr', 'Le Livre des solutions', 'Marc s''enfuit avec toute son équipe dans un petit village des Cévennes pour finir son film chez sa tante Denise. Sur place, sa créativité se manifeste par un million d''idées qui le plongent dans un drôle de chaos. Marc se lance alors dans l’écriture du Livre des Solutions, un guide de conseils pratiques qui pourrait bien être la solution à tous ses problèmes…', 11.292, '/1ljGxJuIB7PVnvtEFiNSVRnfXch.jpg', '2023-06-07', 0, '', '', 'Le Livre des solutions');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1109715, '', 'en', 'The Jewel Thief', 'L''incroyable récit de première main de Gerald Blanchard, l''un des cerveaux criminels les plus créatifs, les plus calculateurs et les plus accomplis de l''histoire moderne.', 10.605, '/ayS8efUEJeMkUfxEgxcp3CYWvxZ.jpg', '2024-01-17', 0, '', '', 'Le voleur de bijoux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1207731, '', 'en', 'June', 'Jusqu''à l''interprétation oscarisée de Reese Witherspoon dans le film biographique Walk the Line, consacré à Johnny Cash, la plupart des spectateurs ne connaissaient pas June Carter Cash. Le documentaire "June" de Kristen Vaurio permet de mieux comprendre cette artiste aux multiples talents, chanteuse, compositrice, comédienne et actrice. Des documents d''archives inédits révèlent la vivacité d''esprit et le charisme de June en tant qu''artiste.', 12.901, '/ybt8XseXREN5WKXRaDy70ERvHnL.jpg', '2024-01-17', 0, '', '', 'June');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1001079, '', 'fr', 'Iris et les hommes', 'Iris répond "Pas", à l’ostéopathe quand il lui demande comment ça se passe avec son mari. Cet aveu échappe à Iris, il la surprend elle-même, il la bouleverse, comme une brutale prise de conscience : depuis combien de temps n’a-t-elle pas fait l’amour ? Pourtant, tout va bien : Iris a un mari qui semble n’avoir aucun défaut, deux filles formidables, un cabinet dentaire où les patients se succèdent sans temps mort, un bel appartement dans un quartier qu’elle adore, des amis qui lui ressemblent… Et bientôt 50 ans. C’est une inconnue qui lui souffle l’idée : "Prenez un amant". Iris ouvrira la boîte de Pandore et aussitôt, les hommes surgiront - comme s’il en pleuvait.', 9.763, '/uJTAta3TUM5mUuBTGGgbGOcyQjz.jpg', '2024-01-03', 0, '', '', 'Iris et les hommes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 622082, '', 'en', 'In from the Side', 'Les tensions sont vives dans un club de rugby Londonien : l''équipe est à court d''argent et divisée. C''est dans ce contexte électrique que lors d''une soirée arrosée, Mark, une nouvelle recrue, couche avec Warren, un de ses coéquipiers les plus populaires. Le hic, c''est qu''ils sont tous les deux déjà en couple. Les deux hommes doivent dissimuler leurs sentiments grandissants en conciliant leurs propres vulnérabilités et les démonstrations de machisme sur le terrain. Ou risquer de détruire le club qu''ils affectionnent.', 16.041, '/8xPdGkGNZEemJMvq99weCBRx6H4.jpg', '2024-02-28', 0, '', '', 'Dans la mêlée');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1170719, '', 'fr', 'Sirènes', 'Alison Flesh, capitaine de police niçoise rebelle et intrépide est contrainte par son supérieur le commissaire Djiba de travailler en binôme avec la lieutenante parisienne Leïla Balani, experte en criminologie mais véritable boulet sur le terrain. Sur la piste d’un serial killer, Flesh et Balani vont devoir faire équipe quitte à retourner toute la Riviera.', 11.67, '/fUAzK5RRPIrmY2gFVKgfdXlrTiX.jpg', '2024-03-15', 0, '', '', 'Sirènes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1238127, '', 'ja', 'パレード', 'Après son réveil sur la plage, Minako apprend qu''elle est décédée et participe aux Parades, un rassemblement de personnes décédées à la recherche d''un être cher.', 10.636, '/icx1KNeoYbLAQpSeySJgWiHfQaD.jpg', '2024-02-29', 0, '', '', 'The Parades');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 39436, '', 'it', 'Non ho sonno', 'À Turin, un tueur assassine des jeunes filles dans des circonstances identiques à celles d''une série de meurtres perpétrés vingt ans auparavant. La police piétine et le commissaire Ulysse Moretti, qui fut autrefois chargé de l''enquête, reprend du service. Il est aidé par Giacomo Gallo, un jeune homme dont la mère fut jadis victime du meurtrier sanguinaire.', 9.451, '/oq9bLMOdINfN1FPKiSZd6y3WmQQ.jpg', '2002-03-12', 0, '', '', 'Le Sang des innocents');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 23847, '', 'en', 'Across 110th Street', 'Malgré leur malchance, trois débutantes cambrioleurs réussissent un coup inouï, mais sont poursuivis par la mafia blanche, la mafia noire et la police.', 11.187, '/4pNe0rWStRrjcIUfvIRxkwsvYqe.jpg', '1973-08-09', 0, '', '', 'Meurtres dans la 110e rue');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 923387, '', 'nl', 'Het smelt', 'De nombreuses années après cet été où tout a basculé, Eva retourne pour la première fois dans son village natal avec un énorme bloc de glace dans son coffre, bien déterminée à affronter son passé.', 8.525, '/9ZldVzgeAoN9QgOTxKqBsTBwPvZ.jpg', '2024-02-28', 0, '', '', 'Débâcle');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1194184, '', 'fr', 'Les Enfoirés 2024 - On a 35 ans !', 'En janvier 2024, les Enfoirés se sont produits à L’Arkéa Arena à Bordeaux pour fêter leurs 35 ans. Cette édition anniversaire nous promet un show spectaculaire, porté par 47 Enfoirés plus que jamais engagés auprès des Restos du Cœur. De beaux moments d’émotion et de rire sont au rendez-vous.', 8.706, '/zQR3hAKRxBGBNUN74H12Q0O287y.jpg', '2024-03-01', 0, '', '', 'Les Enfoirés 2024 - On a 35 ans !');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 998642, '', 'fr', 'Nouveau départ', 'Amoureux de Diane comme au premier jour, Alain traverse la cinquantaine sans crise. Même le départ des enfants, il l’a bien vécu. Diane moins… Cette période, elle l’entame avec la sensation qu’elle pourrait mourir d’ennui ou d’angoisse. Pour Alain, qui voit pour la première fois son couple vaciller, il est temps de se poser les questions essentielles, et de prendre un risque majeur après trente ans de vie commune : quitter Diane pour réveiller la flamme et l’envie de se retrouver. Nouveau départ, c’est l’histoire d’une histoire d’amour à quitte ou double.', 8.062, '/nhBv37kJBloQeZZpjOdBMwcXvBV.jpg', '2023-09-27', 0, '', '', 'Nouveau départ');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 868660, '', 'te', 'గుంటూరు కారం', 'Guntur Kaaram, le roi du monde souterrain de la ville de Guntur, tombe amoureux d''une journaliste qui travaille à dévoiler les activités illégales de la ville.', 10.909, '/3Q2aeiTfuDkG8rscFSpMzdvw1uv.jpg', '2024-01-12', 0, '', '', 'Guntur Kaaram');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1171861, '', 'en', 'The Last Repair Shop', 'Depuis 1959, Los Angeles est l''un des derniers bastions américains à fournir gratuitement des instruments de musique aux élèves des écoles publiques. Réalisé par le duo de documentaristes Ben Proudfoot et Kris Bowers, tous deux nominés aux Oscars, "The Last Repair Shop" vous donne un accès privilégié à cet atelier de centre-ville où une poignée d''artisans dévoués maintiennent quelques 80 000 instruments en bon état de fonctionnement.', 12.962, '/DJ9wKCKIprMA3krenGOhNFqofU.jpg', '2024-01-23', 0, '', '', 'The Last Repair Shop');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1227136, '', 'hi', 'भक्षक', 'Une journaliste en mal de copie entame une enquête de longue haleine sur la complicité des autorités dans d''abominables crimes commis au sein d''un foyer pour jeunes filles.', 14.616, '/2eCELoyf0l3njFKfn3oddo3JaRG.jpg', '2024-02-10', 0, '', '', 'Bhakshak : L''injustice en face');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 745611, '', 'fr', 'Délicieux', 'À l’aube de la Révolution Française, Pierre Manceron, cuisinier audacieux mais orgueilleux, est limogé par son maître le duc de Chamfort. La rencontre d’une femme étonnante, qui souhaite apprendre l’art culinaire à ses côtés, lui redonne confiance en lui et le pousse à s’émanciper de sa condition de domestique pour entreprendre sa propre révolution. Ensemble, ils vont inventer un lieu de plaisir et de partage ouvert à tous : le premier restaurant. Une idée qui leur vaudra clients… et ennemis.', 12.654, '/jFnT5TNGBSsD1GIfU8OhS9mXOgz.jpg', '2021-09-08', 0, '', '', 'Délicieux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1121956, '', 'pl', 'Zielona granica', 'Dans les forêts dangereuses et marécageuses qui constituent la « frontière verte » entre la Biélorussie et la Pologne, les réfugiés du Moyen-Orient et d’Afrique qui tentent d’atteindre l’Union européenne sont piégés dans une crise géopolitique cyniquement orchestrée par le dictateur biélorusse Alexandre Loukachenko. Dans une tentative de provoquer l’Europe, les réfugiés sont attirés vers la frontière par une propagande promettant un passage facile vers l’UE. Pions de cette guerre cachée, les vies de Julia, une nouvelle militante qui a renoncé à sa vie confortable, de Jan, un jeune garde-frontière, et d''une famille syrienne s''entremêlent.', 12.464, '/cmNA11urfmPGHxQlooVNCaxZSwR.jpg', '2024-02-15', 0, '', '', 'Green Border');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 975407, '', 'en', 'Lifemark', 'Le monde tranquille de David est bouleversé lorsque sa mère biologique le contacte et lui tend inopinément la main, désireuse de rencontrer le fils de 18 ans qu’elle n’a tenu qu’une fois dans ses bras. Encouragé par ses parents adoptifs, David entreprend un voyage qui va l’amener à découvrir une vérité inouïe sur son passé.', 15.088, '/YtUiBOuF2MfWCjYqQRH77Hkd3f.jpg', '2024-02-14', 0, '', '', 'Adopté');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1044741, '', 'fr', 'Hors-Saison', 'Mathieu habite Paris, Alice vit dans une petite cité balnéaire de l’Ouest de la France. Il caresse la cinquantaine, c’est un acteur connu. Elle a dépassé la quarantaine, elle est professeure de piano. Ils se sont aimés il y a une quinzaine d’années. Puis séparés. Depuis, le temps a passé, chacun a suivi sa route et les plaies se sont refermées peu à peu. Quand Mathieu vient diluer sa mélancolie dans les bains à remous d’une thalasso, il retrouve Alice par hasard.', 8.267, '/6T3gDfcxvzLgMKbSQ1GmxiRBNrU.jpg', '2024-03-20', 0, '', '', 'Hors-Saison');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 836486, '', 'en', 'JFK Revisited: Through the Looking Glass', 'Trente ans après son film JFK, Oli­ver Stone fait voya­ger les spec­ta­teurs à tra­vers les dos­siers récem­ment déclas­si­fiés de l’assassinat du Pré­sident Ken­ne­dy – le meurtre mys­té­rieux amé­ri­cain le plus consé­quent du XXème siècle. Avec le concours d’une équipe d’experts scien­ti­fiques, médi­caux et balis­tiques, d’historiens et de témoins, Oli­ver Stone pré­sente des preuves convain­cantes que dans l’affaire Ken­ne­dy, la « théo­rie du com­plot » est à pré­sent « com­plot accompli ».', 14.229, '/hSXZdWmND9fcYm32SfYDU7SWYdj.jpg', '2021-07-12', 0, '', '', 'JFK : L''Enquête');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1072799, '', 'en', 'Down Low', 'Divorcé et dépressif, Gary engage Cameron, un travailleur du sexe plein d''entrain et sans limites, pour lui faire un massage érotique. Mais lorsque Cameron découvre à quel point Gary est inexpérimenté malgré son âge, il est bien déterminé à lui donner un cours de rattrapage intensif sur la vie homosexuelle. Le programme prévu par Cameron en matière d''applications de rencontre et de découverte du monde gay fait rapidement prendre à la journée une tournure extrêmement obscure. Notre duo va devoir faire face à un voisin curieux qui prend beaucoup trop de pilules, un casse-pieds du Dark Web et bien plus encore au terme d''une soirée hilarante de conséquences et de confrontations !', 10.337, '/uQZmja9EbwztnFwDAeJ8aJxFRmR.jpg', '2024-01-22', 0, '', '', 'Low Profile');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 528502, '', 'en', 'Aporia', 'Depuis qu''elle a perdu son mari, Sophie s''efforce de gérer son deuil, son travail à plein temps et l''éducation de sa fille dévastée. Peu après, un ancien physicien révèle l''existence d''une machine secrète permettant de modifier le temps.', 9.943, '/zYt35YGKoc85LXULzMKDfJ8qX1C.jpg', '2024-01-01', 0, '', '', 'Aporia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 937633, '', 'ro', 'Sparta', 'Ewald s''est installé en Roumanie il y a plusieurs années. La quarantaine passée, il cherche un nouveau départ. Il quitte alors sa petite amie et part vivre dans l''arrière-pays où il ouvre un centre de judo pour jeunes garçons. Si les enfants profitent d''une nouvelle existence insouciante, la méfiance des villageois, elle, s''éveille rapidement. Ewald est alors obligé d''affronter une vérité qu''il a longtemps refoulée.', 11.706, '/wKYXcKmJngpKWgwtrJr4RQCdwF9.jpg', '2023-05-31', 0, '', '', 'Sparta');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 964877, '', 'fr', 'Nous, les Leroy', 'Sandrine Leroy annonce à son mari Christophe qu’elle veut divorcer. Leurs enfants ont bientôt l’âge de quitter la maison. Dans une opération de la dernière chance aussi audacieuse qu’invraisemblable, Christophe organise un week-end pour sauver son mariage : un voyage passant par les endroits clés de l’histoire de leur famille. Un voyage qui ne va pas être de tout repos.', 6.948, '/tydkeN2ClMSLVnohzyiLIUmaRLT.jpg', '2024-04-10', 0, '', '', 'Nous, les Leroy');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1118033, '', 'en', 'The Seeding', 'Un randonneur se perd dans le désert et cherche refuge auprès d''une femme qui vit seule. Il découvre qu''elle n''est peut-être pas là de son plein gré...', 10.438, '/sMHvmhKlyyyCWLc3sCxY9wQ0Gai.jpg', '2024-01-27', 0, '', '', 'The Seeding');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1100474, '', 'fr', 'Inestimable', 'Inspiré de l''histoire vraie de la découverte du trésor de Lava, en 1985 près d''Ajaccio.', 6.176, '/e7G9qdYdQBJ5MEDAH7id0vMEBbi.jpg', '2023-11-01', 0, '', '', 'Inestimable');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1001028, '', 'fr', 'Une affaire de principe', 'Bruxelles, 2012. Quand le commissaire à la santé est limogé du jour au lendemain, dans la plus grande opacité, le député européen José Bové et ses assistants parlementaires décident de mener l’enquête. Ils vont alors découvrir un véritable complot menaçant de déstabiliser les instances européennes, jusqu’à leur sommet. Tiré de faits réels.', 8.312, '/547rE9XMU62a9IuL3fMcwffuTJb.jpg', '2024-05-01', 0, '', '', 'Une affaire de principe');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 30137, '', 'en', 'The Haunted Palace', 'Un sorcier lance une malédiction sur un groupe de villageois. Des années plus tard, l''un des descendants du sorcier revient dans le village maudit.', 13.885, '/3kWwUpquLxMYpxVHtsJbAgPl2t.jpg', '1970-05-27', 0, '', '', 'La Malédiction d''Arkham');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 923150, '', 'zh', '燃冬', 'C’est l’hiver à Yanji, une ville au nord de la Chine, à la frontière de la Corée. Venu de Shanghai pour un mariage, Haofeng s’y sent un peu perdu. Par hasard, il rencontre Nana, une jeune guide touristique qui le fascine. Elle lui présente Xiao, un ami cuisinier. Les trois se lient rapidement après une première soirée festive. Cette rencontre intense se poursuit, et les confronte à leur histoire et à leurs secrets. Leurs désirs endormis dégèlent alors lentement, comme les paysages et forêts enneigées du Mont Changbai.', 8.904, '/gpIvH8KWu5L7dm0AixZSh3OJHQo.jpg', '2023-11-22', 0, '', '', 'Un hiver à Yanji');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 48155, '', 'en', 'The Big Knife', 'Charlie Castle, vedette d''Hollywood, a promis à sa femme de ne pas se lier à son producteur Stanley Hoff par un autre contrat. Mais le malheureux, pour ne pas voir exploiter certains faits délicats de sa vie privée, est obligé de revenir sur sa décision. Quand il tente de faire machine arrière, il est trop tard : tous ceux qui ont intérêt à lui nuire sont là, et sa femme, qu''il veut reconquérir, parle de le quitter..', 9.912, '/pCij1oYi0hGnWcn4yr9mNJAzB6C.jpg', '1956-10-01', 0, '', '', 'Le Grand Couteau');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1211785, '', 'fr', 'Désir et rébellion : « L''Art de la joie » – Goliarda Sapienza', 'Entre 1967 et 1976, l''écrivaine italienne Goliarda Sapienza (1924-76) a écrit « L''Art de la joie », un roman subversif sur l''ascension sociale fulgurante d''une héroïne rebelle, trop scandaleux pour être publié à cette époque contradictoire.', 8.791, '/aXAjHYefzzGfLwX6JXe3OHowPp3.jpg', '2023-10-21', 0, '', '', 'Désir et rébellion : « L''Art de la joie » – Goliarda Sapienza');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 926967, '', 'de', 'Schweigend steht der Wald', 'Au cours d''une mission, Anja Grimm, une brillante géologue, retourne dans la forêt où son père a disparu sans laisser de trace lorsqu''elle était enfant. Elle y fait une découverte déconcertante en rapport avec cette disparition et se lance dans sa propre enquête, malgré les réticences des autorités locales.', 13.719, '/hBKaj1NqsK9GUdECqU25fHSAC3U.jpg', '2022-11-17', 0, '', '', 'La Forêt silencieuse');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1084863, '', 'fr', 'Le Mangeur d''âmes', 'La commandante Élisabeth Guardiano est chargée d''aller enquêter sur un double meurtre d''une rare brutalité dans une petite commune des Vosges. Sur place, elle rencontre le capitaine de gendarmerie Franck de Rolan qui fait face à une série de disparitions d’enfants. Impuissants face à un village hostile, ils vont être contraints d’unir leurs forces pour découvrir la vérité, une vérité terrifiante empreinte de légendes occultes...', 7.955, '/yrMzqj6P75GfBg0SiFbm0adu16s.jpg', '2024-04-24', 0, '', '', 'Le Mangeur d''âmes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1208992, '', 'cs', 'Hranice Evropy', 'Victimes d’un "rideau de fer des salaires", des centaines de milliers de travailleurs migrent de l’est de l’Europe vers l’ouest. Quatre ans après une première enquête en République tchèque, la journaliste Sasa Uhlová poursuit, en caméra cachée, sa charge contre le néolibéralisme.', 9.16, '/9JMQaNzvOrXUcrudf92WsTImy8Y.jpg', '2024-03-19', 0, '', '', 'Invisibles : Les Travailleurs de l''Europe de l''Est');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1047227, '', 'fr', 'Les Chèvres !', 'Saviez-vous qu’au 17ème siècle, les animaux pouvaient être jugés pour avoir commis un crime ? Maître Pompignac, risée du barreau, pense avoir trouvé l’affaire de sa vie : défendre la jeune et innocente Josette, accusée à tort du meurtre d’un maréchal… Mais c’était sans compter sur son adversaire, le redoutable et réputé Maître Valvert, et surtout sur Josette, qui s’avère n’être autre… qu’une chèvre !', 7.069, '/iQTcohGPjv522w0F7KdwX58etJc.jpg', '2024-02-21', 0, '', '', 'Les Chèvres !');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1882, '', 'en', 'The Men', 'Ken, jeune lieutenant américain, est gravement blessé lors d''un assaut dans une ville européenne pendant la Seconde Guerre mondiale. De retour aux Etats-Unis, aigri et paraplégique, Ken va connaître une difficile réadaptation à la vie de tous les jours.', 7.339, '/bSGm9aRftcFRwNLay4Dh0F3OBnd.jpg', '1951-10-17', 0, '', '', 'C''étaient des hommes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 18462, '', 'en', 'Night of the Comet', 'Après le passage d''une comète à proximité de la Terre, presque toute la population mondiale est décimée. Regina et sa sœur Samantha survivent et vont trouver refuge dans le studio d''une radio locale. Elles y rencontrent Hector, un routier protégé par la cabine en acier de son engin. Dans ce monde dévasté, les jeunes filles commencent par faire du shopping, mais ce sera sans compter sur des survivants mutants agressifs, et une équipe de scientifiques cherchant à créer un sérum permettant de guérir ce mal.', 9.665, '/rhwimpEkyPWVV9p5wlSyde6QS0V.jpg', '2024-02-29', 0, '', '', 'La nuit de la comète');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 818721, '', 'fr', 'Visions', 'Estelle, une brillante capitaine de ligne, mène une vie parfaite avec son mari Guillaume, un médecin réputé. Quelque chose est sur le point de changer lorsqu''Estelle tombe amoureuse d''Ana et entame une liaison intense.', 10.28, '/9eE05EIusx5OdRvLA76lrAiRXL6.jpg', '2023-09-06', 0, '', '', 'Visions');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 48688, '', 'it', 'Porcile', 'Deux histoires entrelacées dans un montage parallèle. Sur l''Etna, à une période inconnue, un jeune homme affamé devient meurtrier et cannibale.  Dans l''Allemagne contemporaine, un jeune homme connaît un étrange amour avec des porcs.', 9.903, '/5Jfe1XTw61wBK37npqbU6nNjpSD.jpg', '1969-10-10', 0, '', '', 'Porcherie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1083300, '', 'fr', 'Maison de retraite 2', 'Quand le foyer Lino Vartan - qui accueille jeunes orphelins et seniors - doit fermer pour raisons sanitaires, Milann n’a pas d’autre choix que de répondre à l’invitation d’une maison de retraite dans le Sud qui les accueille pour l’été. Tous embarquent dans le bus d’Alban. Enfants et anciens découvrent alors le Bel Azur Club, une villa idyllique au bord de la mer : le rêve ! Une aubaine pour ces gamins orphelins qui n’ont jamais eu de vacances... Mais le paradis tourne à l’enfer car anciens et nouveaux pensionnaires du 3e âge se détestent ! La guerre des seniors est déclarée !', 11.62, '/jRVpU4mH28wmXfY56ZcrPVNFZm5.jpg', '2024-02-14', 0, '', '', 'Maison de retraite 2');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1114751, '', 'fr', 'Ici et là-bas', 'Adrien, installé depuis quinze ans au Sénégal, forme un couple mixte heureux avec sa femme Aminata. Renvoyé en France pour un problème de visa, il est accueilli par Sékou, un cousin de sa femme, qui vit à Paris et cherche à tout prix à s’intégrer. Le temps de trouver une solution pour rentrer, Adrien va devoir faire équipe avec Sékou qui l’embarque dans un tour de France des terroirs ; entre Adrien plus sénégalais que son cousin et Sékou qui va affronter pour la première fois le regard de ses clients, le duo va au-devant de bien des surprises.', 6.823, '/pwZ55jD7mZWXgDLRKJIYCdVucDd.jpg', '2024-04-17', 0, '', '', 'Ici et là-bas');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 29030, '', 'en', 'The Tomb of Ligeia', 'Un homme de la haute bourgeoisie est hanté par des visions de sa défunte femme. Il est certain d''une chose : sa nouvelle compagne est possédée par l''esprit diabolique de celle-ci.', 10.387, '/6SeEMnR0AnXAwWfgmgttQiCaHIb.jpg', '1968-12-18', 0, '', '', 'La tombe de Ligeia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 890116, '', 'hi', 'मेरी क्रिसमस', 'Deux étrangers se rencontrent lors d''un désastreux réveillon de Noël. Une folle nuit d''amour tourne au cauchemar. Révéler quoi que ce soit de plus serait un crime.', 6.928, '/zF3cqcT8OlDTcKIFAoXVK1gO9qW.jpg', '2024-01-12', 0, '', '', 'Merry Christmas');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1015356, '', 'en', 'To Kill a Tiger', 'Lorsque sa fille adolescente survit à un crime atroce, un modeste fermier se lance dans une remarquable quête de justice et se dresse contre l''intolérance de son village.', 10.537, '/c0fDaEhVl98h8hd5QuNVzPp6njT.jpg', '2024-03-10', 0, '', '', 'Tuer un tigre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 5425, '', 'it', 'Buio Omega', 'Lorsque sa fiancée meurt des suites d''un rituel vaudou, le monde de Franck s''écroule. Refusant d''y croire, il se sert de sa passion pour la taxidermie pour embaumer le corps de son amie et la garder auprès de lui. Ayant définitivement perdu tout contact avec la réalité, et aidé par une inquiétante gouvernante, Franck va alors chercher une nouvelle amie. Une femme qui l''acceptera, lui et le cadavre de son ancienne compagne...', 7.847, '/wnlE3wyNXTrh2FrE1ZbuU8lsNn4.jpg', '1982-06-30', 0, '', '', 'Blue Holocaust');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 22815, '', 'en', 'The Prince and the Pauper', 'Un pauvre garçon et le prince de Galles identités de change, mais les événements forcent la paire à l''expérience la vie des autres aussi bien.', 8.135, '/s75YOBwNbbMZnNRCuVhm07Fapmc.jpg', '1978-08-02', 0, '', '', 'Le Prince et le Pauvre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 961084, '', 'fr', 'Bernadette', 'Quand elle arrive à l’Elysée, Bernadette Chirac s’attend à obtenir enfin la place qu’elle mérite, elle qui a toujours œuvré dans l’ombre de son mari pour qu’il devienne président. Mise de côté car jugée trop ringarde, Bernadette décide alors de prendre sa revanche en devenant une figure médiatique incontournable.', 7.397, '/jVn6xUNLfbGvzUhjKblV8r8EkgE.jpg', '2023-10-04', 0, '', '', 'Bernadette');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 965791, '', 'fr', 'Une année difficile', 'Albert et Bruno sont surendettés et en bout de course, c’est dans le chemin associatif qu’ils empruntent ensemble qu’ils croisent des jeunes militants écolos. Plus attirés par la bière et les chips gratuites que par leurs arguments, ils vont peu à peu intégrer le mouvement sans conviction.', 8.63, '/calbXqzHVxfKlmtcwnSdCMW4owN.jpg', '2023-10-18', 0, '', '', 'Une année difficile');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 944117, '', 'fr', 'L''Abbé Pierre : Une vie de combats', 'Né dans une famille bourgeoise, Henri Grouès a été à la fois résistant, député, défenseur des sans-abris, révolutionnaire et iconoclaste. Des bancs de l’Assemblée Nationale aux bidonvilles de la banlieue parisienne, son engagement auprès des plus faibles lui a valu une renommée internationale. Pourtant, chaque jour, il a douté de son action. Une vie intime inconnue et à peine crédible. Révolté par la misère, les inégalités et les injustices, souvent critiqué, parfois trahi, Henri Grouès a eu mille vies et mille combats. Il a marqué l’Histoire sous le nom qu’il s’était choisi : l’abbé Pierre.', 8.87, '/6LjskUOqWCVN7GTFn6stARLzoyf.jpg', '2023-11-08', 0, '', '', 'L''Abbé Pierre : Une vie de combats');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 938508, '', 'fi', 'Neljä pientä aikuista', 'Juulia découvre que son mari Matias a une liaison avec une autre femme appelée Enni. Le trio décide d''embrasser le polyamour sans cacher de secrets l''un envers l''autre.', 7.026, '/eKQoChf1KFAAhG6b2jljVl03G34.jpg', '2024-01-03', 0, '', '', 'Amours à la finlandaise');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1174024, '', 'fr', 'Et plus si affinités', 'Usé par vingt-cinq ans de vie commune, le couple formé par Xavier et Sophie semble à bout de souffle. Et le moins que l’on puisse dire c’est que l’idée de Sophie d’inviter à dîner Adèle et Alban, leurs voisins du dessus, n’enchante pas Xavier ! Il leur reproche un manque de discrétion lors de leurs ébats nocturnes, aussi fréquents que déchaînés. Au contact de leurs voisins aux mœurs des plus libérées, Xavier et Sophie vont alors se confronter à leur réalité, avant d’être poussés dans leur retranchement par une proposition quelque-peu... indécente.', 5.046, '/bvNAtzzRt7peDXx1GTVi8ftHyfN.jpg', '2024-04-03', 0, '', '', 'Et plus si affinités');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 64897, '', 'fr', 'Remorques', 'Le capitaine du remorqueur le Cyclone, André Laurent, se voit contraint de quitter précipitamment la noce d''un de ses marins pour aller au secours du Mirva. Il abandonne ainsi sa femme Yvonne et la mariée. Au matin, le Cyclone remorque le Mirva avec à son bord Catherine, la femme du capitaine dont André Laurent va tomber amoureux. Entre sa femme gravement malade et celle qu''il aime, Laurent choisira la dernière. Il lui offre de partir avec lui, mais cette dernière refuse. Il finira par perdre les deux femmes qu''il aimait.', 6.382, '/nsNNj7fWA62TYK6PjTwl1X6Oba3.jpg', '1941-11-27', 0, '', '', 'Remorques');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1108371, '', 'en', 'Knit''s Island', 'Sous les traits d’avatars, une équipe de tournage pénètre dans des jeux-vidéos en ligne et entre en contact avec des « joueurs ». Qui sont ces habitants, sont-ils réellement en train de jouer ? À travers les rencontres avec ces personnages, leurs histoires, leurs imaginaires et leurs rapports sociaux, ce flm nous fait poser un autre regard sur le virtuel et nous questionne sur le devenir de notre monde.', 8.247, '/pfGzpB2znP04zlOjqGuTrb9aJnK.jpg', '2024-04-17', 0, '', '', 'Knit''s Island, l''île sans fin');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1112545, '', 'fa', 'آیه های زمینی', 'Un homme déclare la naissance de son fils.  Une mère habille sa fille pour la rentrée.  Une élève est convoquée par la directrice.  Une jeune femme conteste une contravention.  Une jeune fille se présente à un entretien d’embauche.  Un jeune homme vient retirer son permis de conduire.  Un homme au chômage répond à une annonce.  Un réalisateur demande une autorisation de tournage.  Une femme cherche à retrouver son chien.  Neuf visages de la vie quotidienne à Téhéran.', 7.213, '/c6dTCsN7a0QNWaGFrUKIBUnb3H1.jpg', '2024-03-13', 0, '', '', 'Chroniques de Téhéran');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1219158, '', 'ta', 'Lover', 'Arun vit son idylle dans le bonheur, mais l''arrivée de Vicky, son ennemi, va perturber sa vie amoureuse...', 10.055, '/w6cu5wybAwZ46ZzcIKrAXd3VGI0.jpg', '2024-02-09', 0, '', '', 'Lover');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 84922, '', 'en', 'Mahler', 'En 1911, alors qu''il ne sait pas qu''il ne lui reste que quelques jours à vivre, le compositeur autrichien Gustav Mahler, très malade, se remémore les grandes étapes de sa vie.', 6.547, '/nKwKd7jn6pqmxfcfqxHpxL2Oio2.jpg', '1974-05-01', 0, '', '', 'Mahler');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 934506, '', 'fr', 'Un métier sérieux', 'C’est la rentrée. Une nouvelle année scolaire au collège qui voit se retrouver Pierre, Meriem, Fouad, Sophie, Sandrine, Léo et Sofiane, un groupe d''enseignants engagés et soudés. Ils sont vite rejoints par Benjamin, jeune professeur remplaçant sans expérience et rapidement confronté aux affres du métier. À leur contact, il va découvrir combien la passion de l’enseignement demeure vivante au sein d’une institution pourtant fragilisée.', 9.297, '/5mLX4dGLgLaxvUc7hmeJZxsNObX.jpg', '2023-09-13', 0, '', '', 'Un métier sérieux');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1056133, '', 'ja', '劇場版シティーハンター 天使の涙(エンジェルダスト)', 'Nicky Larson est City Hunter, un détective privé opérant à Tokyo avec sa partenaire Laura. Un jour, Angie fait appel à eux pour une demande étrangement simple : retrouver son chat. En parallèle, Hélène, lieutenante de la police de Tokyo, enquête sur l''Angel Dust, une technologie mystérieuse qui transforme les soldats en surhommes mais le trio des Cat''s Eye est aussi sur le coup. Nicky Larson va se retrouver au cœur d''une bataille épique, qui l''amènera sur les traces de son propre passé.', 8.211, '/v7QRFHDYQwC1o3Kw6eXpb6De8oc.jpg', '2024-01-24', 0, '', '', 'Nicky Larson : Angel Dust');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1129780, '', 'en', 'Sins in the Family', 'Rose a été adoptée bébé par la famille Miller. Elle va fêter ses 18 ans et souhaite retrouver ses parents biologiques. Alors que sa mère, Zara, prend assez mal la nouvelle, Rose fait la rencontre de Hayley, la nouvelle conseillère d''orientation du lycée, qui lui propose de l''aider dans ses recherches. Rose et Hayley deviennent très proches. Trop, de l''avis de Zara.', 4.897, '/qHfpXn04kBcJEUFFKgntiO1jXbp.jpg', '2024-01-18', 0, '', '', 'Ma deuxième mère');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 958186, '', 'es', 'Tótem', 'Sol, sept ans, passe la journée dans la grande maison de famille où se prépare une fête pour son père. Alors que les invités arrivent, une atmosphère étrange et chaotique s’installe, brisant les liens qui unissent chacun. Sol comprend peu à peu que son monde est sur le point de changer radicalement.', 7.019, '/1Ll28o9kbyjbhBONkfQwITy7vqJ.jpg', '2024-03-27', 0, '', '', 'Tótem');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 15483, '', 'it', 'Il cartaio', 'Un tueur en série pousse la police à jouer à un jeu dangereux : si elle perd la partie de poker engagée avec lui, une nouvelle victime sera assassinée en direct sur le net. Anna, une jeune policière, va pénétrer dans le monde terrifiant du Card Player.', 6.961, '/7bGFXBCmrGKKlE9tLLk1cg0H73Z.jpg', '2024-01-31', 0, '', '', 'Card player');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 880644, '', 'en', 'The Good Father: The Martin MacNeill Story', 'Ce film est inspiré de la véritable histoire Martin MacNeill et de sa famille. Installé dans l''Utah avec son épouse et leurs cinq enfants, dont quatre filles adoptées, Martin Mac Neill était un notable réputé de la ville, médecin reconnu et respecté. En 2007, son épouse Michele MacNeill meurt brutalement dans des circonstances floues, suite à un lifting. Très rapidement, MacNeill installe dans la maison familiale une femme nommée Gypsy, qu''il présente comme une nounou. Il faudra des années à Alexis MacNeill, sa fille, pour prouver l''immense réseau de mensonges sur lequel son père avait construit toute sa vie et réussir à le faire condamner pour le meurtre de son épouse.', 9.893, '/2gfPjlZZz6NlkpIEG2YB5XZS4fR.jpg', '2024-01-22', 0, '', '', 'Chirurgie mortelle : l''histoire vraie de Martin MacNeill');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1109711, '', 'en', 'Freaknik: The Wildest Party Never Told', 'Ce documentaire retrace l’épopée mouvementée du festival emblématique d’Atlanta, Freaknik, qui a réuni des centaines de milliers de personnes dans les années 80 et 90, contribuant à l’importance culturelle d’Atlanta', 7.411, '/7aZbOI0EbIloc4Huuf5JdPbFrUD.jpg', '2024-03-21', 0, '', '', 'Freaknik : Un festival sauvage');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 962319, '', 'fr', 'Un silence', 'Silencieuse depuis 25 ans, Astrid la femme d’un célèbre avocat voit son équilibre familial s’effondrer lorsque ses enfants se mettent en quête de justice.', 8.779, '/bFjpZgmq5cWbbvTuNM2raIeo8wh.jpg', '2024-01-10', 0, '', '', 'Un silence');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1155840, '', 'fr', 'Opération Portugal 2 - La Vie De Château', 'Hakim alias Joaquim, notre policier maladroit et gaffeur, est de retour ! Il doit déjouer les plans d''un promoteur véreux : le comte de Neuville. Avec ses amis, il infiltre le Château du Comte mais doit en plus préparer son mariage avec Julia, à qui il a caché sa mission. Rien ne se passe comme prévu et Hakim et sa bande de pieds nickelés, dépassés, vont aller de catastrophes en catastrophes. Il va alors entrer dans une course folle pour réussir sa mission et sauver son couple.', 6.266, '/ipm8H0UNidY25p4IJDRpWRwVf3g.jpg', '2024-02-07', 0, '', '', 'Opération Portugal 2 - La Vie De Château');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1062644, '', 'fr', 'La Promesse verte', 'Pour tenter de sauver son fils Martin injustement condamné à mort en Indonésie, Carole se lance dans un combat inégal contre les exploitants d’huile de palme responsables de la déforestation et contre les puissants lobbies industriels.', 6.103, '/6UbIE85b2mM7Vb7jxPvvjdnRLPv.jpg', '2024-03-27', 0, '', '', 'La Promesse verte');
