INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 944368, '', 'fr', 'Le bonheur est pour demain', 'Anna, engluée dans une relation avec un homme autoritaire et violent avec qui elle a un enfant, tombe amoureuse de Claude qui prépare un braquage. Celui-ci va mal tourner et Claude se retrouver en prison. Soutenue par la mère de Claude, Lucie, Anna ne renonce pas à son amour et ira jusqu''au bout.', 1.589, '/3Qd55l221ri45hbWGfzuA8baG6v.jpg', '2024-01-31', 0, '', '', 'Le bonheur est pour demain');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1226078, '', 'en', 'Jack Whitehall: Settle Down', 'À l''O2 Arena de Londres, Jack Whitehall se lâche sur la boisson, les chiens, les dîners en solo et sa décision d''enfin se poser pour avoir un enfant.', 1.398, '/2hj7gJ33mocBxB7sqzX4tjaYRow.jpg', '2024-01-30', 0, '', '', 'Jack Whitehall: Settle Down');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 893030, '', 'vi', 'Những đứa trẻ trong sương', 'Di a douze ans et vit dans les montagnes du nord du Vietnam. Avec sa famille, elle attend les festivités du Nouvel An lunaire, durant lesquelles les hommes Hmong enlèvent des jeunes filles afin de les épouser.', 2.646, '/jV8NVgL1yOetwVnxzlQq6a9dMN2.jpg', '2024-03-06', 0, '', '', 'Children of the Mist');
