INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1247911, '', 'en', 'Elon Musk’s Twitter Takeover', 'Twitteur compulsif, Elon Musk s’est offert en 2022 son réseau social préféré, et l''a brutalement façonné selon ses désirs. Cette enquête punchy relate les relations orageuses entre la plate-forme et le milliardaire, et leurs incidences sur le débat public.', 1.565, '/13jPnTrvHfHmmK65tp6bGsiV16c.jpg', '2024-02-27', 0, '', '', 'La prise de contrôle de Twitter par Elon Musk');
