INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1233271, '', 'fr', 'Slow, une histoire d''amour', 'Le slow est un rituel en voie de disparition. À travers l''évocation du genre musical sans équivalent, Slow : une histoire d''amour raconte comment une simple danse a codifié les rencontres amoureuses depuis la seconde moitié du 20è siècle. Exploration sensitive unique, ces quelques minutes sur la piste qui marquent profondément au coeur, révèlent notre histoire du rapport à l''autre dans ce qu''il a de plus intime et de plus universel.', 1.021, '/zb0v0UIwLkx8Zc8LO2uCBMqm5kR.jpg', '2024-01-18', 0, '', '', 'Slow, une histoire d''amour');
