INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1253665, '', 'fr', 'Les Préjugés : bienvenue au pays des idées reçues !', 'Qui n''a jamais été confronté à des préjugés ? Qui n''a jamais eu à subir des réflexions ou simplement essuyé des regards sur son origine, son poids, son orientation sexuelle ou sa couleur de cheveux ? Tour à tour victime et bourreau. Car derrière ces préjugés parfois amusants, se cachent des stéréotypes tenaces qui irriguent les champs du racisme, du sexisme, de l''âgisme, des préjugés de classes et aboutissent souvent à des discriminations. Ce documentaire propose d''explorer les mécanismes et les origines de certains préjugés.', 0.969, '/tzdco3iHm1gnVDBDCd0jM5ehMFm.jpg', '2024-02-26', 0, '', '', 'Les Préjugés : bienvenue au pays des idées reçues !');
