INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1255200, '', 'fr', 'Les Outre-mer, des terroirs en or', 'Plongez dans les coulisses de la plus importante foire agricole de France, très appréciée du public et des politiques, qui en font un passage obligé dans leur agenda. Au cœur de cet événement hors normes, le Concours général agricole. Véritable vitrine du Salon, il récompense chaque année les meilleurs produits du terroir français. À cette occasion, les caméras d’Hervé Corbière ont suivi des producteurs ultramarins venus concourir dans les catégories « rhum », « miel » et « vanille ».', 0.739, '/kBlpntuaytfUmhxGYLR1ckM4Ztr.jpg', '2024-03-05', 0, '', '', 'Les Outre-mer, des terroirs en or');
