INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1089537, '', 'pl', 'Drzewo Miłosierdzia', 'Une île, un arbre et une femme. Des gestes répétés jour après jour avec des variations imperceptibles. Jusqu''à ce qu’un écho sinistre et prophétique du monde extérieur fasse irruption dans cette routine, changeant le cours des choses.', 0.365, '/9z4ErZzFgG8FdduOmbjrBvQksng.jpg', '2024-03-13', 0, '', '', 'The Mercy Tree - Drzewo Miłosierdzia');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1202309, '', 'fr', 'Hiver', 'C’est l’hiver et comme chaque année, la famille Seghers se rend à Tignes où ils possèdent un chalet. Rosa les accompagne pour s’occuper du ménage. Mais cette année il y a une nouveauté : on a proposé à Rosa d’emmener avec elle son fils, Auréliano, pour qu’il s’initie aux joies du ski.', 0.839, '/3qeGdSF2BuWZVm5qBmCtRQhHs3K.jpg', '2023-12-12', 0, '', '', 'Hiver');
