INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1221094, '', 'en', 'Sopa Fria', 'Une femme se remémore ses années de mariage et raconte son quotidien fait de violences physiques et psychologiques. Enfermée dans sa maison, elle se noie dans sa culpabilité, jusqu’au jour où elle décide que cela doit cesser.', 0.837, '/5pEQiH8roRcoLaz1AJaoQ1Qujp3.jpg', '2024-03-03', 0, '', '', 'Soupe froide');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1290832, '', 'fr', 'Une Autre Fin', '2124: Dans un monde en pleine canicule, Nora et un groupe de survivants insensibles au changement climatique s''est retranché dans une maison isolée doté de ressources abondantes. Leur insouciance les pousse à répéter les erreurs du passé en sur-consommant, provoquant une pénurie alimentaire et des tensions internes qui les conduisent à l''autodestruction.', 0.834, '/xCIzsW5AEtgTN9HexYnKA4gAJ0g.jpg', '2024-03-16', 0, '', '', 'Une Autre Fin');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1234784, '', 'fr', 'Win-Win', 'Dans l''espoir de conserver son poste de pigiste pour une chaîne locale, Audrey se retrouve à faire un reportage racoleur. Mais ce qui devait être un sujet bateau tourne vite au naufrage.', 0.834, '/65EtYDmQKpKPof5cC0c7wnoWkMn.jpg', '2024-01-22', 0, '', '', 'Win-Win');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1297223, '', 'fr', 'Ycare, le live', 'Accompagné des plus grands artistes de la variété française, Ycare propose un voyage musical au cœur de son répertoire foisonnant. Musicien polyvalent et autodidacte, Ycare navigue depuis une quinzaine d''années entre pop et chanson française. S''il écrit et compose ses propres titres, il lui arrive souvent d''en offrir aux nombreux chanteurs et amis qui l''entourent. Zaz, Nolwenn Leroy, Joyce Jonathan, Amel Bent, Garou, Lilian Renaud, Arcadian, Patrick Fiori.', 0.423, '/z7JaenZ6EiXgpKS2h0RShMfPwXB.jpg', '2024-01-09', 0, '', '', 'Ycare, le live');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1221465, '', 'vi', 'Saigon Kiss', 'Pour éviter les appels téléphoniques de sa petite amie possessive, Mơ, une professeure d''arts martiaux, se promène dans les rues de Saïgon. Une brève rencontre avec Vicky, une danseuse, entraîne Mơ sur un nouveau chemin, la ramenant en douceur à qui elle est vraiment, dans cette métropole bruyante et animée.', 0.679, '/6YsfeLx1FS2K6DmAtjr9YnopIhn.jpg', '2024-02-03', 0, '', '', 'Saigon Kiss');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1231642, '', 'fr', 'Pistache : Le Retour ?', 'Les deux CEO se retrouvent, des mois après la fin de Pistache Corporation™, causée par la distance. Dans une aventure mirobolante (pas plus que ça), ils vont devoir apprendre à surpasser leurs désaccords afin de sauver leur amitié et la vie d''Antony Moretti, de nouveau au fond du trou...', 0.703, '/f9mkHoQXMby8pT22LeCigx7oYec.jpg', '2024-01-12', 0, '', '', 'Pistache : Le Retour ?');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1227274, '', 'el', 'Wishbone', 'Costas (25) est depuis peu agent de sécurité dans un hôpital public. Après le décès soudain de son frère aîné, il doit non seulement s’occuper de sa petite nièce Niki mais aussi trouver l’argent nécessaire pour couvrir les dettes laissées par son frère. La maison de famille, leur seule possession, risque d’être saisie. Il se laisse alors entraîner dans une combine juteuse : monter de toutes pièces des dossiers pour faute médicale. Lorsqu’un docteur de son hôpital, accusé injustement, apparaît chez lui pour lui demander de l’aide, il implose sous la pression…', 0.695, '/39TaVczCB6L1lN1NalUUiwWj7yD.jpg', '2024-02-28', 0, '', '', 'Wishbone');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1294289, '', 'fr', 'Seul avec mes tripes', 'Un documentaire poignant sur la triste réalité de ce que c''est de faire un film.', 0.346, '/xrFJGmC77UlLc0vtbOuinDHrBKf.jpg', '2024-02-07', 0, '', '', 'Seul avec mes tripes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1274957, '', 'fr', 'Lo zenzero nero', 'Un clip d''effeuillage dévoilant le corps d''un homme et son animalité.', 0.693, '/178Z09BQdh62SDZL6QLA355fS3.jpg', '2024-02-27', 0, '', '', 'Lo zenzero nero');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1176304, '', 'zh', '長夜', 'Dans un monde post-apocalyptique, Hao survit dans une ville sous le joug des militaires qui pourchassent les habitants devenus monstrueux. La petite amie de Hao, Ying, est l''une d''entre eux. Hao tente de la protéger malgré le danger qu''elle représente pour lui.', 0.426, '/HCJr3aWCaBEjeg5cro5GtsRqpS.jpg', '2024-01-24', 0, '', '', 'La nuit noire');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1223836, '', 'fr', 'Aïcha', 'Elsa, 12 ans, vit avec sa mère dans un petit village de Provence. Elle attend avec impatience la venue de son grand-père algérien qui leur rend visite pour la première fois. Contre toute attente, cette rencontre va bouleverser sa relation avec sa mère.', 0.662, '/mhetdj4a3tb2R28xrlPKEQEsKYX.jpg', '2023-12-15', 0, '', '', 'Aïcha');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1194897, '', 'fr', 'Après l’aurore', 'Une odyssée au cœur d’un quartier HLM de banlieue à travers trois de ses habitants. Ces trois récits inachevés permettent au cinéaste de se ressaisir de la banlieue en y injectant un imaginaire riche de la singularité de ses personnages. Yohann Kouam utilise la narration chorale pour établir une sociologie nette, multipliant les échos à un réel élargi. Dans le chaos des codes inconnus, voilà de quoi les films sont encore capables.', 0.447, '/gHVsoKtIzN11bV3EzJfIjMleJim.jpg', '2024-03-04', 0, '', '', 'Après l’aurore');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1240775, '', 'fr', 'Feu Maman', 'Emma s''occupe du cheval de sa mère, depuis la mort de celle-ci il y a des années. Quand il faut prendre la décision de le piquer, elle ne peut se résoudre à le laisser partir...', 0.341, '/nfmGATA6MtZyWyJRAD0ahICSZ4o.jpg', '2024-02-01', 0, '', '', 'Feu Maman');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1230151, '', 'fr', 'Penser à demain', 'Découvrez l’agroécologie, la permaculture, la biodynamie et tant d''autres choses encore à travers l’expérience et le témoignage d’agriculteurs et d’associations de tous domaines et de tous âges. Comment grâce à leurs regards et leur amour de la Terre, ces héros du quotidien oeuvrent chaque jour à bâtir un monde meilleur. Plus qu’un travail ou qu’une initiative, c’est une philosophie de vie qui pense à demain.', 0.338, '/i5ysFrZYOOaMkexBZ9ZyOVXJR8V.jpg', '2024-02-14', 0, '', '', 'Penser à demain');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1205805, '', 'en', 'La Périchole (Théâtre des Champs-Elysées)', 'Opéra en trois actes de Jacques Offenbach filmé au Théâtre des Champs-Elysées.  A l’origine de La Périchole, on trouve le récit de la vie d’une comédienne et courtisane péruvienne Camila Périchole, lui-même inspiré de celui d’un personnage historique, Micaëla Villegas dite « la perra chola » (littéralement « chienne de métisse »), qui fut en son temps actrice et surtout maîtresse du vice-roi du Pérou Don Manuel. En adaptant la pièce de Mérimée Le Carrosse du Saint-Sacrement, les librettistes d’Offenbach l’ont transformée en chanteuse des rues. Plus « politique » par sa caricature du pouvoir des hautes sphères que La Vie parisienne, La Périchole embaume néanmoins de cet esprit immoral et libertin qui ont fait le succès du musicien. 25 ans après le début de leur collaboration à succès sur des œuvres d’Offenbach, le tandem Marc Minkowski - Laurent Pelly se reforme pour mettre en musique et en scène cette satire sociale et politique saisissante.', 0.672, '/uqdZ5rHPQgrbiKKU9VADYotItZc.jpg', '2024-01-18', 0, '', '', 'La Périchole (Théâtre des Champs-Elysées)');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239290, '', 'fr', 'My dad never told me I love you', 'Portrait d''un père vu par son fils.', 0.334, '/gij81vKfCRUo7PUVQdDGdagp2gT.jpg', '2024-02-02', 0, '', '', 'My dad never told me I love you');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1089827, '', 'en', 'Last Summer of Nathan Lee', 'Nathan Lee apprend qu’il est atteint d’un cancer du cerveau juste avant d’avoir 18 ans et se promet de vivre le reste de sa vie avec passion… et refuse de mourir vierge. Sachant que son meilleur ami gay, Dash, souhaite devenir cinéaste, Nathan se propose comme sujet de documentaire sur les derniers jours de sa vie.', 0.654, '/bIqSWqXWZcrXcvDxj1AZAGO0w0w.jpg', '2024-01-18', 0, '', '', 'Un été inoubliable');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1224645, '', 'fr', 'Baisse les bras!', '"Baisse les bras !" Les parents de Noé répètent inlassablement cette phrase à leur fils, qui a les bras en perpétuel mouvement lorsqu’il parle ou éprouve de fortes émotions. Noé a dix-huit ans. Il est majeur pour la société... Mais il n''est en aucun cas autonome. Sa sœur, qu’il adore, quitte la maison familiale pour aller faire ses études dans une autre ville. Sa vie en est chamboulée.', 0.399, '/lU1DJArgfXo3jYNiD4qTFkjAY6X.jpg', '2024-01-29', 0, '', '', 'Baisse les bras!');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1244333, '', 'fr', 'De Gaulle vs Churchill : Mémoires de guerre, guerre des mémoires', 'A-t-on déjà vu dans l''histoire ancienne et contemporaine beaucoup de chefs d''Etat et de géants de l''Histoire, dotés d''un si beau style d''écriture, relater de si grands événements, après avoir occupé de si hautes fonctions ? En écrivant et en publiant chacun leurs "Mémoires de guerre" sur le second conflit mondial de 1939 à 1945, Charles De Gaulle et Winston Churchill sont entrés dans la grande histoire.', 0.668, '/iXmAKXxNzeJW8mmBnN78wD7UHoP.jpg', '2024-01-12', 0, '', '', 'De Gaulle vs Churchill : Mémoires de guerre, guerre des mémoires');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1328103, '', 'fr', 'Moi & mWA : crise d''un algorithme !', 'Un homme s''entretient avec son Moi numérique à propos du contenu auquel il est confronté sur les réseaux sociaux, sur sa consommation quotidienne, sur l''obsession du numérique & sur le matraquage des algorithmes. Rapidement, une dispute éclate !', 0.756, '/fhQR2zNaULJ9OBBqnkO1yTycy3d.jpg', '2023-10-20', 0, '', '', 'Moi & mWA : crise d''un algorithme !');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239921, '', 'fr', 'Les pendules noires', 'Une vieille amitié de l''enfance peut-elle résister à tout ? Ambre et Jade devront trouver la réponse à cette question.', 0.425, '/AaXo83V8jz9OwOUG36l6Gs9f2Wv.jpg', '2024-02-02', 0, '', '', 'Les pendules noires');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 843750, '', 'it', 'Talponi', 'Anna, Francesco et leur fils de 8 ans, Giorgio, n''ont pas les moyens de s''offrir des vacances de rêve. Craignant de perdre leur réputation, ils font semblant de partir aux Bahamas et s''enferment chez eux.', 0.749, '/yunGw9AnrLdFVcqeHydNdhnHUWH.jpg', '2024-02-28', 0, '', '', 'Talponi');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1181502, '', 'fr', 'In Vino Veritas', '5 membres d''une famille se rejoignent dans la maison de leur mère récemment décédée. Mais le vin fait éclater les secrets de tous, quitte à tout détruire.', 0.746, '/7KrkTPgWMbfrY96DcpEChnmNmwK.jpg', '2024-01-01', 0, '', '', 'In Vino Veritas');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1140724, '', 'fr', 'We Have a Dream', 'Qui a dit que vivre avec un handicap signifiait renoncer à ses plus grands rêves ? A travers le monde, Pascal Plisson est allé à la rencontre de Xavier, Charles, Antonio, Maud, Nirmala et Khendo, des enfants extraordinaires qui vont prouver que l’amour, l’éducation inclusive, l’humour et le courage peuvent déplacer des montagnes, et que le destin est parfois plein de surprises.', 0.697, '/pAzq7Axtgbo1gUknBp85rFwkmC.jpg', '2023-09-27', 0, '', '', 'We Have a Dream');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1187928, '', 'hi', 'Places I''ve Called My Own', 'Après plusieurs années aux États-Unis, Tara, qui est en pleine procédure de PMA, retourne en Inde pour les funérailles de son père, mort du Covid.', 0.727, '/zGFTl3BZcFfascmEPQcYzYNucKT.jpg', '2024-03-27', 0, '', '', 'Places I''ve Called My Own');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1184957, '', 'et', 'Miisufy', 'Miisu, chatte de compagnie virtuelle, en a assez de sa maîtresse et commence à se révolter. Inspiré des Tamagotchis, "Miisufy" observe le monde à travers les yeux d''animaux virtuels.', 0.725, '/t1uwjOw7SyaUEHfIER8EK3TOywP.jpg', '2024-02-03', 0, '', '', 'Miisufy');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1250634, '', 'fr', 'Le Fantôme Vagabond', 'Il y a une chose que vous devez savoir : tous les fantômes, pour pouvoir traverser l''autre rive, doivent repasser au moins une fois par chez eux. Comme pour dire au revoir. C''est la seule et unique règle du royaume des morts. Mais lui, pour une raison inconnue, n''arrive pas à se souvenir d''où il vient. Il erre à travers la terre, sans succès. Jusqu''à ce soir-là...', 0.721, '/wgs0FNCRXGOsHdy9UBaS5q7e4m2.jpg', '2024-03-11', 0, '', '', 'Le Fantôme Vagabond');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1277388, '', 'fr', 'Marc-Antoine Le Bret à la Cigale', 'Marc-Antoine Le Bret revient avec un nouveau spectacle : Solo. Toujours seul sur scène, comme le titre le suggère, il est néanmoins accompagné d''une centaine de personnages dont il imite les voix avec un talent consommé tout au long d''un bouquet de nouveaux sketchs. Dans ce spectacle, le public a un petit aperçu de ce qui pourrait arriver si le monde se mettait à tourner à l''envers : un véritable show d''après la fin du monde, avant la fin du monde, en somme. Mais bien heureusement, l''humour est là pour faire passer la pilule.', 0.72, '/gaIadCq2JxclBJEaZYd5wKE76HB.jpg', '2024-03-22', 0, '', '', 'Marc-Antoine Le Bret à la Cigale');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1226824, '', 'fr', 'Tout le monde chante : les stars relèvent le défi', 'Les stars se sont une nouvelle fois donné rendez-vous en ce début d''année pour pousser la chansonnette au profit d''une association. Vedettes de la chanson, de l''humour, des médias et autres partagent la scène du cirque Phénix pour reprendre en duos des titres incontournables d''hier et d''aujourd''hui, devant un public déchaîné. Cette année, Lorie, Emma Daumas, Jean-Baptiste Guégan, Dave, Lola Dubini, Marc-Antoine Le Bret, Kimberose, Laury Thilleman, Joyce Jonathan, Laurent Maistret, Molière le spectacle musical et bien d''autres sont venus soutenir l''association "Tout le monde contre le cancer".', 0.409, '/nlDUIGxlX69t8IqVLo2poDZbVZm.jpg', '2024-01-04', 0, '', '', 'Tout le monde chante : les stars relèvent le défi');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1131844, '', 'fr', 'Cordes sensibles', 'C''est l''automne, Lucile, 13 ans et demi, passe le temps comme elle peut. Alors qu''elle flâne dans un magasin de musique, la confusion s''installe dans son esprit quand elle aperçoit Mona, une jeune femme à l''aura électrique. Pour se rapprocher de cette fille qui la fascine, Lucile décide de prendre des cours de guitare avec elle.', 0.442, '/uZlsOmubLogHh3V2zy8CbPuFjBT.jpg', '2024-01-10', 0, '', '', 'Cordes sensibles');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1282287, '', 'fr', 'Bankai', 'Court métrage de danse participant au concours "Quand le son créé l''image 2023" et soumis au festival de Cannes sélection "court métrage", réalisé par un groupe d''étudiants de 16 ans en spécialité cinéma du Lycée Pasteur Besançon', 0.305, '/iPm2YuG4lecShKklurjZv5OwmUC.jpg', '2024-01-15', 0, '', '', 'Bankai');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1265354, '', 'fr', 'Irlande du Nord, la frontière de tous les dangers', 'Et si le Brexit ravivait le conflit en Irlande du Nord ? Avec la sortie de l''Union européenne, le Royaume-Uni a réveillé des divisions dangereuses sur son territoire et ressuscité le dossier tabou de la frontière irlandaise. Décryptage d''une situation éruptive.  Un tremblement de terre aux conséquences infinies. Parmi les nombreuses secousses occasionnées par le Brexit du 30 janvier 2020, le ravivement des tensions en Irlande du Nord n''est pas la moins inquiétante. Les heurts entre protestants loyalistes et catholiques républicains se sont multipliés depuis le départ du Royaume-Uni de l''Union européenne, entre défilés des groupes extrémistes et incendies de bus à Belfast.', 0.708, '/rgliUz3XUO5TPKBYAhDzDZHn2cS.jpg', '2024-03-26', 0, '', '', 'Irlande du Nord, la frontière de tous les dangers');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1196709, '', 'en', 'Non-Negotiable', 'Un couple dans un café attend avec impatience la critique la plus sévère de leur confident le plus important : leur fille de 9 ans. Elle va donner les termes et les conditions dont elle a besoin pour l''arrivée de son petit frère.', 0.696, '/hZGF7fZPhFwxKGgaQwg5480fHGf.jpg', '2024-02-28', 0, '', '', 'Non négociable');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1265133, '', 'fr', 'BACKSEAT LE FILM : 2023, L''année où tout s''est embrasé', 'Avec ce documentaire, composé d’extraits de plateau de cette année passée, mais également de nombreuses interviews inédites et d’images de l’AFP, revivez à travers le regard particulier de l’émission l’année politique qu’a été 2023. Cette année, le principal théâtre de l’actualité politique n’a pas été l’Assemblée nationale et ses 49.3, ni les ministères et leurs décrets. Mais la rue.  La rue qui a vu des luttes nombreuses, et notamment la mobilisation entière du front syndical contre la réforme des retraites. La rue qui a vu Nahel, 17 ans, tué par un policier à bout portant. La rue qui a vu les défilés de l’extrême-droite se multiplier. La rue qui a vu partout dans le monde, de New York à Bethléem, les répercussions de la guerre au Proche-Orient.', 0.694, '/q3ffCirm8blbW5k0t0IZB36Bx6A.jpg', '2024-03-26', 0, '', '', 'BACKSEAT LE FILM : 2023, L''année où tout s''est embrasé');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1251425, '', 'fr', 'La traque des nazis - Le dernier combat', 'En 1945, les Alliés promettent de poursuivre les criminels nazis où qu''ils soient. Mais deux ans plus tard, la guerre froide rebat les cartes. La traque des nazis n''est plus la priorité. De quoi permettre à d''anciens nazis d''échapper à la justice. Pour quelques hommes et femmes, cette impunité est intolérable. L''Allemand Fritz Bauer, l''Autrichien Simon Wiesenthal, l''Américaine Elizabeth Holtzman ou le couple Beate et Serge Klarsfeld refusent que les bourreaux échappent à la justice. Ils vont consacrer leurs vies à traquer les anciens nazis. Leur combat va changer le cours de l''Histoire.', 0.298, '/xt7u6J6w3aF0fhFQNVcOB189Fnx.jpg', '2024-02-28', 0, '', '', 'La traque des nazis - Le dernier combat');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1225517, '', 'fr', 'La Réponse des bergers', 'Dans les Pyrénées ariégeoises, les Garcia vivent de leur élevage de chèvres depuis les années 70. Cette riche expérience, ils la transmettent à des jeunes qui aujourd’hui, s’autonomisent, renouent avec la tradition de l’estive et souhaitent lancer leur projet de fromagerie. Malheureusement, leur vision du pastoralisme va entrer en conflit avec une administration tatillonne et normative...', 0.659, '/qF0ZIAe1DRe49ql02KaYl04gnxt.jpg', '2024-01-10', 0, '', '', 'La Réponse des bergers');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1267147, '', 'fr', 'Les chevaliers du fiel  M. et Madame Lambert aux jeux Olympiques', 'M. et Mme Lambert ne pouvaient pas rater cet événement mondial !  Le rêve de Mme Lambert est d’assister à l’épreuve de gymnastique rythmique.  Ils louent un appartement avec Airbnb, ils achètent des billets en ligne... ils arrivent à Paris et la galère commence !!!  Les Lambert vivront-ils une aventure unique et hilarante ?  Vous le saurez en assistant à “ M. et Mme Lambert aux jeux olympiques ” et vous verrez que les jeux ne sont pas toujours faits.', 0.687, '/l7R2IfVCICbMMAZgUbbRO7YH9GL.jpg', '2024-03-29', 0, '', '', 'Les chevaliers du fiel  M. et Madame Lambert aux jeux Olympiques');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1270657, '', 'fr', 'Ptite Clope', 'Un homme se fait surprendre entrain de fumer pendant sa pause alors qu''il se trouve dans un endroit sombre qui inspire la peur...', 0.676, '/oADU0ai2jfDoKUBkv38EicGkNyE.jpg', '2024-02-19', 0, '', '', 'Ptite Clope');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1322202, '', 'fr', 'À propos des fleurs', 'Quelques étudiants se retrouvent pour tourner un film de zombie. Celui-ci se révèle peu engageant, et les acteurs, mécontents, cherchent à éviter le tournage.', 0.675, '/yqk5f7lXDFpPHGMGIaU9Jiuy0go.jpg', '2024-02-25', 0, '', '', 'À propos des fleurs');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1243249, '', 'zh', 'Di Shi San Ye', 'Après que Xiaoxue a reçu un appel de son défunt fiancé, une force inconnue commence à perturber sa vie normale.', 0.675, '/o0cbOOYUihwnHYOsoO4TGW11dAg.jpg', '2024-02-05', 0, '', '', 'Di Shi San Ye');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1194902, '', 'fr', 'Les Mystérieuses Aventures de Claude Conseil', 'Claude Conseil vit une retraite paisible avec son mari dans une maison au milieu des bois. Elle occupe son temps à écouter les oiseaux. Un soir de printemps, d’incessants et énigmatiques appels viennent rompre le calme de la forêt. Les Mystérieuses Aventures de Claude Conseil raconte le télescopage imprévu du chant des grives et des instrus de rap, de la génération Z et de celle de ses grands-parents, de twerk et d’ornithologie, de deux univers hermétiques mis à l’unisson le temps d’un film.', 0.444, '/njJZYaEggUNxfqez9ymThclou7Z.jpg', '2023-10-21', 0, '', '', 'Les Mystérieuses Aventures de Claude Conseil');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1338714, '', 'fr', 'The collector', 'Alors qu’une femme invite un homme à manger chez elle, les choses ne se déroulent pas exactement comme l’un d’entre eux l''aurait voulu. Pour l’autre, en revanche, les choses se déroulent à merveille.', 0.284, '/xFMi70IOhMo5oLO7R1lCwxedALM.jpg', '2024-03-02', 0, '', '', 'The collector');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1240546, '', 'fr', 'Ma Poule', 'Depuis que le coq est mort, l’unique poule survivante de Jasper, est en proie à la dépression. Contraint de descendre de sa montagne où il vit loin de tout, le septuagénaire se met en quête de compagnie pour son cher gallinacé.', 0.662, '/egFwCzDTP5ZPyf3Ye0tetoaE3Ei.jpg', '2024-02-03', 0, '', '', 'Ma Poule');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1240417, '', 'fr', 'La Fuite', 'Main dans la main, deux jeunes femmes s’échappent d’un asile. Pieds nus, elles vont devoir braver le froid, traquées par un homme et son chien.', 0.662, '/awwVOhczaqLhhv2uKW4daRYYObr.jpg', '2024-02-01', 0, '', '', 'La Fuite');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1339238, '', 'fr', 'Mines antipersonnel : La guerre des lâches', 'La guerre a fait de l''Ukraine le plus vaste champ de mines au monde avec une surface correspondant à celle du Royaume-Uni. Le traité d''Ottawa entré en vigueur il y a 25 ans en interdit pourtant l''acquisition, la production et l''utilisation. Quel bilan peut-on tirer de cette convention inédite alors que 32 Etats n''en sont toujours pas signataires ?', 0.428, '/sGVOtDcyvCyPdS74VlmSRR33BS9.jpg', '2024-02-27', 0, '', '', 'Mines antipersonnel : La guerre des lâches');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1212673, '', 'fr', 'La Photographe', 'France 1936. Gerta Pohorylle, jeune photoreporter allemande, vit en exil avec son compagnon, le photographe hongrois André Friedmann. En raison de la xénophobie ambiante, ils peinent à trouver des journaux qui acceptent de les publier. Quand la guerre civile éclate en Espagne, Gerta est déterminée à couvrir les évènements pour lutter contre le fascisme, malgré le refus des journaux de les envoyer au front.', 0.283, '/h5xPj7mE0Y6PG65lxuFZF9Ga5mW.jpg', '2024-03-13', 0, '', '', 'La Photographe');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1218775, '', 'fr', 'Le feu engendre le feu', 'Lorsqu’un inconnu menace de déclencher un incendie dans une résidence étudiante, Killian, Julien, Maël et Tom essaient de l’arrêter. Le problème, c’est qu’ils ne savent pas quand, ni comment il va s’y prendre.', 0.279, '/11xlqdDkX4TVxTyLdb7sHREgVc9.jpg', '2024-02-01', 0, '', '', 'Le feu engendre le feu');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239982, '', 'fr', 'Viol, défi de justice', 'Depuis #MeToo, les plaintes pour viol ont doublé, mais seules 10 % d’entre elles aboutissent à une condamnation criminelle. Souvent perçue comme trop patriarcale, déconnectée et lente, la justice est critiquée. Faute de preuves, comment dépasse-t-elle le « parole contre parole » ? Évolue-t-elle depuis #MeToo ? Plongée inédite au cœur d’un procès pour viol sur fond de soirée étudiante alcoolisée.', 0.505, '/jIU0nRLtnOjozD3bDBkM9KnOLc4.jpg', '2024-03-26', 0, '', '', 'Viol, défi de justice');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 589733, '', 'fr', 'Le Bon grain et l''ivraie', 'En petite bande joyeuse, ils dansent, rient, font des batailles de boules de neige, mais où dormiront-ils cette nuit ? Dans un hall de gare ? Dans un centre d’hébergement ? En France, aucun enfant ne devrait se poser ces questions.', 0.584, '/qpjG1TL1nOnwaf5r9dYycJFBRQY.jpg', '2021-06-09', 0, '', '', 'Le Bon grain et l''ivraie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1263941, '', 'fr', 'Sainte Soline. Autopsie d''un carnage', 'Le 25 mars 2023, une grande manifestation avait lieu à Sainte-Soline (Deux-Sèvres), pour protester contre une mégabassine en projet. Contestées depuis des années, ces immenses retenues d’eau sont destinées à irriguer de grandes exploitations céréalières. Mais le gouvernement n’entendait pas céder d’un pouce. Pis, il avait préparé une répression d’une ampleur démesurée. Le bilan : plus de 200 blessés, dont plusieurs dans un état tel qu’ils sont restés entre la vie et la mort pendant plusieurs semaines. Cette journée de carnage a aussi accablé le mouvement écologiste, traumatisé par la violence étatique.', 0.275, '/7gylFKRrhIZT9qEIjC1aRXLto9b.jpg', '2024-03-22', 0, '', '', 'Sainte Soline. Autopsie d''un carnage');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1258610, '', 'fr', 'Objectif Matchs 23-24 - Partie 2', 'Entre joies et peines, réussites et déceptions, revivez le début d’année 2024 en 𝘪𝘯𝘴𝘪𝘥𝘦 dans un documentaire de 𝟰𝟱 𝗺𝗶𝗻𝘂𝘁𝗲𝘀 au plus près du groupe pro’ 🔵⚪️', 0.275, '/eC7snrEMwZemwLTIthjemDeNPOm.jpg', '2024-03-13', 0, '', '', 'Objectif Matchs 23-24 - Partie 2');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 951175, '', 'fr', 'Ma vie en papier', 'Un portrait intime et subtilement expansif d''une famille syrienne vivant dans une maison à Bruxelles qui sort rarement des limites de ses quatre murs roses. Le film de Dena se concentre sur les deux filles aînées, Hala et Rima, et sur la collection croissante de dessins et de rêves de la famille. Entourées de croquis, les jeunes femmes évoquent leur parcours et leur foyer, leurs projets d''études et de mariage, et les autres possibilités qu''elles espèrent avoir dans cette nouvelle vie.', 0.275, '/7gsiTZOCNOXp82TWgH238MYM9HI.jpg', '2024-01-24', 0, '', '', 'Ma vie en papier');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1278106, '', 'fr', 'Stephan Eicher - Basique, le concert', 'Après de nombreux albums, de "Engelberg" à "Homeless Songs", en passant par "Carcassonne", Stephan Eicher récidive en 2022 avec l''aide de la plume de Martin Suter et Philippe Djian pour donner naissance à "Ode", un opus saisissant et touchant. Cherchant à se réinventer dans de nouvelles idées de mise en scène, le chanteur est accompagné d''une harpiste choriste et de ses fameux automates. Il fait découvrir ses nouvelles chansons telles que "Autour de ton cou" ou "Je te mentirais disant", et reprend "Déjeuner en paix", "Pas d''amis" ou encore "Combien de temps", auxquels il instille un souffle nouveau tout au long d''un show planant et envoûtant, rythmé par quelques surprises.', 0.274, '/zybOarliMYj2SPeqti3K5ik1pdO.jpg', '2024-03-15', 0, '', '', 'Stephan Eicher - Basique, le concert');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1231118, '', 'fr', 'Lola sang contact', 'Alors que les femmes vivent sous l''autorité conjugale de leur mari, Lola et ses copines viennent d''obtenir l''autorisation de passer une soirée entre elles au restaurant. Un pur moment de liberté qui leur permet de se livrer sur leur quotidien rocambolesque et le cauchemar qu''elles vivent au quotidien. Cependant, elles ignorent que Lola est équipée d''un micro.', 0.433, '/lvmgksrfP0YlCQDBSGsiSZruEdb.jpg', '2024-01-10', 0, '', '', 'Lola sang contact');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1225519, '', 'fr', 'Prendre soin de la Terre', 'Dans un monde épuisé par l''agriculture industrielle, Guy Chapouillié a rencontré des paysans heureux, dans la différence et la diversité de leurs paroles, de leurs gestes et de leurs sites, mais très proches par le commun de leur engagement : faire autrement pour prendre soin de la terre, pour prendre soin de soi et des autres. Il s''agit d''une rupture avec le productivisme dévastateur loin de contraindre la nature mais de vivre avec, sans l''épuiser. C''est un nouveau chant de la terre, une symphonie du vivant.', 0.271, '/m4NMssEmQrzvvrRRrf4xsNwc3eJ.jpg', '2024-01-10', 0, '', '', 'Prendre soin de la Terre');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1193076, '', 'fa', 'کجادوست؟', 'Sarah et Mehri sont deux amies proches qui boivent de l’alcool à l’école. Leur amitié est mise à l’épreuve quand le père de Mehri, un important fonctionnaire, les confronte.', 0.629, '/nVm09aLn7yt8EMlwGg6CrtEv3Vx.jpg', '2024-02-02', 0, '', '', 'Où est mon amie ?');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1262495, '', 'es', 'Sariri', 'Dans un petit village minier, au cœur du désert dans le nord chilien, les femmes sont soumises aux diktats imposés par les hommes. Dina (16 ans), confrontée à une grossesse non désirée, prévoit de partir à la ville, fuyant le machisme. Sa jeune sœur Sariri vient d’avoir ses premières règles jugées malignes par la gent masculine. Elle devra donc quitter le village pour un parcours solitaire dans le désert. Dina ne veut pas laisser sa sœur subir la violence de ces rapports masculin-féminin, mais comment ? De la complicité de Sariri avec son aînée, de son regard innocent, confiant et plein d’espoir naît une émotion intime. Un an après Chili 1976, Sariri a obtenu, en 2023, le Grand prix Cinéma en Construction, récompensant une autre réalisatrice chilienne. Première mondiale.', 0.626, '/7jij0dVQBagcXboNr3M9FQRwcxn.jpg', '2024-03-21', 0, '', '', 'Sariri');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1231098, '', 'fr', 'Les Abîmés', 'Mélanie tente de se remettre d''un drame familliale.', 0.266, '/6gT9mQWvaTeylsxH03DzRiZqnYT.jpg', '2024-01-24', 0, '', '', 'Les Abîmés');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1224433, '', 'pt', 'Até onde o mundo alcança', 'Une équipe d''ethnobotanistes doit répertorier l''une des plus importantes collections d''histoire naturelle au monde, tandis qu''un ornithologue amateur finit par enregistrer un son oublié depuis longtemps. Cet enregistrement est à l''origine d''une exploration des effets interminables du colonialisme sur la façon dont on perçoit la nature aujourd''hui.', 0.62, '/dByvVuXNnuOCxrFkx592pE1nbpl.jpg', '2024-02-03', 0, '', '', 'Aux confins du monde');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239688, '', 'fr', 'Ici en silence tout hurle', 'Georgie. Dans un village de montagne encore régi par des coutumes ancestrales, deux jeunes, Zourab et Natia, consomment leur amour en secret. La nouvelle se répand et la situation devient vite incontrôlable.', 0.619, '/lnBqzz2IOWd8tSkfCjLNTPXCWly.jpg', '2024-01-29', 0, '', '', 'Ici en silence tout hurle');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1265370, '', 'fr', 'Mayas : Les Secrets des dernières cités', 'Mystérieuse et fascinante, la civilisation maya a laissé derrière elle des énigmes qui captivent le monde encore aujourd''hui. Au cœur de cette intrigue se trouve la péninsule du Yucatan, au sud du Mexique, témoin silencieux des derniers jours de cette civilisation aujourd''hui disparue.', 0.617, '/wcEAIy5qkZ4ldU25LPRJgB6g9Io.jpg', '2024-01-24', 0, '', '', 'Mayas : Les Secrets des dernières cités');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1252937, '', 'xx', 'Laufey: Sounds Like Art', 'La chanteuse sino-islandaise, qui a remporté un Grammy Award en février 2024, se produit au musée Barberini de Potsdam : les sons de l’artiste mêlant jazz, classique et pop répondent aux couleurs des toiles de Monet, Munch et Signac.', 0.616, '/m24VFJMEs859F26snrxcPWlYL21.jpg', '2024-03-02', 0, '', '', 'Laufey : Sounds Like Art - Monet, Munch, Signac - Musée Barberini');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1267678, '', 'fr', 'Gauvain Sers : la voix des oubliés', 'Auteur, compositeur et interprète, Gauvain Sers fait partie d''une nouvelle génération d''artistes qui donne une portée sociale à son œuvre, mettant en chanson la France qu''il observe. Son portrait est agrémenté d''extraits de reportages, de clips ou de concerts, de témoignages de ses proches, permettant de découvrir un chanteur qui tente de saisir la complexité de la société dans ses textes et ses mélodies', 0.606, '/erqJreA34shMtHEsmfhoYHF7rwB.jpg', '2024-03-28', 0, '', '', 'Gauvain Sers : la voix des oubliés');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1257605, '', 'fr', 'Wrestlemania : 40 ans d''histoires', 'Philippe Chéreau et Christophe Agius passent en revue les moments les plus emblématiques de Wrestlemania des 40 dernières années, accompagnés du commentateur de la WWE Nadir Mohammedi, du streamer Rivenzi, ainsi que des anciennes superstars de la WWE Tom La Ruffa et Amale.', 0.26, '/10Mnw2OOFvOHA6LXf8YeQbTbqza.jpg', '2024-03-14', 0, '', '', 'Wrestlemania : 40 ans d''histoires');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1229558, '', 'fr', 'Au prix de la chair', 'Alice est une adolescente totalement paralysée qui ne peut communiquer qu''à travers les mouvements de ses yeux. Son frère et sa sœur tentent une méthode obscure pour la délivrer du mal qui l''entrave…', 0.405, '/jQbJpNx1aYRlkNR9vONoCQsOYMG.jpg', '2024-01-27', 0, '', '', 'The Cost of Flesh');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1269832, '', 'fr', 'Aurélien Sanchez, la Barkley à ses pieds', 'Obsédé par la Barkley depuis de longues années, Aurélien Sanchez est devenu en 2023 le premier Français à remporter cette infernale course d''ultra-trail que si peu parviennent à dompter.', 0.254, '/dBVvsknFc4YM69CpNBfy1o7AqlU.jpg', '2024-03-11', 0, '', '', 'Aurélien Sanchez, la Barkley à ses pieds');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1299078, '', 'fr', 'Les Interprètes', 'Deux comédiens engagés pour donner vie à des dragons, vont discuter de leurs rôles avec la réalisatrice.', 0.591, '/gIQv1Mxoo8UkXtiBcZNJHkkYy5W.jpg', '2024-01-15', 0, '', '', 'Les Interprètes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1290175, '', 'fr', 'L''Homme du Michigan', 'D''une rixe entre conscrits est né Uton, créature mi-homme mi-mouton, dont les premiers pas dans ce monde risquent d''être mis à mal par l''infâme Docteur Razovsky.', 0.253, '/mtzdfQ63pQXWwtuRg6Zb1P8QzVy.jpg', '2024-03-01', 0, '', '', 'L''Homme du Michigan');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1249962, '', 'fr', 'Ukraine, le coût des armes', 'Le 24 février 2022, la Russie lançait son offensive contre l''Ukraine. Depuis cette date, chaque jour ou presque, une nouvelle livraison d''armes est annoncée par les pays qui soutiennent l''Ukraine. Les Etats-Unis ont déjà dépensé 45 milliards de dollars en aide militaire, l''Union européenne 17 milliards d''euros. Comment se déroulent ces livraisons, de la production en Europe jusqu''à leur réception en Ukraine ? Dans l''ombre, des intermédiaires peu scrupuleux profitent aussi de cette situation historique. Le spectre du trafic d''armes en Ukraine est d''ailleurs devenu la pierre angulaire de la propagande russe, cherchant à effrayer les pays qui soutiennent le gouvernement de Volodymyr Zelensky.', 0.478, '/77uV4a5y97Ufq7QahgtyzLcCntm.jpg', '2024-02-25', 0, '', '', 'Ukraine, le coût des armes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 891965, '', 'fr', 'L''Empire du silence', 'Thierry Michel a été témoin des combats, des souffrances mais aussi des espoirs du peuple congolais. ... Il s’appuie sur un document de l’ONU, le rapport Mapping qui répertorie les violences commises au Congo entre 1993 et 2003, un document dérangeant qui fut enterré.', 0.25, '/aNbfWtMpUUEPtNzOodrifUPRejR.jpg', '2022-03-16', 0, '', '', 'L''Empire du silence');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1250465, '', 'fr', 'Une petite heure dans les nuages, 2008', '63'', silence  "En 2008, je commençais à aller en Corse. je découvrais cette île merveilleuse que j''ai filmée jusqu''en 2017. Je posais ma caméra sur le rebord de la fenêtre et j''enregistrais les nuages dans la vallée." - Jacques Perconte', 0.411, '/wM07mCMwMFJ8OsQdHO8rUmw2KFI.jpg', '2024-02-12', 0, '', '', 'Une petite heure dans les nuages, 2008');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1213722, '', 'fr', 'Supernova', 'Basile, 17 ans, activiste, veut changer le monde. Malik, 19 ans, agent de sécurité, fait avec le monde tel qu''il va. Le hasard va les rassembler.', 0.247, '/dZPcalyHftmWw8CmaOlDdvhMe15.jpg', '2023-04-04', 0, '', '', 'Supernova');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1226259, '', 'fr', 'Les Trésors de Marie-Antoinette à Versailles', 'Avec le temps, la reine Marie-Antoinette, qui fut la femme la plus haïe de son époque, a connu un spectaculaire retour en grâce. Aujourd''hui, les historiens et les conservateurs donnent à voir un autre personnage : une femme indépendante et aimante en quête permanente d''intimité qui a su garder ses secrets ; une femme au goût raffiné, féminin et moderne qui a marqué son temps. A Versailles, dans ce sublime écrin coupé du monde où elle s''est barricadée, Marie-Antoinette a cultivé son propre style et influencé, à travers toute l''Europe, les goûts de son époque.', 0.423, '/pIOzcX4nNAAZtCxDIqGleFtPTtR.jpg', '2024-01-03', 0, '', '', 'Les Trésors de Marie-Antoinette à Versailles');
