INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1230520, '', 'fr', 'Revoir Swann', 'Charlotte a un fils, Swann, qu''elle n''a pas revu depuis dix ans. Quand on lui annonce son décès, Charlotte repart sur ses traces. Elle découvre que Swann n''était plus son fils mais une femme affirmée.', 0.567, '/17GJZVGEPr1fO4AoY2jJFgQfvMM.jpg', '2024-01-22', 0, '', '', 'Revoir Swann');
