INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1290328, '', 'de', 'Beethoven : Symphonie n° 9 Leipzig, Paris, Milan, Vienne', 'En léger différé, les chefs d’orchestre Andris Nelsons, Klaus Mäkelä, Riccardo Chailly et Petr Popelka dirigent successivement les quatre mouvements de la "Symphonie n° 9" de Beethoven. Un concert événement qui célèbre le bicentenaire de la création du chef-d’oeuvre de Beethoven.  La Symphonie n° 9, op. 125 de Ludwig van Beethoven est incontestablement l’un des plus grands chefs-d’œuvre de la musique classique. Pour célébrer le 200e anniversaire de sa création, le 7 mai 1824 au Kärntnertortheater de Vienne, ARTE a imaginé un ambitieux projet artistique. L''œuvre sera jouée le soir du mardi 7 mai 2024 simultanément à Leipzig, Paris, Milan et Vienne. Chaque concert sera capté en direct et chacun de ses quatre mouvements sera interprété par quatre prestigieuses formations musicales. Du premier au quatrième mouvement, chacun d’entre eux sera joué depuis chacune des quatre villes européennes afin d’offrir une Neuvième exceptionnelle.', 0.536, '/OyfT6GComHpyqK14mNpsLHedce.jpg', '2024-01-01', 0, '', '', 'Beethoven : Symphonie n° 9 Leipzig, Paris, Milan, Vienne');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 608149, '', 'xx', 'Flow', 'Respirations, odeurs, chaleur, vent, et air, par nature invisibles, prennent chair. Comme par un effet d’inversion, les autres objets et les êtres qui peuplent le monde sont devenus invisibles à l’œil nu. Ce sont ces courants d’air qui vont révéler leur présence, en entrant en collision avec eux, en prenant vie sous l’action de leurs mouvements, ou en glissant sur eux.', 0.533, '/hw9D1abEQZSWWy3J9W6VsGzF5sw.jpg', '2024-02-02', 0, '', '', 'Flow');
