INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1283178, '', 'fr', 'L''art en vie', 'Une jeune fille trouve la vieille plume de sa grand-mère en rangeant son grenier. Cette plume n''est pas comme les autres.  Court métrage réalisé pour le concours "Images Fixes" du festival Chefs Op.  Disponible sur Youtube.', 0.224, '/kVzVGK9gqb1o4RzPZgfE0lPlwuT.jpg', '2024-02-24', 0, '', '', 'L''art en vie');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1241400, '', 'en', 'The Very Small Creatures', 'Suivez les aventures de Rose, Bleu, Orange, Jaune et Vert, un groupe de toutes petites créatures drôles et attachantes. À travers le jeu, ces petites créatures en pâte à modeler découvrent avec amusement le monde qui les entoure. L’humour est au cœur de ce programme, abordant avec légèreté des thèmes tels que la découverte de l’autre, le travail d’équipe, l’empathie et la tolérance.', 0.12, '/qmDid3dQ5nQiNZpuwVs2DRKWkSL.jpg', '2024-02-07', 0, '', '', 'Les Toutes Petites Créatures');
