INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1228999, '', 'el', 'Nous n''avons pas peur des ruines', 'Grèce, 2019 à 2023. Mitsotakis remplace Tsipras au pouvoir en Grèce et promet d’en finir avec Exarcheia, un quartier rebelle et solidaire d’Athènes. Mais la résistance s’organise et des renforts arrivent d’autres villes d’Europe. Le cri de ralliement devient No Pasaran ! Au fil des années, d’autres luttes s’étendent du nord au sud de la Grèce pour défendre la terre, la mer et la vie : en Crète, en Thessalie, en Épire… Même sur l’île de Paros dans les Cyclades, la population manifeste sur les plages devenues payantes et réussit à ce qu’elles redeviennent un bien commun, pour le bonheur de tous. Quand tout semble s’effondrer, à Athènes comme ailleurs, une même réponse se fait entendre : « nous n’avons pas peur des ruines, nous portons un monde nouveau dans nos cœurs. »', 0.114, '/vE3WEDONpQEmkKybJTO49WqlyFa.jpg', '2024-01-24', 0, '', '', 'Nous n''avons pas peur des ruines');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239155, '', 'fr', 'Nataraja', 'Fable musicale, interprétation moderne chorégraphiée du mythe hindou de la flamme sacrée de Nataraja.', 0.217, '/1ENXMuequU03AGBoCsaXAWNI4GM.jpg', '2024-02-01', 0, '', '', 'Nataraja');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1234541, '', 'fr', 'La Forêt, c’est la classe !', 'L’enfant du XXIème siècle, coupé de la nature, est-il condamné malgré lui à un monde réductible aux murs de la chambre et de la salle de classe ? En réaction à l’éducation « hors sol » et en s’inspirant des pratiques scandinaves, des enseignants de tout l’hexagone ont décidé de « sortir les enfants ». La démarche de l’école publique primaire de Mancenans dans le Doubs s’inscrit dans cette « révolution verte de l’éducation ». Durant toute l’année , chaque mardi, quel que soit le temps, Elise Sergent, une institutrice pionnière en France, fait école dans la forêt et remet en question le dispositif pédagogique. Au cours d’un voyage d’étude au Danemark, Elise a eu la confirmation que dehors, au contact du monde réel, l’enfant construit une part considérable de son rapport à son corps, à ses sens, à son intelligence et à la vie.', 0.116, '/unDfiYsBUScpWcwJMlU1Hksluiu.jpg', '2024-01-31', 0, '', '', 'La Forêt, c’est la classe !');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1254609, '', 'fr', 'Filante', 'Chaque nuit, Paulette observe la même étoile filante dans le ciel. La petite fille lui adresse son vœu le plus cher : celui de retrouver son rat domestique mystérieusement disparu. Les jours passent et l’animal ne revient pas. Paulette veut comprendre ce qui cloche avec son étoile.', 0.15, '/a64Kit1ZNf9gNvR5Tonvn5OQIe6.jpg', '2024-03-27', 0, '', '', 'Filante');
