INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1241377, '', 'fr', 'Americium', 'Son nouveau film, "Americium", est un long métrage qui aborde la question du racisme environnemental, du colonialisme nucléaire et de la propagande décrite dans les musées, y compris l''opposition des récits entre les acteurs et les victimes.', 0.211, '/8KnFhDy7CDp3uKZM9AucmWt21yu.jpg', '2024-03-24', 0, '', '', 'Americium');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1229058, '', 'fr', 'Les pieds dans le plat cuisiné', 'Malgré les scandales et polémiques qui ont frappé le marché depuis quelques années, rien n''a l''air d''arrêter le succès des plats cuisinés. Aujourd''hui, 700 000 tonnes de plats préparés sont vendues chaque année en France. Afin d''allécher les consommateurs, les industriels sollicitent régulièrement les plus les grands chefs cuisiniers. Mais tous ces plats sont-ils de qualité équivalente ? Gros plan sur ce qui se cache derrière ces emballages souvent trompeurs, sans occulter les bons élèves, qui misent par exemple sur le local et le bio.', 0.211, '/8HyM8uF6kZVNgIOwLFmgaSeq5F0.jpg', '2024-01-09', 0, '', '', 'Les pieds dans le plat cuisiné');
