INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1233766, '', 'fr', 'Du blé au pain', 'En Corse, le blé reste inscrit profondément dans les mémoires et dans le paysage malgré l''abandon brutal de sa production. Depuis quelques années, des passionnés, des aventuriers, des utopistes peut être, tentent de redonner une vie à la culture du blé. Par nostalgie pour conserver la mémoire d''anciens temps, ou dans l''idée d''un développement d''une filière pour une plus grande autonomie alimentaire. Dans tous les cas il s''agit de retrouver et de relier les liens ancestraux du blé au pain.', 0.157, '/62YKcX50m73WuPypy6gELdE09z1.jpg', '2024-01-19', 0, '', '', 'Du blé au pain');
