INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1237282, '', 'fr', 'Lettre à Milan et Zach', 'Le réalisateur Thierry Kübler avait 20 ans dans les années 80. A travers son film, il emmène Milan et Zack (10 ans chacun) sur les traces de cette décennie charnière, avec les souvenirs de l''époque et ce qu''il en reste aujourd''hui, mais aussi la mise en perspective des 20 ans à venir de Milan et Zack en 2032...', 0.154, '/1VxhUSGNW3E3O5WoyGxtf4GRIww.jpg', '2024-01-18', 0, '', '', 'Lettre à Milan et Zach');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1235573, '', 'fr', 'Le bœuf voit rouge', 'Du pré jusqu''à l''assiette, en passant par l''abattoir, le supermarché et la boucherie, la viande bovine constitue une filière qui fait vivre et nourrit des millions de personnes. Derrière les steaks que nous consommons se cache une incroyable industrie mondialisée de la viande. Mais aujourd''hui, les lignes bougent et certains cherchent à faire évoluer les modes de consommation et de production. Quelles sont les nouvelles pratiques à encourager dans ce secteur ? Comment s''inscrivent-elles dans le parcours d''un morceau de viande ? Et, à contrario, sur quels procédés faudrait-il revenir ?', 0.106, '/6pT8ZZEPNPFglxF81NhgGhTLCJe.jpg', '2024-01-23', 0, '', '', 'Le bœuf voit rouge');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1218238, '', 'fr', 'Le Chant des bêtes', 'Décembre 2022. En pleine Coupe du monde, un journaliste sportif reçoit une clef USB. À l''intérieur, des dizaines d''images de maltraitance animale en abattoir.', 0.106, '/rPs46fVIY8P2PjQiSVh0o1wqklb.jpg', '2024-01-23', 0, '', '', 'Le Chant des bêtes');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1239979, '', 'fr', 'Le Corps vermeil - Une anatomie du désir', 'Est-ce qu’il y a un temps pour aimer, pour l’érotisme, pour le plaisir d’un voyage ou d’une caresse ? Ce film essaie d’y répondre à travers des dialogues entre une jeune actrice et des personnes presque centenaires, qui dévoilent l’anatomie la plus intime de nous-mêmes, celle qui est faite de nos désirs les plus profonds.', 0.105, '/eBQAR0hjlBBI2rKWcK1HJhzuV6t.jpg', '2024-01-21', 0, '', '', 'Le Corps vermeil - Une anatomie du désir');
