INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 365177, '', 'en', 'Borderlands', 'L''adaptation du jeu vidéo Borderlands. Sorti en 2009 et développé par le studio texan Gearbox, Borderlands était un jeu mélangeant le jeu de rôle et tir à la première personne, dans lequel quatre aventuriers badass et charismatiques se lançaient à la recherche d''une fameuse Arche sur une planète du nom de Pandore, supposée renfermer tous les trésors de l''univers.', 153.189, '/lrlSOlTiIlTQqVqJGW4HaWjJaBJ.jpg', '2024-08-07', 0, '', '', 'Borderlands');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 626412, '', 'ko', '외계+인 2부', 'Afin d''anéantir la menace d''Aliens capable de posséder les corps humains, un Gardien est envoyé du futur pour traquer ces envahisseurs. Mais lorsque Séoul est attaqué, ce protecteur comprend qu''il ne s''en sortira pas seul. Des siècles plus tôt, une mystérieuse élue parcourt le pays à la recherche de l''Alien originel. Des quêtes parallèles de ces deux Voyageurs du Temps dépendra le salut de l''Humanité.', 118.041, '/1WHrVlJ82O0ITb9SrDkgEIc7sQ2.jpg', '2024-08-28', 0, '', '', 'Alienoid : L''Affrontement');
INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 923667, '', 'cn', '九龍城寨之圍城', 'Dans les années 80, le seul endroit de Hong Kong où la Loi Britannique ne s''appliquait pas était la redoutable Citadelle de Kowloon, une enclave livrée auw gangs et trafics en tous genre...', 152.125, '/yY1IfHtQr3CPg0biA0Qdx2mVnG4.jpg', '2024-08-14', 0, '', '', 'City of Darkness');
