INSERT INTO `movies` (`budget`, `homepage`, `id`, `imdb_id`, `original_language`, `original_title`, `overview`, `popularity`, `poster_path`, `release_date`, `runtime`, `status`, `tagline`, `title`) VALUES (0, '', 1062215, '', 'en', 'Afraid', 'Curtis et sa famille sont sélectionnés pour tester un nouvel appareil révolutionnaire : un assistant familial numérique appelé AIA. Rapidement le robot apprend les comportements de la famille et commence à anticiper leurs besoins. Il souhaite s''assurer que rien - ni personne - ne se met en travers du chemin de la famille.', 78.486, '/oJ64JVF4unLSznJZtN0F1yNklyp.jpg', '2024-08-28', 0, '', '', 'L''I.A. du mal');
